(2, 'A ', 'അ', 'അഡെനിന്‍ എന്ന അമിനോ അമ്ലത്തിന്റെ ചുരുക്കം', ' ', 0),
(3, 'A', 'അ', 'ആമ്പിയര്‍ എന്നതിന്റെ പ്രതീകം.', '', NULL),
(4, 'A', 'ആങ്‌സ്‌ട്രാം', 'ആങ്‌സ്‌ട്രാം യൂണിറ്റ്‌ (10^-10 m, 0.1 nm)', ' ', 0),
(5, 'AA ', 'അമിനോ അമ്ലം', 'അമിനോ അമ്ലം എന്നതിന്റെ ചുരുക്കം.', '', NULL),
(6, 'aa', 'ആ', 'ഒരിനം ലാവ. പരുപരുത്ത ഈ ലാവ താരതമ്യേന ചൂട്‌ കുറഞ്ഞ മാഗ്മാ പ്രവാഹത്തിന്റെ ഫലമായുണ്ടാകുന്നു.', '', NULL),
(7, 'AAAS', 'American Association for the Advancement of Science എന്നതിന്റെ ചുരുക്കം.', 'ശാസ്‌ത്രത്തെയും സമൂഹത്തെയും സംബന്ധിച്ച പ്രശ്‌നങ്ങള്‍ കൈകാര്യം ചെയ്യുന്ന യു എസ്‌ സംഘടന', ' ', 0),
(8, 'ab', 'അബ്‌', 'വൈദ്യുത ഏകകങ്ങള്‍ക്ക്‌ സമാനമായ കേവല (absolute) വിദ്യുത്‌കാന്തിക ഏകകങ്ങള്‍ക്കു ചേര്‍ക്കുന്ന സൂചകപദം.', '', NULL),
(9, 'Abacus', 'അബാക്കസ്‌', 'കണക്ക്‌ കൂട്ടുവാന്‍ സഹായിക്കുന്ന ഒരു ഉപകരണം. ക്രിസ്‌തുവിന്‌ മുമ്പ്‌ ഏകദേശം 2500 ല്‍ ചൈനക്കാര്‍ കണ്ടുപിടിച്ചു.', '', NULL),
(10, 'ab ampere', 'അബ്‌ ആമ്പിയര്‍', 'വൈദ്യുത ഏകകങ്ങള്‍ക്ക്‌ സമാനമായ കേവല (absolute) വിദ്യുത്‌കാന്തിക ഏകകങ്ങള്‍ക്കു ചേര്‍ക്കുന്ന സൂചകപദം.', '', NULL),
(11, 'abaxia', 'അബാക്ഷം', 'കാണ്ഡത്തിന്‌ എതിര്‍വശം. (ഇലകളുടെയും ദളങ്ങളുടെയും മറ്റും വശം സൂചിപ്പിക്കാന്‍)', '', NULL),
(12, 'abdomen', 'ഉദരം', 'കശേരുകികളില്‍ ഹൃദയവും ശ്വാസകോശങ്ങളും ഒഴികെയുള്ള അവയവങ്ങള്‍ അടങ്ങിയ ശരീരഭാഗം. കുടല്‍, കരള്‍, വൃക്ക മുതലായ അവയവങ്ങള്‍ ഇവിടെയാണ്‌ കാണപ്പെടുന്നത്‌. സസ്‌തനികളില്‍ മാത്രം ഈ ഭാഗത്തിന്റെ മുന്‍വശത്തെ അതിര്‍ത്തി ഡയഫ്രം ആണ്‌. അകശേരുകികളില്‍ പെട്ട ആര്‍ത്രാപോഡുകളുടെ മൂന്നു ശരീരഭാഗങ്ങളില്‍ ഒന്നാണിത്‌. തലയും നെഞ്ചും കഴിഞ്ഞാലുള്ള ഈ ഭാഗത്തിലെ ഖണ്ഡങ്ങളെല്ലാം ഒരുപോലെയിരിക്കും.', '', NULL),
(13, 'aberration', 'വിപഥനം', 'ലെന്‍സുകൊണ്ടോ ദര്‍പ്പണം കൊണ്ടോ രൂപീകരിക്കപ്പെടുന്ന പ്രതിബിംബത്തിന്റെ ഒരു ന്യൂനത. രണ്ടു വിധത്തിലുണ്ട്‌. 1. chromatic aberration വര്‍ണ്ണവിപഥനം. ലെന്‍സുകള്‍ പ്രദര്‍ശിപ്പിക്കുന്ന ന്യൂനത. അപവര്‍ത്തനാങ്കം തരംഗദൈര്‍ഘ്യത്തെ ആശ്രയിക്കുന്നതിനാല്‍ ഓരോ വര്‍ണവും ലെന്‍സിലൂടെ കടന്നുപോകുമ്പോള്‍ കേന്ദ്രീകരിക്കുന്നത്‌ ഒരേ ബിന്ദുവിലല്ല. തന്മൂലം പ്രതിബിംബം അവ്യക്തമാവുന്നു. 2. spherical aberration ഗോളീയ വിപഥനം. ലെന്‍സിനും ഗോളീയ ദര്‍പ്പണത്തിനും ബാധകം. ലെന്‍സിന്റെ/ ദര്‍പ്പണത്തിന്റെ വിവിധ സ്ഥാനങ്ങളില്‍ അക്ഷത്തിനു സമാന്തരമായി പതിക്കുന്ന രശ്‌മികളുടെ ഫോക്കല്‍ദൂരം വ്യത്യസ്‌തമാണ്‌. അക്ഷത്തോട്‌ അടുത്ത രശ്‌മികളുടെ ഫോക്കസല്ല അകന്ന രശ്‌മികളുടേത്‌. തന്മൂലം പ്രതിബിംബം അവ്യക്തമാവുന്നു.', '', NULL),
(14, 'abietic acid', 'അബയറ്റിക്‌ അമ്ലം', 'റെസിനില്‍ നിന്ന്‌ ലഭിക്കുന്ന ക്രിസ്റ്റലീയ കാര്‍ബണിക അമ്ലം', '', NULL),
(15, 'abiogenesis', 'സ്വയം ജനം', 'അജൈവ പദാര്‍ഥങ്ങളില്‍ നിന്ന്‌ രാസപരിണാമം വഴിയാണ്‌ ജീവനുണ്ടായത്‌ എന്ന സിദ്ധാന്തം. ജീവജാലങ്ങള്‍ അജൈവ പദാര്‍ഥങ്ങളില്‍ നിന്ന്‌ സ്വയം ജനിക്കുന്നുവെന്ന പഴയ സിദ്ധാന്തവും ഈ പേരില്‍ അറിയപ്പെടുന്നു.', '', NULL),
(16, 'abiotic factors', 'അജീവിയ ഘടകങ്ങള്‍', '-', 'biotic factors', NULL),
(17, 'ablation', 'അപക്ഷരണം', '1. ഒഴുകുന്ന ജലം, കാറ്റ്‌ തുടങ്ങിയവയുടെ പ്രവര്‍ത്തനഫലമായി പാറകള്‍ക്ക്‌ സംഭവിക്കുന്ന തേയ്‌മാനം. ', '', NULL),
(18, 'ablation', 'അപക്ഷരണം', '2. ഉരുകല്‍, ബാഷ്‌പീകരണം തുടങ്ങിയവ വഴി ഹിമാനികളുടെയും ഹിമപാളികളുടെയും കനം കുറഞ്ഞ്‌ ഇല്ലാതാകല്‍.', '', NULL),
(19, 'ab ohm', 'അബ്‌ ഓം', 'വൈദ്യുത ഏകകങ്ങള്‍ക്ക്‌ സമാനമായ കേവല (absolute) വിദ്യുത്‌കാന്തിക ഏകകങ്ങള്‍ക്കു ചേര്‍ക്കുന്ന സൂചകപദം.', '', NULL),
(20, 'abomesum', 'നാലാം ആമാശയം', 'അയവെട്ടുന്ന ജന്തുക്കളുടെ സങ്കീര്‍ണമായ ആമാശയത്തിലെ നാലാമത്തെ അറ.', '', NULL),
(21, 'aboral', 'അപമുഖ', 'ശരീരത്തില്‍ വായയ്‌ക്ക്‌ മറുവശത്തുള്ള.', '', NULL),
(22, 'abrasion', 'അപഘര്‍ഷണം', 'ഭൗമോപരിതലത്തില്‍ അനാവൃതമാക്കപ്പെട്ട പാറകള്‍ക്ക്‌ കാറ്റ്‌, ജലപ്രവാഹം, ഹിമാനികള്‍ എന്നിവയിലേതെങ്കിലുമായി ഉള്ള സമ്പര്‍ക്കം മൂലം ഉണ്ടാകുന്ന വിഘടനം.', '', NULL),
(23, 'abrasive', 'അപഘര്‍ഷകം', 'രാകിയോ, ഉരച്ചോ രിഖിതം ചെയ്യാന്‍ സഹായിക്കുന്ന വസ്‌തു (ഉദാ: കാര്‍ബറണ്ടം). മിനുസപ്പെടുത്താനുള്ള ഉപകരണം.', '', NULL),
(24, 'abscess', 'ആബ്‌സിസ്‌', 'ബാക്‌ടീരിയയുടെ പ്രവര്‍ത്തനം വഴി ഉണ്ടാകുന്ന പഴുപ്പ്‌, ചലം എന്നിവ ഊറിക്കൂടി നില്‍ക്കുന്ന ഭാഗം. ഉദാ: പല്ലിന്റെ കടയ്‌ക്കല്‍ ഉണ്ടാകുന്നത്‌.', '', NULL),
(25, 'abscisic acid', 'അബ്‌സിസിക്‌ ആസിഡ്‌', 'വളര്‍ച്ച തടയുന്ന ഒരു സസ്യ ഹോര്‍മോണ്‍. സസ്യഭാഗങ്ങളുടെ ജരണത്തിന്‌ കാരണമാകുന്നു.', '', NULL),
(26, 'abscissa', 'ഭുജം', 'ഒരു സമതലത്തിലുള്ള ഏതു ബിന്ദുവും അതേ നിരപ്പിലെ രണ്ടു നേര്‍വരകളെ ആധാരമാക്കി രണ്ട്‌ സംഖ്യകള്‍ കൊണ്ട്‌ നിര്‍ദ്ദേശിക്കാം.', 'cartesian coordinates', NULL),
(27, 'abscission layer', 'ഭഞ്‌ജകസ്‌തരം', 'സസ്യങ്ങളുടെ ഉപാംഗങ്ങള്‍ (ഇല, പൂവ്‌) കൊഴിയുന്നതിന്റെ മുന്നോടിയായി രൂപപ്പെടുന്ന കോശനിര. ഇത്‌ മാതൃസസ്യവുമായി ഉപാംഗത്തിനുള്ള ജൈവബന്ധം വിഛേദിക്കുന്നു.', '', NULL),
(28, 'absent spectrum', 'അഭാവ സ്‌പെക്‌ട്രം', '-', 'spectrum', NULL),
(29, 'absolute age', 'കേവലപ്രായം', 'ഫോസിലിന്റെയോ ധാതുവിന്റെയോ ശിലയുടെയോ ഭൂവിജ്ഞാനീയ പ്രായം വര്‍ഷങ്ങളില്‍ സൂചിപ്പിക്കുന്നത്‌. റേഡിയോ മെട്രിക്‌ പ്രായം. radiometric datingനോക്കുക.', '', NULL),
(30, 'absolute alcohol', 'ആബ്‌സൊല്യൂട്ട്‌ ആല്‍ക്കഹോള്‍', '99-100 ശതമാനം (ദ്രവ്യമാനത്തില്‍) ശുദ്ധ എഥനോള്‍.', '', NULL),
(31, 'absolute configuration', 'കേവല സംരചന', 'ഒരു പ്രകാശീയ ഐസോമറിന്റെ കേവല സംരചനയെ സംബന്ധിച്ചത്‌.', '', NULL),
(32, 'absolute expansion', 'കേവല വികാസം ', 'യഥാര്‍ഥ വികാസം.  expansion of liquids', 'expansion of liquids', NULL),
(33, 'absolute humidity', 'കേവല ആര്‍ദ്രത', 'അന്തരീക്ഷത്തില്‍ ഓരോ യൂനിറ്റ്‌ വ്യാപ്‌തത്തിലുമുള്ള ജലബാഷ്‌പത്തിന്റെ അളവ്‌. യൂനിറ്റ്‌ കെ ജി എം.', '', NULL),
(34, 'Absolute magnitude', 'കേവല അളവ്', '-', 'magnitude', NULL),
(35, 'absolute pressure', 'കേവലമര്‍ദം', 'യൂണിറ്റ്‌ പ്രതലത്തില്‍ അനുഭവപ്പെടുന്ന ബലം. SI യൂണിറ്റ്‌ ഉദാ: ന്യൂട്ടണ്‍/മീറ്റര്‍ 2.', '', NULL),
(36, 'absolute scale of temperature', 'കേവലതാപനിലാ തോത്‌', '-', 'temperature scales', NULL),
(37, 'absolute value', 'കേവലമൂല്യം', 'നിരപേക്ഷ മൂല്യം, വാസ്‌തവിക സംഖ്യാരേഖയിലെ ഏതു സംഖ്യയുടെയും, ധനാത്മകമോ ഋണാത്മകമോ എന്ന പരിഗണന കൂടാതെയുള്ള അളവ്‌. ഉദാഹരണമായി, +2, -2 എന്നീ രണ്ടു സംഖ്യകളുടെയും നിരപേക്ഷ മൂല്യം 2 ആണ്‌. a യുടെ നിരപേക്ഷ മൂല്യത്തെ ⏐a⏐എന്ന്‌ കുറിക്കുന്നു.', '', NULL),
(38, 'absolute zero', 'കേവലപൂജ്യം', 'താപഗതികസിദ്ധാന്തത്തിന്റെ അടിസ്ഥാനത്തില്‍ നിര്‍വചിക്കുന്ന താപനിലയുടെ ഏറ്റവും താഴ്‌ന്ന നില. ഡിഗ്രി സെല്‍ഷ്യസില്‍ ഇത്‌ -273.15 0C ആണ്‌. ഇതാണ്‌ പൂജ്യം കെല്‍വിന്‍ ( 0K).', '', NULL),
(39, 'absorbent', 'അവശോഷകം', '-', 'absorptance', NULL),
(40, 'absorber', 'ആഗിരണി', 'ഒന്നോ ഒന്നിലധികമോ ഘടക വാതകങ്ങള്‍ ലായകങ്ങളില്‍ ലയിപ്പിച്ച്‌, വാതക മിശ്രിതങ്ങളില്‍ നിന്ന്‌ ഘടക വാതകങ്ങളെ വേര്‍തിരിക്കുവാനായി ഉപയോഗിക്കുന്ന ഉപകരണം.', '', NULL),
(41, 'absorptance', 'അവശോഷണാങ്കം', 'പ്രകാശം ആഗിരണം ചെയ്യാനുള്ള ഒരു വസ്‌തുവിന്റെ ശേഷിയെ സൂചിപ്പിക്കുന്ന പദം. ആഗിരണം ചെയ്യപ്പെട്ട ഊര്‍ജവും പതിച്ച ഊര്‍ജവും തമ്മിലുള്ള അനുപാതം എന്ന്‌ നിര്‍വചിച്ചിരിക്കുന്നു. സാധാരണയായി ശതമാനത്തിലാണ്‌ പറയാറുള്ളത്‌. പ്രകാശം ആഗിരണം ചെയ്യുന്ന പദാര്‍ഥത്തെ അവശോഷകം എന്ന്‌ വിളിക്കുന്നു. പ്രകാശത്തിന്‌ പകരം, ദ്രാവകങ്ങളെ ആഗിരണം ചെയ്യുവാനുള്ള ഖരവസ്‌തുവിന്റെ ശേഷിയെ അല്ലെങ്കില്‍ ഖരവസ്‌തുവിനെയോ, വാതകത്തെയോ ആഗിരണം ചെയ്യാനുള്ള ദ്രാവകത്തിന്റെ ശേഷിയെ സൂചിപ്പിക്കാനും ഈ പദം ഉപയോഗിക്കാം.', '', NULL),
(42, 'absorption gases', 'അബ്‌സോര്‍പ്‌ഷന്‍ ഗ്യാസസ്‌', 'വാതകങ്ങള്‍ ദ്രാവകങ്ങളില്‍ ലയിച്ചുണ്ടാകുന്ന ലായനി.', '', NULL),
(43, 'absorption indicator', 'അവശോഷണ സൂചകങ്ങള്‍', 'അവക്ഷിപ്‌ത ടൈട്രഷനുകളില്‍ ഉപയോഗിക്കുന്ന സൂചകങ്ങള്‍. ഉദാ: സില്‍വര്‍ നൈട്രറ്റും പൊട്ടാസ്യം ബ്രാമൈഡും തമ്മിലുള്ള ടൈട്രഷനില്‍ ഇയോസിന്‍.', '', NULL),
(44, 'absorption spectrum', 'അവശോഷണ സ്‌പെക്‌ട്രം', 'ഒരു മാധ്യമത്തിലൂടെ കടന്നു പോകുന്ന വിദ്യുത്‌ കാന്തിക തരംഗങ്ങളില്‍ ചില തരംഗങ്ങള്‍ മാധ്യമത്തില്‍ അവശോഷിക്കപ്പെടുന്നു. ഏതേത്‌ ആവൃത്തികളിലെ ഊര്‍ജമാണ്‌ അവശോഷണം ചെയ്യപ്പെടുന്നത്‌ എന്നത്‌ മാധ്യമത്തെ ആശ്രയിച്ചിരിക്കും. മാധ്യമത്തിലൂടെ കടന്നുവന്ന വിദ്യുത്‌കാന്തിക തരംഗങ്ങളുടെ സ്‌പെക്‌ട്രത്തില്‍ അവശോഷിക്കപ്പെട്ട തരംഗങ്ങളുടെ സ്ഥാനങ്ങള്‍ ഇരുണ്ട രേഖകളായി കാണാം. ഇതാണ്‌ മാധ്യമത്തിന്റെ അവശോഷണ സ്‌പെക്‌ട്രം.', '', NULL),
(45, 'abundance', 'ബാഹുല്യം', '1. ഭൂവല്‍ക്കത്തില്‍ ഒരു മൂലകത്തിന്റെ മൊത്തം ദ്രവ്യമാനവും ഭൂവല്‍ക്കത്തിന്റെ മൊത്തം ദ്രവ്യമാനവും തമ്മിലുള്ള അനുപാതം. 2. ഒരു മൂലകത്തിന്റെ ഒരു നിശ്ചിത ഐസോടോപ്പിന്റെ അണുക്കളുടെ മൊത്തം എണ്ണവും മൂലകത്തിന്റെ എല്ലാ ഐസോടോപ്പുകളുടെയും അണുക്കളുടെ മൊത്തം എണ്ണവും തമ്മിലുള്ള അനുപാതം. ഇത്‌ സാധാരണയായി ശതമാനത്തിലാണ്‌ സൂചിപ്പിക്കുക.', '', NULL),
(46, 'abundance ratio', 'ബാഹുല്യ അനുപാതം', 'ഒരു മൂലകത്തിന്റെ സാമ്പിളില്‍ ഒരു ഐസോടോപ്പിന്റെ അളവ്‌ എത്രയെന്ന്‌ സൂചിപ്പിക്കുന്ന പദം. ഉദാ: കാര്‍ബണ്‍ സാമ്പിളില്‍ കാര്‍ബണിന്റെ ഐസോടോപ്പ്‌ C12 99.008 ശതമാനമാണ്‌. ബാക്കി C13 ഉം C14 ഉം ഉണ്ടെങ്കില്‍ C12+C13+C14=100. C12 ന്റെ ബാഹുല്യ അനുപാതം എന്ന്‌ നിര്‍വ്വചിച്ചിരിക്കുന്നു.', '', NULL),
(47, 'abyssal', 'അബിസല്‍', '1000 മീറ്റര്‍ മുതല്‍ അടിത്തട്ടുവരെയുള്ള സമുദ്രഭാഗത്തെ സൂചിപ്പിക്കുന്ന പദം. ആഴക്കടലില്‍ ജീവിക്കുന്ന ജന്തുക്കളുടെ വിശേഷണമായി പദം ഉപയോഗിക്കുന്നു.', '', NULL),
(48, 'abyssal plane', 'അടി സമുദ്രതലം', 'സമുദ്രാന്തര്‍ഭാഗത്ത്‌, വന്‍കരച്ചെരിവുകള്‍ക്ക്‌ വളരെത്താഴെ കാണുന്ന വിശാലമായ സമതലം.', '', NULL),
(49, 'AC', 'ഏ സി.', 'Alternating Current (പ്രത്യാവര്‍ത്തിധാര) എന്നതിന്റെ ചുരുക്കം.', '', NULL),
(50, 'Acanthopterygii', 'അക്കാന്തോടെറിജി', 'അസ്ഥിമത്സ്യങ്ങളുടെ ഒരു വിഭാഗം. മുള്ളുള്ള ചിറകുകിരണങ്ങളാണ്‌ ഇവയ്‌ക്കുള്ളത്‌.', '', NULL),
(51, 'Acarina', 'അകാരിന', 'ചെള്ളുകളും ഉണ്ണികളും ഉള്‍പ്പെടുന്ന ജന്തുവിഭാഗം. ആര്‍ത്രാപോഡ്‌ ജന്തുവിഭാഗം. ആര്‍ത്രാപോഡ്‌ ഫൈലത്തിന്റെ അരാക്‌നിഡ എന്ന ക്ലാസില്‍പ്പെടുന്നു. ശിരോവക്ഷസും ഉദരവും തമ്മില്‍ യോജിച്ചിരിക്കും.', '', NULL),
(52, 'acceleration', 'ത്വരണം', 'പ്രവേഗം മാറുന്നതിന്റെ നിരക്ക്‌. ഓരോ സെക്കന്റിലും ഉണ്ടാകുന്ന പ്രവേഗ മാറ്റമായാണ്‌ ഇത്‌ അളക്കുന്നത്‌. ത്വരണം രണ്ട്‌ തരത്തിലുണ്ട്‌. 1. രേഖീയം. ഏകകം ms-2.2. കോണീയം. ഏകകം rads-2', '', NULL),
(53, 'acceleration due to gravity', ' ഗുരുത്വ ത്വരണം', 'സ്വതന്ത്ര പതനത്തില്‍ ഒരു വസ്‌തുവിനുണ്ടാകുന്ന ത്വരണം . പ്രതീകം g. ഭൂമിയിലേക്ക്‌ വസ്‌തുക്കള്‍ പതിക്കുമ്പോള്‍ ഉണ്ടാകുന്ന ത്വരണത്തിന്‌ ഭൂഗുരുത്വ ത്വരണം എന്നു പറയുന്നു. ഭൂതലത്തില്‍ ഇത്‌ എല്ലായിടത്തും ഒരേ പോലെയല്ല. അക്ഷാംശം, ഉന്നതി എന്നിവയ്‌ക്കനുസരിച്ച്‌ ചെറിയ മാറ്റം വരുന്നു. ഭൂമധ്യരേഖാ പ്രദേശത്ത്‌ മൂല്യം 9.81 ms-2', '', NULL),
(54, 'accelerator', 'ത്വരിത്രം', 'particle accelerator', 'particle accelerator', NULL),
(55, 'acceptor', 'സ്വീകാരി', 'പി ടൈപ്പ്‌ അര്‍ധചാലകങ്ങള്‍ ഉണ്ടാക്കുവാന്‍ ഉപയോഗിക്കുന്ന അപദ്രവ്യം. ബാഹ്യ പരിപഥത്തില്‍ മൂന്ന്‌ ഇലക്‌ട്രാണുകളുള്ള എല്ലാ മൂലകങ്ങള്‍ക്കും സ്വീകാരികളാവാന്‍ കഴിയും. ഉദാ: ബോറോണ്‍.', '', NULL),
(56, 'acceptor circuit', 'സ്വീകാരി പരിപഥം', 'നിശ്ചിത ആവൃത്തിയില്‍ ഉള്ള വൈദ്യുത സ്‌പന്ദങ്ങളെ സ്വീകരിക്കുന്ന പരിപഥം. അനുനാദമാണ്‌ ഇതിനു കാരണം. അനുനാദ ആവൃത്തിയില്‍ പരിപഥത്തിന്റെ കര്‍ണരോധം വളരെ കുറവും മറ്റെല്ലാ ആവൃത്തികളിലും വളരെ കൂടുതലും ആയിരിക്കും. ശ്രണീ അനുനാദ പരിപഥം എന്നും പറയുന്നു.', '', NULL),
(57, 'acclimation', 'അക്ലിമേഷന്‍', 'മാറിവരുന്ന പരിസ്ഥിതിക്കനുസരിച്ച്‌ ജന്തുക്കളുടെ ഉപാപചയ പ്രക്രിയയില്‍ വരുന്ന മാറ്റം. ഉദാ: ഉഷ്‌ണമേഖലയില്‍ ജനിച്ചു വളര്‍ന്ന ഒരാള്‍ മിതശീതോഷ്‌ണമേഖലയിലേക്ക്‌ മാറി താമസിക്കുമ്പോള്‍ ഉണ്ടാകുന്ന മാറ്റങ്ങള്‍. ഉയര്‍ന്ന പ്രദേശങ്ങളില്‍ താമസിക്കുന്നവരില്‍ ചുവന്ന രക്ത കോശങ്ങളുടെ തോത്‌ കൂടുതലായിരിക്കും. അക്ലിമേഷന്റെ ഫലമാണിത്‌. ഇവ ഫിസിയോളജീയ അനുവര്‍ത്തനങ്ങളാണ്‌. സാവധാനത്തില്‍ ഉണ്ടാകുന്ന സ്ഥിരതയുള്ള മാറ്റങ്ങള്‍ മാത്രമേ ഇതില്‍പ്പെടുകയുള്ളൂ.', '', NULL),
(58, 'accommodation of eye', 'സമഞ്‌ജന ക്ഷമത', 'അടുത്തോ അകലെയോ ഉള്ള വസ്‌തുക്കളെ വ്യക്തമായി കാണാന്‍ സജീവനേത്രം നടത്തുന്ന ക്രമീകരണം. കണ്ണിലെ ലെന്‍സിന്റെ പ്രതല വക്രത വ്യത്യാസപ്പെടുത്തുന്നതിലൂടെ അതിന്റെ ഫോക്കല്‍ദൂരത്തിന്‌ മാറ്റം വരുത്തുന്നതുവഴിയാണ്‌ ഇത്‌ സാധിക്കുന്നത്‌. പല കശേരുകികള്‍ക്കും ഈ കഴിവുണ്ട്‌. എന്നാല്‍ മത്സ്യങ്ങളും ഉഭയ ജീവികളും ലെന്‍സിനെ മുന്നോട്ടും പിന്നോട്ടും നീക്കിയാണ്‌ (ക്യാമറയിലെ പോലെ) സമഞ്‌ജനം നടത്തുന്നത്‌.', '', NULL),
(59, 'accretion', 'ആര്‍ജനം', '1. മണ്ണ്‌, മണല്‍ തുടങ്ങി ജലത്തിലടങ്ങിയ വസ്‌തുക്കള്‍ ഏതെങ്കിലും പ്രദേശത്ത്‌ അടിഞ്ഞുകൂടുന്നത്‌.', '', NULL),
(60, 'accretion', 'ആര്‍ജനം', '2. ( astr.) ആര്‍ജനം. ഒരു പ്രപഞ്ചവസ്‌തു ചുറ്റുപാടുനിന്നോ സമീപനക്ഷത്രത്തില്‍ നിന്നോ പദാര്‍ഥത്തെ ആകര്‍ഷിച്ചു പിടിക്കുന്ന പ്രക്രിയ. ഉദാഹരണത്തിന്‌ ഇരട്ട നക്ഷത്രങ്ങളിലൊന്ന്‌ ഒരു വെള്ളക്കുളളനോ ന്യൂട്രാണ്‍ താരമോ തമോദ്വാരമോ ആയശേഷം മറ്റേ നക്ഷത്രം ഒരു ചുവപ്പുഭീമനായി വീര്‍ക്കാനിടയായാല്‍ അതിന്റെ പുറം അടരുകള്‍ മൃതനക്ഷത്രത്തോടു കൂടുതല്‍ അടുക്കുകയും ഗുരുത്വാകര്‍ഷണത്താല്‍ അതിലേക്ക്‌ ഒഴുകിയെത്തുകയും ചെയ്യാം. ഇത്‌ മൃതനക്ഷത്രത്തിനു ചുറ്റും ഒരു ആര്‍ജിത ഡിസ്‌ക്‌ ( accretion disc) ആയി കറങ്ങിക്കൊണ്ട്‌ ക്രമേണ അതില്‍പ്പോയി പതിക്കുന്നു. ഇങ്ങനെ ആര്‍ജിക്കുന്ന പദാര്‍ഥത്തിന്റെ അളവ്‌ ഒരു നിശ്ചിത അളവില്‍ കൂടിയാല്‍ വെള്ളക്കുള്ളന്‍ നക്ഷത്രത്തില്‍ വീണ്ടും ഫ്യൂഷന്‍ നടക്കുകകയും അതൊരു നോവ ആയി മാറുകയും ചെയ്യാം.', '', NULL),
(61, 'Accretion disc', ' ആര്‍ജിത ഡിസ്‌ക്‌', '-', 'Accretion ', NULL),
(62, 'accumulator', 'അക്യുമുലേറ്റര്‍', '1. (comp) കംപ്യൂട്ടറിന്റെ കേന്ദ്രഭാഗത്തുള്ള ഒരു പ്രത്യേക രജിസ്റ്റര്‍. അരിത്‌മെറ്റിക്ക്‌ ആന്റ്‌ ലോജിക്ക്‌ യൂണിറ്റ്‌ എന്ന ഭാഗത്ത്‌ ചെയ്യുന്ന ക്രിയകളുടെ ഉത്തരങ്ങള്‍ പുനര്‍ക്രിയയ്‌ക്ക്‌ എടുക്കാന്‍ വേണ്ടി താത്‌കാലികമായി സൂക്ഷിക്കുന്ന സ്ഥലം.', '', NULL),
(63, 'accumulator', 'അക്യുമുലേറ്റര്‍', '2. (phy.) അക്യുമുലേറ്റര്‍, സംഭരണ സെല്‍ . വൈദ്യുതി സംഭരിക്കുന്ന സെല്‍. വൈദ്യുതോര്‍ജത്തെ രാസികോര്‍ജമായി സംഭരിക്കുകയും ആവശ്യമുള്ളപ്പോള്‍ വൈദ്യുതോര്‍ജമാക്കി മാറ്റുകയും ആണ്‌ ചെയ്യുന്നത്‌. നിക്കല്‍-അയേണ്‍ അക്യുമുലേറ്റര്‍, ലെഡ്‌-ആസിഡ്‌ അക്യുമുലേറ്റര്‍ എന്നിങ്ങനെ പല വിധത്തിലുണ്ട്‌.', '', NULL),
(64, 'accuracy', ' കൃത്യത', '1. പിശക്‌ അഥവാ തെറ്റ്‌ എത്രമാത്രം കുറവാണ്‌ എന്ന്‌ സൂചിപ്പിക്കാനുള്ള ഗുണപരമായ വിലയിരുത്തല്‍. 2. പിശക്‌ അഥവാ തെറ്റിന്റെ അളവ്‌. ഒരു നിരീക്ഷണത്തില്‍ അളവുകള്‍ കൃത്യമായിരിക്കണമെന്നില്ല. കൃത്യമായ വിലയോട്‌ എത്രത്തോളം അടുത്ത വില ലഭിച്ചു എന്നതിനെ അടിസ്ഥാനമാക്കിയാണ്‌ കൃത്യത വിലയിരുത്തുന്നത്‌.', '', NULL),
(65, 'accustomization', 'അനുശീലനം', '(bio) മാറിയ പരിസ്ഥിതിയുമായി ഇണങ്ങിച്ചേരല്‍.', '', NULL),
(66, 'acellular', 'അസെല്ലുലാര്‍', 'വ്യത്യസ്‌ത കോശങ്ങളായി വിഭജിക്കപ്പെടാത്ത ജീവി. ബഹുകോശജീവികളുടെ ശരീരം നിര്‍മ്മിക്കപ്പെട്ടിരിക്കുന്നത്‌ പലതരം കോശങ്ങള്‍ കൊണ്ടാണ്‌. ഇതില്‍ ഓരോ തരം കോശങ്ങള്‍ക്കും പ്രത്യേകം പ്രത്യേകം ധര്‍മ്മങ്ങളാണുള്ളത്‌. എന്നാല്‍ അമീബ മുതലായ ജീവികളില്‍ ഒരു കോശത്തില്‍ തന്നെയാണ്‌ ജീവന്റെ നിലനില്‍പ്പിനു വേണ്ട എല്ലാ പ്രക്രിയകളും നടക്കുന്നത്‌. അതിനാല്‍ അവയെ ഏകകോശ ജീവികളെന്നു വിളിക്കുന്നതില്‍ അപാകതയുണ്ട്‌. ഈ പ്രശ്‌നമൊഴിവാക്കുവാനാണ്‌ അവയെ \"കോശനിര്‍മിതമല്ലാത്ത\' എന്നര്‍ഥം വരുന്ന \"അസെല്ലുലാര്‍\' എന്ന വാക്കുകൊണ്ട്‌ വിശേഷിപ്പിക്കുന്നത്‌.', '', NULL),
(67, 'acervate', 'പുഞ്‌ജിതം', '(bot) കൂട്ടം ചേര്‍ന്നു വളരുന്ന, വളരെ അടുത്തടുത്തായി വളരുന്ന', '', NULL),
(68, 'acetabulum', 'എസെറ്റാബുലം', 'നാല്‍ക്കാലി കശേരുകികളുടെ ശ്രാണീവലയത്തില്‍, തുടയെല്ലിന്റെ ഉരുണ്ട അറ്റം ചേര്‍ക്കുവാനായുള്ള, കപ്പിന്റെ ആകൃതിയിലുള്ള നിമ്‌നഭാഗം.', '', NULL),
(69, 'acetaldehyde', 'അസറ്റാല്‍ഡിഹൈഡ്‌ ', 'നിറമില്ലാത്ത, ബാഷ്‌പീകരണ ശീലമുള്ള ദ്രാവകം. തിളനില 210C . സവിശേഷ തീക്ഷ്‌ണഗന്ധം, ജലത്തെ അപേക്ഷിച്ച്‌ ഘനത്വം കുറവ്‌. ജലം, ആല്‍ക്കഹോള്‍, ഈഥര്‍ തുടങ്ങിയ ലായകങ്ങളില്‍ ലയിക്കും IUPAC നാമം എഥനാല്‍.', '', NULL),
(70, 'acetamide', 'അസറ്റാമൈഡ്‌ ', 'CH3−CO−NH2. നിറമില്ലാത്ത ക്രിസ്റ്റലീയ ഖരം. ഉരുകല്‍ നില 82 0 C. മണമില്ല. ജലം, ആല്‍ക്കഹോള്‍, ഈഥര്‍ എന്നീ ദ്രാവകങ്ങളില്‍ ലയിക്കും. അമോണിയം അസറ്റേറ്റ്‌ തപിപ്പിച്ച്‌ അസറ്റാമൈഡ്‌ ഉണ്ടാക്കാം.', '', NULL),
(71, 'acetate', 'അസറ്റേറ്റ്‌', 'അസറ്റിക്‌ അമ്ലത്തിന്റെ ലവണം. ഉദാ: സോഡിയം അസറ്റേറ്റ്‌ CH3−COOH+NaOH→CH3COO−Na+H2O', '', NULL),
(72, 'acetic acid', 'അസറ്റിക്‌ അമ്ലം', 'CH3−COOH. നിറമില്ലാത്ത, തീക്ഷ്‌ണ ഗന്ധമുള്ള ദ്രാവകം. ദേഹത്ത്‌ വീണാല്‍ പൊള്ളും. 16.50C ല്‍ താഴെ ശീതീകരിച്ചാല്‍ ഐസ്‌പോലുള്ള ഖരം ലഭിക്കും. ഗ്ലേഷ്യല്‍ അസറ്റിക്‌ അമ്ലം എന്ന്‌ ഇതിനെ വിളിക്കുന്നു. തിളനില 118 0 C. ജലം ആല്‍ക്കഹോള്‍, ഈഥര്‍ എന്നീ ലായകങ്ങളില്‍ ലയിക്കും. ആല്‍ക്കഹോളിന്റെ പുളിപ്പിക്കല്‍ വഴി നിര്‍മ്മിക്കാം. IUPAC നാമം എഥനോയിക്‌ അമ്ലം.', '', NULL),
(73, 'acetoin', 'അസിറ്റോയിന്‍', '3-ഹൈഡ്രാക്‌സി-2 ബ്യൂട്ടനോന്‍. സുഗന്ധ തൈലങ്ങള്‍ ഉണ്ടാക്കാന്‍ ഉപയോഗിക്കുന്ന മഞ്ഞനിറത്തിലുള്ള ഒരു ദ്രാവകം.', '', NULL),
(74, 'acetone', 'അസറ്റോണ്‍', 'CH3−CO−CH3, നിറമില്ലാത്ത സുഖകരമായ ഗന്ധമുള്ള ദ്രാവകം. തിളനില 550C. ജലം, ആല്‍ക്കഹോള്‍, ഈഥര്‍ എന്നീ ലായകങ്ങളില്‍ ലയിക്കും. കാത്സ്യം അസറ്റേറ്റിന്റെ സ്വേദനം വഴി നിര്‍മ്മിക്കാം. ( CH3COO)2 Ca→CH3−CO−CH3+CaCO3. അസറ്റോണ്‍ നല്ലൊരു ലായകമാണ്‌.', '', NULL),
(75, 'acetonitrile', 'അസറ്റോനൈട്രില്‍', 'CH3−CN, മീഥൈല്‍ സയനൈഡിന്റെ ( IUPAC ഈഥേന്‍ നൈട്രല്‍) മറ്റൊരു പേര്‌. നിറമില്ലാത്ത വിഷപ്രഭാവമുള്ള ദ്രാവകം. ഈഥറിന്റെ ഗന്ധം. ജലത്തില്‍ ലയിക്കും.', '', NULL),
(76, 'acetyl', 'അസറ്റില്‍', 'സംയോജകത ഒന്നായിട്ടുള്ള റാഡിക്കല്‍.', '', NULL),
(77, 'acetyl chloride', 'അസറ്റൈല്‍ ക്ലോറൈഡ്‌', 'CH3CO Cl, തീക്ഷ്‌ണ ഗന്ധമുള്ള നിറമില്ലാത്ത ദ്രാവകം. വായുവില്‍ പുകയും. ഗ്ലേഷ്യല്‍ അസറ്റിക്‌ അമ്ലവും ഫോസ്‌ഫറസ്‌ പെന്റാക്ലോറൈഡും തമ്മിലുള്ള പ്രതിപ്രവര്‍ത്തനം വഴി നിര്‍മിക്കാം. CH3COOH+PCl5→CH3⎯CO⎯C+POCl3', '', NULL),
(78, 'acetyl number', 'അസറ്റൈല്‍ നമ്പര്‍', 'എണ്ണകളിലും കൊഴുപ്പുകളിലുമുള്ള സ്വതന്ത്ര ഹൈഡ്രാക്‌സില്‍ ( −OH) ഗ്രൂപ്പുകളുടെ ഒരളവ്‌.', '', NULL),
(79, 'acetyl salicylic acid', 'അസറ്റൈല്‍ സാലിസിലിക്‌ അമ്ലം', 'CH3−COO−C6H4−COOH, വെള്ള നിറമുള്ള ഖര പദാര്‍ഥം. ജലത്തില്‍ അല്‌പാല്‌പം ലയിക്കും. ജ്വരശമനി. ആസ്‌പിരിന്‍ എന്നും പേരുണ്ട്‌.', '', NULL),
(80, 'Acetylation', 'അസറ്റലീകരണം', 'ആല്‍ക്കഹോള്‍, ഫീനോള്‍, അമീനുകള്‍ എന്നീ സംയുക്തങ്ങളിലെ −OH,−NH2 ഗ്രൂപ്പുകളിലുള്ള ഹൈഡ്രജന്‍ വിസ്ഥാപിച്ച്‌ അസറ്റൈല്‍( ) ഗ്രൂപ്പു ചേര്‍ക്കുന്ന പ്രക്രിയ. അസറ്റൈല്‍ ക്ലോറൈഡ്‌, അസറ്റിക്‌ അണ്‍ഹൈഡ്രഡ്‌ എന്നിവയാണ്‌ അസറ്റലീകരണത്തിന്‌ ഉപയോഗിക്കുന്നത്‌. ഉദാ: CH3-CO-Cl+C2H5OH→CH3-COO-C2H5+HCl അസറ്റൈല്‍ എഥനോള്‍ ഈഥൈല്‍ അസറ്റേറ്റ്‌  ക്ലോറൈഡ്‌', '', NULL),
(81, 'acetylcholine', 'അസറ്റൈല്‍കോളിന്‍', 'ആക്‌സോണുകളുടെ അഗ്രഭാഗത്ത്‌ ആവേഗങ്ങള്‍ എത്തുമ്പോള്‍ ഉത്‌പാദിപ്പിക്കുന്ന രാസികം. ആവേഗങ്ങള്‍ സൈനാപ്‌സിലൂടെ കടന്നുപോകാന്‍ സഹായിക്കുന്നു.', '', NULL),
(82, 'acetylene', 'അസറ്റിലീന്‍', '(ഈഥൈന്‍) C2H2, ശുദ്ധരൂപത്തില്‍ നിറമില്ലാത്ത, സുഖകരമായ ഗന്ധമുള്ള വാതകം. കാത്സ്യം കാര്‍ബൈഡും ജലവുമായുള്ള പ്രതിപ്രവര്‍ത്തനം വഴി നിര്‍മ്മിക്കാം. CaC2+2H2O → C2H2+Ca(OH)2. ലോഹവെല്‍ഡനത്തിനും ലോഹം മുറിക്കാനും ഓക്‌സി അസറ്റിലീന്‍ ജ്വാല ഉപയോഗിക്കുന്നു.', '', NULL),
(83, 'achene', 'അക്കീന്‍', 'ഒറ്റ വിത്തുള്ളതും പൊട്ടിത്തുറക്കാത്തതും ഏക അണ്ഡപര്‍ണത്തില്‍ നിന്ന്‌ ഉണ്ടാവുന്നതുമായ ഫലം. ഉദാ: സൂര്യകാന്തി.', '', NULL),
(84, 'achilles tendon', 'അക്കിലെസ്‌ സ്‌നായു', 'കണങ്കാലിന്റെ പിന്നിലുള്ള പേശികളെ പാദത്തിന്റെ അഗ്രവുമായി ബന്ധിപ്പിക്കുന്ന സ്‌നായു.', '', NULL),
(85, 'achlamydeous', 'അപരിദളം', 'പുഷ്‌പങ്ങളില്‍ വിദളവും ദളവും ഇല്ലാത്ത അവസ്ഥ.', '', NULL),
(86, 'achondroplasia', 'അകോണ്‍ഡ്രാപ്ലാസിയ', 'ശരീരത്തിലെ നീളം കൂടിയ അസ്ഥികളുടെ (കൈ, കാല്‌) വളര്‍ച്ച മുരടിക്കുന്ന അവസ്ഥ. ഇതുമൂലം വാമനത്വം ഉണ്ടാകുന്നു. ബുദ്ധിശക്തിയെ ബാധിക്കുകയില്ല.', '', NULL),
(87, 'achromasia', 'അവര്‍ണകത', 'ത്വക്കിലെ വര്‍ണകത്തിന്റെ അഭാവം.', '', NULL),
(88, 'achromatic lens', 'അവര്‍ണക ലെന്‍സ്‌', 'വര്‍ണ വിപഥനം സൃഷ്‌ടിക്കാത്ത ലെന്‍സ്‌. പൊതുവേ, വ്യത്യസ്‌ത ഗ്ലാസ്സുകള്‍ കൊണ്ട്‌ നിര്‍മിച്ച രണ്ടോ അതിലധികമോ ലെന്‍സുകള്‍ ചേര്‍ന്നതാണ്‌ ഇത്‌. അക്രാമാറ്റിക്‌ ലെന്‍സ്‌ ', '', NULL),
(89, 'achromatic prism', 'അവര്‍ണക പ്രിസം', 'പ്രകാശ രശ്‌മികളെ സ്‌പെക്‌ട്രമായി വേര്‍തിരിക്കാത്ത പ്രിസം. വ്യത്യസ്‌ത പ്രകാശസാന്ദ്രതയുള്ള സ്‌ഫടികം കൊണ്ടു നിര്‍മിച്ച രണ്ടോ അതിലധികമോ പ്രിസങ്ങള്‍ ചേര്‍ത്താണ്‌ ഉണ്ടാക്കുന്നത്‌. എല്ലാ നിറങ്ങള്‍ക്കും ഒരേ വ്യതിചലനം നല്‍കുന്നതിനാലാണ്‌ സ്‌പെക്‌ട്രം ഉണ്ടാകാത്തത്‌.', '', NULL),
(90, 'achromatopsia', 'വര്‍ണാന്ധത', 'അക്രാമറ്റോപ്‌സിയ. നിറങ്ങള്‍ തിരിച്ചറിയാന്‍ കഴിയാത്ത അവസ്ഥ.', '', NULL),
(91, 'acid', 'അമ്ലം', 'രാസ പ്രവര്‍ത്തനത്തിലേര്‍പ്പെടുമ്പോള്‍ ജലത്തിന്റെ സാന്നിദ്ധ്യത്തില്‍ ഹൈഡ്രജന്‍ അയോണിനെ വിട്ടുകൊടുക്കാന്‍ കഴിയുന്നതോ അല്ലെങ്കില്‍ ഒരു കോ-ഓര്‍ഡിനേറ്റ്‌ സഹസംയോജക ബന്ധനം ഉണ്ടാക്കാന്‍ ഒരു ജോഡി ഇലക്‌ട്രാണുകളെ സ്വീകരിക്കുന്നതോ ആയ പദാര്‍ഥം. അരീനിയസ്‌ സിദ്ധാന്തപ്രകാരം ജലത്തിന്റെ സാന്നിദ്ധ്യത്തില്‍ H+നെ വിട്ടുകൊടുക്കുന്നതാണ്‌ ആസിഡ്‌.', '', NULL),
(92, 'acid anhydrides', 'അമ്ല അണ്‍ഹൈഡ്രഡുകള്‍', 'കാര്‍ബോക്‌സിലിക്‌ ആസിഡുകളില്‍ നിന്ന്‌ ജല തന്മാത്ര നീക്കം ചെയ്യുക വഴി ലഭിക്കുന്ന കാര്‍ബണിക സംയുക്തങ്ങള്‍.', '', NULL),
(93, 'acid dye', 'അമ്ല വര്‍ണകം', 'സില്‍ക്കിലും കമ്പിളിയിലും ചായം കയറ്റാന്‍ ഉപയോഗിക്കുന്ന കാര്‍ബണിക അമ്ലങ്ങളുടെ സോഡിയം ലവണങ്ങള്‍.', '', NULL),
(94, 'acidimetry', ' അസിഡിമെട്രി', 'അമ്ല നിര്‍ണയം ടൈട്രഷനിലൂടെ ഒരു ലായനിയിലുള്ള അമ്ലത്തിന്റെ അളവ്‌ നിര്‍ണയിക്കുന്ന പ്രക്രിയ.', '', NULL),
(95, 'acidolysis', ' അസിഡോലൈസിസ്‌', 'അമ്ലം ഉപയോഗിച്ചുള്ള ജലവിശ്ലേഷണം.', '', NULL),
(96, 'Acid radical', 'അമ്ല റാഡിക്കല്‍', 'അമ്ലത്തില്‍ നിന്ന്‌ ഹൈഡ്രജന്‍ നഷ്‌ടപ്പെട്ട്‌ കിട്ടുന്ന റാഡിക്കല്‍. ഉദാ അസറ്റേറ്റ്‌ റാഡിക്കല്‍.', '', NULL),
(97, 'acid rain', 'അമ്ല മഴ', 'ഉയര്‍ന്ന അളവില്‍ അമ്ലതയുള്ള മഴ. അന്തരീക്ഷ മലിനീകരണത്തിന്റെ ഫലമായി സള്‍ഫര്‍ സംയുക്തങ്ങള്‍ മഴയില്‍ അലിഞ്ഞുചേരുന്നതുമൂലമാണിതുണ്ടാകുന്നത്‌. ഫോസില്‍ ഇന്ധനങ്ങളായ കല്‍ക്കരിയും എണ്ണയും കത്തുമ്പോഴാണ്‌ ഈ അന്തരീക്ഷ മലിനീകരണമുണ്ടാകുന്നത്‌.', '', NULL),
(98, 'acid rock', 'അമ്ല ശില', '66 ശതമാനത്തില്‍ കൂടുതല്‍ മിശ്രിത സിലിക്ക അടങ്ങിയ ഒരു ആഗ്നേയ ശില. ഇതിന്റെ ക്രിസ്റ്റലില്‍ ശുദ്ധ ക്വാര്‍ട്‌സ്‌ ഉണ്ടാകും.', '', NULL),
(99, 'acid salt', 'അമ്ല ലവണം', 'അമ്ല ഹൈഡ്രജനുകള്‍ ലവണങ്ങളാല്‍ അഥവാ കാറ്റയോണുകളാല്‍ ഭാഗികമായി വിസ്ഥാപനം ചെയ്യപ്പെടുമ്പോള്‍ ഉണ്ടാകുന്ന ലവണങ്ങള്‍. ഉദാ: സോഡിയം ഹൈഡ്രജന്‍ സള്‍ഫേറ്റ്‌ NaHSO4. സള്‍ഫ്യൂറിക്‌ അമ്ലത്തിന്റെ ( H2SO4) ഒരു ഹൈഡ്രജന്‍ മാത്രം സോഡിയം അയോണാല്‍ വിസ്ഥാപനം ചെയ്യപ്പെടുമ്പോള്‍ ഉണ്ടാകുന്ന ലവണം. ഇതിന്‌ അമ്ല ഗുണമുണ്ടായിരിക്കും.', '', NULL),
(100, 'acid value', 'അമ്ല മൂല്യം', 'കൊഴുപ്പുകള്‍, എണ്ണകള്‍, റെസിനുകള്‍, ലായകങ്ങള്‍ തുടങ്ങിയ പദാര്‍ഥങ്ങളിലുള്ള സ്വതന്ത്ര അമ്ലത്തിന്റെ അളവിനെ സൂചിപ്പിക്കുന്ന പദം. ഒരു ഗ്രാം പദാര്‍ഥത്തില്‍ ഉപസ്ഥിതമായ സ്വതന്ത്ര അമ്ലത്തെ നിര്‍വ്വീര്യമാക്കാന്‍ എത്ര മില്ലിഗ്രാം പൊട്ടാസ്യം ഹൈഡ്രാക്‌സൈഡ്‌ വേണമോ അതിനെയാണ്‌ ആ പദാര്‍ഥത്തിന്റെ അമ്ലമൂല്യം എന്നു പറയുന്നത്‌.', '', NULL),
(101, 'acidic oxide', 'അലോഹ ഓക്‌സൈഡുകള്‍', 'ക്ഷാരവുമായി പ്രവര്‍ത്തിച്ച്‌ ലവണങ്ങളുണ്ടാക്കുന്ന രാസപദാര്‍ഥം. ജലീയ ലായനിക്ക്‌ അമ്ല സ്വഭാവമുണ്ടായിരിക്കും. ഉദാ: കാര്‍ബണ്‍ ഡയോക്‌സൈഡ്‌, സള്‍ഫര്‍ ഡയോക്‌സൈഡ്‌.', '', NULL),
(102, 'aciniform', 'മുന്തിരിക്കുല രൂപമുള്ള', 'ദ്രാക്ഷാകാര', '', NULL),
(103, 'aclinic', 'അക്ലിനിക്‌', 'ഭൂകാന്തക്ഷേത്രം തികച്ചും തിരശ്ചീനമായിരിക്കുന്ന ബിന്ദുക്കളെ (കാന്തിക നതി പൂജ്യം) ബന്ധിപ്പിക്കുന്ന സാങ്കല്‍പ്പിക രേഖ.', '', NULL),
(104, 'acoelomate', 'എസിലോമേറ്റ്‌ ', 'സീലോം എന്ന ശരീരഗഹ്വരം ഇല്ലാത്ത ജന്തുക്കള്‍. ഹൈഡ്ര, വിരകള്‍ എന്നിവയെല്ലാം ഇതില്‍പ്പെടും.', '', NULL),
(105, 'acoustics', 'ധ്വനിശാസ്‌ത്രം', 'ശബ്‌ദത്തെ സംബന്ധിച്ച പഠനശാഖ.', '', NULL),
(106, 'acquired characters', 'ആര്‍ജിത സ്വഭാവങ്ങള്‍', 'നിരന്തരമായ ഉപയോഗം കൊണ്ടോ ഏതെങ്കിലും പ്രത്യേക പാരിസ്ഥിതിക ഘടകവുമായുള്ള പ്രതിപ്രവര്‍ത്തനം കൊണ്ടോ ശരീരത്തിലുണ്ടാകുന്ന മാറ്റങ്ങള്‍. ഉദാ: പതിവായി വ്യായാമം ചെയ്യുന്നവരില്‍ മാംസപേശികള്‍ക്കുണ്ടാവുന്ന വികാസം. കൂടുതല്‍ സൂര്യപ്രകാശം ഏല്‍ക്കുന്നവരുടെ തൊലിയുടെ നിറം കൂടുതല്‍ കറുപ്പാകുന്നത്‌.', '', NULL),
(107, 'acranthus', 'അഗ്രപുഷ്‌പി', 'അഗ്രഭാഗത്ത്‌ പൂക്കളുണ്ടാവുന്നത്‌.', '', NULL),
(108, 'acre', 'ഏക്കര്‍', 'ഭൂവിസ്‌തീര്‍ണം അളക്കുന്നതിന്‌ ഉപയോഗിക്കുന്ന ഒരു ഏകകം. നൂറ്‌ സെന്റ്‌ ആണ്‌ ഒരു ഏക്കര്‍. 43,560 ചതുരശ്ര അടിക്ക്‌ തുല്യം. 1 ഏക്കര്‍ = 0.4047 ഹെക്‌ടര്‍.', '', NULL),
(109, 'acrocentric chromosome', 'ആക്രാസെന്‍ട്രിക്‌ ക്രാമസോം', 'സെന്‍ട്രാമിയര്‍ ഭാഗം ഒരറ്റത്തോ അതിന്‌ വളരെ അടുത്തോ ഉള്ള ക്രാമസോം.', '', NULL),
(110, 'acromegaly', 'അക്രാമെഗലി', 'കൈപ്പത്തി, പാദം, മുഖം ഇവയുടെ അസാധാരണമായ വലുപ്പം വയ്‌ക്കല്‍. വളര്‍ച്ചയെ നിയന്ത്രിക്കുന്ന ഹോര്‍മോണിന്റെ ഉത്‌പാദനത്തിലുണ്ടാകുന്ന അപാകതയാണ്‌ ഇതിന്‌ കാരണം.', '', NULL),
(111, 'acropetal', 'അഗ്രാന്മുഖം', 'അടിഭാഗത്തു നിന്നും അഗ്രഭാഗത്തേക്കുള്ളത്‌. ഉദാ: പൂങ്കുലകളില്‍ അടിഭാഗത്ത്‌ പ്രായം കൂടിയ പൂക്കളും അഗ്രഭാഗത്ത്‌ പ്രായം കുറഞ്ഞ പൂക്കളും കാണുന്നത്‌.', '', NULL),
(112, 'acrosome', 'അക്രാസോം', 'ജന്തുക്കളുടെ പുംബീജത്തിന്റെ തലയില്‍ കാണുന്ന സഞ്ചിപോലുള്ള ഭാഗം. ഇതിലെ എന്‍സൈമുകളുടെ സഹായത്താലാണ്‌ പുംബീജം അണ്ഡ ത്തിന്റെ അകത്തേക്ക്‌ തുളച്ചു കയറുന്നത്‌.', '', NULL),
(113, 'actin', 'ആക്‌റ്റിന്‍', 'മാംസ പേശികളിലുള്ള പ്രധാനമായ ഒരു പ്രാട്ടീന്‍. ഇത്‌ രണ്ടുവിധമുണ്ട്‌. G-actin ഗോളാകൃതിയില്‍ ഉള്ള ഒറ്റ യൂണിറ്റായിട്ടുള്ളതും, F-actin പല യൂണിറ്റുകള്‍ ചേര്‍ന്ന്‌ നാരുപോലെയുള്ളതും ആണ്‌.', '', NULL),
(114, 'actinides', 'ആക്‌ടിനൈഡുകള്‍', 'ആവര്‍ത്തനപ്പട്ടികയില്‍ ആക്‌ടിനിയത്തെത്തുടര്‍ന്ന്‌ വരുന്ന പതിനാല്‌ മൂലകങ്ങള്‍. ആവര്‍ത്തന പട്ടികയ്‌ക്ക്‌ താഴെ പ്രത്യേകം ശ്രണിയായി കൊടുത്തിരിക്കും. റേഡിയോ ആക്‌റ്റീവത കാണിക്കുന്ന മൂലകങ്ങളാണിവ. ആക്‌ടിനിയത്തെയും ഇതില്‍ ഉള്‍പ്പെടുത്താറുണ്ട്‌.', '', NULL),
(115, 'actinometer', 'ആക്‌റ്റിനോ മീറ്റര്‍', 'വിദ്യുത്‌ കാന്തിക വികിരണത്തിന്റെ തീവ്രത അളക്കാന്‍ ഉപയോഗിക്കുന്ന ഉപകരണം. പ്രകാശ വൈദ്യുതി പ്രഭാവമാണ്‌ ഇതില്‍ പ്രയോജനപ്പെടുത്തുന്നത്‌.', '', NULL),
(116, 'actinomorphic', 'പ്രസമം', 'നെടുകെ ഛേദിച്ചാല്‍ രണ്ടു തുല്യഭാഗങ്ങള്‍ കിട്ടുന്ന രൂപമുള്ളത്‌. പുഷ്‌പം, ഫലം തുടങ്ങിയ സസ്യഭാഗങ്ങള്‍ക്ക്‌ ഛേദ മാതൃകയുടെ അടിസ്ഥാനത്തില്‍ ഈ പദം പ്രയോഗിക്കാം. ചിത്രം flower നോക്കുക.', '', NULL),
(117, 'action', 'ആക്‌ഷന്‍ ', 'ഒരു നിശ്ചിത ഹ്രസ്വ സമയാന്തരാളത്തില്‍ പ്രയോഗിക്കുന്ന ബലവും സമയാന്തരാളവും തമ്മിലുള്ള ഗുണനഫലം. (F.Δt)', '', NULL),
(118, 'action potential', 'ആക്‌ഷന്‍ പൊട്ടന്‍ഷ്യല്‍', 'നാഡികളില്‍ ആവേഗം ഉത്‌പാദിപ്പിക്കപ്പെടുമ്പോള്‍ നാഡീകോശത്തിന്റെ അകത്തും പുറത്തുമുള്ള വൈദ്യുത പൊട്ടന്‍ഷ്യല്‍ വ്യത്യാസത്തില്‍ ഉണ്ടാകുന്ന വ്യതിയാനം. ഉത്തേജിപ്പിക്കപ്പെടാത്ത നാഡീകോശത്തിന്റെ ഉള്‍ഭാഗം ബാഹ്യപരിസരത്തെ അപേക്ഷിച്ച്‌ ഋണമായിരിക്കും. ആക്‌ഷന്‍ പൊട്ടന്‍ഷ്യലില്‍ ഉള്‍ഭാഗം ധനവും പുറത്ത്‌ ഋണവുമാകും.', '', NULL),
(119, 'action spectrum', ' ആക്‌ഷന്‍ സ്‌പെക്‌ട്രം', 'ഫിസിയോളജീയ പ്രക്രിയകള്‍ക്ക്‌ കാരണമായ പ്രകാശ തരംഗങ്ങള്‍. ഉദാ: പ്രകാശസംശ്ലേഷണം നടക്കുമ്പോള്‍ ചുവപ്പും നീലയുമാണ്‌ ഏറ്റവും കൂടുതല്‍ ആഗിരണം ചെയ്യപ്പെടുന്നത്‌. ഫിസിയോളജീയ പ്രവര്‍ത്തനങ്ങളില്‍ പ്രകാശത്തിന്റെ ഘടകതരംഗങ്ങളുടെ സ്വാധീനം വ്യക്തമാക്കുന്ന ഗ്രാഫും ഈ പേരില്‍ അറിയപ്പെടുന്നു.', '', NULL),
(120, 'activated charcoal', 'ഉത്തേജിത കരി', 'ക്രിയാശീലകരി, നീരാവി പ്രവാഹത്തില്‍ ചൂടാക്കി മാലിന്യങ്ങള്‍ നിര്‍മ്മാര്‍ജനം ചെയ്‌ത കരി. നിറങ്ങളും വാതകങ്ങളും ശോഷണം ചെയ്യാന്‍ ഉപയോഗിക്കാം.', '', NULL),
(121, 'activated complex', 'ആക്‌ടിവേറ്റഡ്‌ കോംപ്ലക്‌സ്‌', 'രാസപ്രവര്‍ത്തനത്തിന്റെ ആദ്യഘട്ടത്തില്‍ അഭികാരക തന്മാത്രകള്‍ ചേര്‍ന്നുണ്ടാകുന്ന സംയുക്തം. ഇത്‌ ഉയര്‍ന്ന ഊര്‍ജനിലയിലുള്ളതും അതിനാല്‍ അസ്ഥിരവുമായിരിക്കും. ഈ സംയുക്തം വിഘടിച്ചാണ്‌ ഉത്‌പന്നങ്ങളായിത്തീരുന്നത്‌. അസ്ഥിരമായതിനാല്‍ വേര്‍തിരിച്ചെടുക്കുവാന്‍ കഴിയില്ല.', '', NULL),
(122, 'activated state', 'ഉത്തേജിതാവസ്ഥ', 'ഒരു രാസ അഭിക്രിയയുടെ മാധ്യമിക അവസ്ഥ.', '', NULL),
(123, 'activation energy', 'ആക്‌ടിവേഷന്‍ ഊര്‍ജം', '-', 'threshold energy', NULL),
(124, 'activator', 'ഉത്തേജകം', 'ക്രിയാകാരകം, ഒരു രാസപ്രവര്‍ത്തനത്തിലെ ഉല്‍പ്രരകത്തിന്റെ ശേഷി മെച്ചപ്പെടുത്താന്‍ ഉപയോഗിക്കുന്ന പദാര്‍ഥം. ഉദാ: ഹേബര്‍ പ്രക്രിയ അനുസരിച്ചുള്ള അമോണിയാ നിര്‍മ്മാണത്തില്‍ ഉല്‍പ്രരകമായ ഇരുമ്പിന്റെ ഉല്‍പ്രരക ശക്തി വര്‍ധിപ്പിക്കാനായി മോളിബ്‌ഡിനമോ പൊട്ടാസ്യത്തിന്റെ ഓക്‌സൈഡോ ഉപയോഗിക്കുന്നു.', '', NULL),
(125, 'active centre', 'ഉത്തേജിത കേന്ദ്രം', 'രാസ അഭിക്രിയകള്‍ ആരംഭിക്കുന്ന ഉല്‍പ്രരകത്തിന്റെ ഉപരിതലത്തിലെ ബിന്ദുക്കള്‍.', '', NULL),
(126, 'active margin', 'സജീവ മേഖല', 'ഭൂകമ്പങ്ങള്‍, അഗ്നിപര്‍വത പ്രവര്‍ത്തനങ്ങള്‍, പര്‍വതനം തുടങ്ങിയ പ്രക്രിയകള്‍ സര്‍വസാധാരണമായ വന്‍കര ഫലക സന്ധികള്‍. സന്ധിക്കുന്നതോ ഉരസി നീങ്ങുന്നതോ ആയ ഫലകാതിരുകളായിരിക്കുമിവ.', '', NULL),
(127, 'active mass', 'ആക്‌ടീവ്‌ മാസ്‌', 'അഭികാരകത്തിന്റെ പ്രഭാവിത ഗാഢത അല്ലെങ്കില്‍ അതിന്റെ ആക്‌റ്റീവത. ആക്‌റ്റീവത \" a\' =γc ഇവിടെ \" c\' ഗാഢതയും \" γ\' ആക്‌റ്റിവിറ്റിയും ആണ്‌. മിക്കപ്പോഴും γ=1 ആകയാല്‍ ആക്‌റ്റീവതയും ഗാഢതയും തുല്യമായിരിക്കും. ഉദാ:- [HI] ഹൈഡ്രജന്‍ അയൊഡൈഡിന്റെ ആക്‌റ്റീവ്‌ മാസിനെ സൂചിപ്പിക്കുന്നു.', '', NULL),
(128, 'active site', 'ആക്‌റ്റീവ്‌ സൈറ്റ്‌', 'ഒരു എന്‍സൈം അഭിക്രിയ നടത്തേണ്ടുന്ന തന്മാത്രയുമായി ബന്ധം സ്ഥാപിക്കുകയും അഭിക്രിയ നടത്തി സബ്‌സ്‌ട്രറ്റിനെ (തന്മാത്രയെ) രാസപരിവര്‍ത്തനം നടത്തുകയും ചെയ്യുന്ന കേന്ദ്രം. എന്‍സൈമിന്റെ ആകെ വ്യാപ്‌തത്തിലെ ചെറിയ ഒരു ഭാഗം മാത്രമാണിത്‌. എന്‍സൈം ഘടനയിലെ അമിനോ അമ്ലങ്ങളുടെ പ്രത്യേക വിന്യാസ ക്രമീകരണത്താല്‍ രൂപീകരിക്കപ്പെട്ട ത്രിമാന ഘടനയുള്ള പ്രത്യേക ഭാഗമാണിത്‌.', '', NULL),
(129, 'active transport', 'സക്രിയ പരിവഹനം', 'കോശസ്‌തരത്തിലൂടെ സാന്ദ്രത കുറഞ്ഞ ഭാഗത്തു നിന്ന്‌ കൂടിയ ഭാഗത്തേക്ക്‌ അയോണുകളും മറ്റും വ്യാപിക്കുന്നത്‌. ചില വാഹക പ്രാട്ടീനുകള്‍ ഇതിന്‌ സഹായിക്കുന്നു. ATP യുടെ ഊര്‍ജം ഉപയോഗിച്ചാണ്‌ ഇത്തരം പരിവഹനം നടക്കുക.', '', NULL),
(130, 'activity', 'ആക്‌റ്റീവത', 'റേഡിയോ ആക്‌റ്റീവതയുടെ അളവ്‌. ഒരു സെക്കന്റില്‍ നടക്കുന്ന വിഘടനങ്ങളുടെ എണ്ണം ആണിത്‌. Becquerel, Curie നോക്കുക.', '', NULL),
(131, 'activity coefficient', 'സക്രിയതാ ഗുണാങ്കം', 'ഒരു രാസവ്യൂഹത്തിലെ താപഗതിക ആക്‌റ്റിവിറ്റിക്ക്‌ തുല്യമാകാന്‍ വേണ്ടി ഒരു പദാര്‍ഥത്തിന്റെ മോളാര്‍ ഗാഢതയെ ഏത്‌ ഘടകം കൊണ്ട്‌ ഗുണിക്കാമോ അതിനെയാണ്‌ ആക്‌റ്റീവതാഗുണാങ്കം എന്നു പറയുന്നത്‌. പ്രതീകം γ', '', NULL),
(132, 'activity series', 'ആക്‌റ്റീവതാശ്രണി', '-', 'electro chemical series ', NULL),
(133, 'acupuncture', 'അക്യുപങ്‌ചര്‍', 'ചൈനയില്‍ കണ്ടുപിടിക്കപ്പെട്ട ചികിത്സാരീതി. ചില പ്രത്യേക രോഗങ്ങളുടെ ചികിത്സയ്‌ക്കും ശസ്‌ത്രക്രിയയില്‍ ശരീരഭാഗങ്ങള്‍ മരവിപ്പിക്കുന്നതിനും ഉപയോഗിക്കുന്നു. വളരെ ചെറിയ സൂചികള്‍ ശരീരത്തിന്റെ ചില പ്രത്യേക ഭാഗങ്ങളില്‍ കുത്തിയിറക്കുന്നതാണ്‌ ചികിത്സാ രീതി.', '', NULL),
(134, 'acute angle', 'ന്യൂനകോണ്‍', '90 ഡിഗ്രിയില്‍ കുറവായ കോണ്‍', '', NULL),
(135, 'acute angled triangle', 'ന്യൂനത്രികോണം', 'എല്ലാ കോണുകളും ന്യൂനകോണുകളായ ത്രികോണം.', '', NULL),
(136, 'acyl', 'അസൈല്‍', 'കാര്‍ബോക്‌സിലിക്‌ അമ്ലത്തിന്റെ ക്രിയാത്മക ഗ്രൂപ്പില്‍ നിന്ന്‌ ഹൈഡ്രാക്‌സില്‍ (- OH) ഗ്രൂപ്പ്‌ നീക്കിയാല്‍ ലഭിക്കുന്ന കാര്‍ബണിക റാഡിക്കല്‍. പൊതുരാസസൂത്രം', '', NULL),
(137, 'acylation', 'അസൈലേഷന്‍', 'OH−NH2 എന്നീ ക്രിയാത്മക ഗ്രൂപ്പില്‍ നിന്ന്‌ ഹൈഡ്രജനെ മാറ്റി അസൈല്‍ ഗ്രൂപ്പ്‌ ചേര്‍ക്കുന്ന പ്രക്രിയ', '', NULL),
(138, 'adaptation', 'അനുകൂലനം', '1. (പരിണാമപരം) ജീവികളുടെ അതിജീവന ക്ഷമത വര്‍ദ്ധിപ്പിക്കാനുതകുന്ന ലക്ഷണങ്ങള്‍. ഉദാ: താറാവിന്റെ കാല്‍വിരലുകള്‍ക്കിടയിലുള്ള ചര്‍മ്മം വെള്ളത്തില്‍ സഞ്ചരിക്കുവാനുള്ള ഒരു അനുവര്‍ത്തനമാണ്‌. 2. (ഫിസിയോളജീയം) acclimation നോക്കുക. 3. (ഇന്ദ്രിയങ്ങളുടെ പ്രവര്‍ത്തനപരം). പാരിസ്ഥിതിക ഘടകങ്ങള്‍ക്കനുസരിച്ച്‌ ഉത്തേജിപ്പിക്കപ്പെടുവാനുള്ള കഴിവില്‍ വരുന്ന മാറ്റം. ഉദാ: പഞ്ചസാര തിന്ന ഉടനെ പഞ്ചസാരയിട്ട കാപ്പിക്ക്‌ മധുരം പോരെന്ന്‌ തോന്നുന്നത്‌.', '', NULL),
(139, 'adaptive radiation', 'അനുകൂലന വികിരണം', 'പരിണാമ പ്രക്രിയയില്‍, ഒരു പ്രാകൃത ജന്തുരൂപത്തില്‍ നിന്ന്‌ പലതരം ജന്തുക്കള്‍ പരിണമിച്ചുണ്ടാകുന്ന പ്രക്രിയ. അവ വ്യത്യസ്‌ത പാരിസ്ഥിതിക ഘടകങ്ങള്‍ക്കായി അനുവര്‍ത്തനം ചെയ്യപ്പെട്ടവയായിരിക്കും. അതായത്‌ അവയുടെ ജീവിതരീതികള്‍ വ്യത്യസ്‌തമായിരിക്കും.', '', NULL),
(140, 'adaxial', 'അഭ്യക്ഷം', 'അക്ഷത്തോട്‌ അടുത്തുള്ളത്‌. ഉദാ: ഇലയുടെ മുകള്‍ ഭാഗം.', '', NULL),
(141, 'addition', 'സങ്കലനം', 'ഒരു ഗണിത ക്രിയ. രണ്ടോ അതിലധികമോ സംഖ്യകളുടെ തുക കാണുവാന്‍ ഉപയോഗിക്കുന്നത്‌. + ആണ്‌ പ്രതീകം.', '', NULL),
(142, 'addition reaction', 'സംയോജന പ്രവര്‍ത്തനം', 'ഒരു രാസപ്രവര്‍ത്തനത്തില്‍ രണ്ടു തന്മാത്രകള്‍ കൂടിച്ചേര്‍ന്ന്‌ മറ്റൊരു തന്മാത്ര ഉണ്ടാവുന്ന പ്രക്രിയ.', '', NULL),
(143, 'adduct', 'ആഡക്‌റ്റ്‌', 'സംയോജകതയില്‍ മാറ്റമൊന്നുമില്ലാതെ നടക്കുന്ന അഭിക്രിയ വഴി ഉണ്ടാകുന്ന രാസ സംയുക്തം.', '', NULL),
(144, 'adelphous', 'അഭാണ്ഡകം', 'കേസരത്തിന്റെ തണ്ടുകള്‍ എല്ലാം ഒന്നുചേര്‍ന്നും അതേ സമയം പരാഗ കോശങ്ങള്‍ തമ്മില്‍ ചേരാതെയും ഇരിക്കുന്ന അവസ്ഥ.', '', NULL),
(145, 'adenine', 'അഡിനിന്‍', '6 അമിനോ പൂരിന്‍. പൂരിന്‍ എന്ന നൈട്രജന്‍ ബേസിന്റെ അമിനോഡെറിവേറ്റീവ്. ന്യൂക്ലിക്‌ അമ്ലത്തിന്റെ ഒരു ഘടകം. നിറമില്ലാത്ത, അമ്ലത്തിലും ക്ഷാരത്തിലും ലയിക്കുന്ന സംയുക്തം. ഈഥറിലും ക്ലോറോഫോമിലും ലയിക്കുന്നില്ല. ന്യൂക്ലിക്‌ അമ്ലത്തിന്റെ അമ്ലീയ ജല വിശ്ലേഷണം വഴി നിര്‍മ്മിക്കാം.', '', NULL),
(146, 'adenohypophysis', 'അഡിനോഹൈപ്പോഫൈസിസ്‌', 'പിറ്റ്യൂറ്ററി ഗ്രന്ഥിയുടെ ഒരു ഭാഗം. ഇവിടെ നിന്നാണ്‌ തൈറോയ്‌ഡ്‌, അഡ്രീനല്‍, പ്രജനനവുമായി ബന്ധപ്പെട്ട ഹോര്‍മോണുകള്‍ ഉത്‌പാദിപ്പിക്കുന്ന കോശങ്ങള്‍ എന്നിവയെ നിയന്ത്രിക്കുന്ന ഹോര്‍മോണുകള്‍ ഉത്‌പാദിപ്പിക്കപ്പെടുന്നത്‌. TSHþ        തൈറോയ്‌ഡ്‌ സ്റ്റിമുലേറ്റിങ്ങ്‌ ഹോര്‍മോണ്‍        തൈറോയ്‌ഡ്‌ ഗ്രന്ഥിയെ ഉത്തേജിപ്പിക്കുന്നു. LH        ലൂട്ടിനൈസിങ്ങ്‌ ഹോര്‍മോണ്‍        സ്‌ത്രീകളുടെ ആര്‍ത്തവ ചക്രത്തില്‍ മുഖ്യമായ പങ്ക്‌ വഹിക്കുന്നു. പുരുഷന്മാരില്‍ ടെസ്റ്റോസ്റ്റിറോണ്‍ ഉല്‍പാദനത്തെ ഉത്തേജിപ്പിക്കുന്നു. FSH ഫോളിക്കിള്‍ സ്റ്റിമുലേറ്റിങ്ങ്‌ ഹോര്‍മോണ്‍. ഇതും ആര്‍ത്തവ ചക്രത്തില്‍ പ്രധാന പങ്ക്‌ വഹിക്കുന്നു. പുരുഷന്മാരില്‍ ബീജോത്‌പാദനത്തെ നിയന്ത്രിക്കുന്ന ഹോര്‍മോണിന്റെ സ്രവത്തെ നിയന്ത്രിക്കുന്നു. GH ഗ്രാത്ത്‌ ഹോര്‍മോണ്‍.  സൊമാറ്റൊ ട്രാഫിന്‍ അഥവാ വളര്‍ച്ചാ ഹോര്‍മോണ്‍. പ്രാലാക്‌റ്റിന്‍- സസ്‌തനികളില്‍ മുലപ്പാലിന്റെ ഉത്‌പാദനത്തെ ഉത്തേജിപ്പിക്കുന്നു. MSH മെലാനോസൈറ്റ്‌- സ്റ്റിമുലേറ്റിങ്ങ്‌ ഹോര്‍മോണ്‍. ഉഭയ ജീവികളിലും ഉരഗങ്ങളിലും ശരീരത്തിന്റെ നിറം മാറ്റത്തെ നിയന്ത്രിക്കുന്നു.', '', NULL),
(147, 'adenosine triphosphate (ATP)', 'അഡിനോസിന്‍ ട്ര ഫോസ്‌ഫേറ്റ്‌', 'ജീവജാലങ്ങളിലെ പ്രതിപ്രവര്‍ത്തനങ്ങള്‍ക്ക്‌ സ്വതന്ത്ര ഊര്‍ജം നല്‍കുന്ന തന്മാത്ര. ഇതിനെ ജീവജാലങ്ങളിലെ ഊര്‍ജ വിനിമയ നാണയം ( energy currency) എന്നാണ്‌ വിശേഷിപ്പിക്കുന്നത്‌. ജീവികളുടെ നിലനില്‍പ്പിനാധാരമായ പ്രവൃത്തി, തന്മാത്രകളുടെയും അയോണുകളുടെയും ക്രിയാശീല പരിവഹനം, പുതിയ തന്മാത്രകളുടെയും കോശങ്ങളുടെയും നിര്‍മ്മിതി എന്നീ അഭിക്രിയകള്‍ക്ക്‌ തുടര്‍ച്ചയായി സ്വതന്ത്ര ഊര്‍ജം ആവശ്യമാണ്‌. അതിനെല്ലാം ഊര്‍ജം നല്‍കുന്നത്‌ ATP തന്മാത്രയാണ്‌. ഇതിലുള്ള ഫോസ്‌ഫേറ്റ്‌ രാസബന്ധങ്ങളുടെ വിടുതലും കൂടിച്ചേരലുകളും വഴിയാണ്‌ ഇതു സാധിക്കുന്നത്‌. ATP യില്‍ നിന്ന്‌ ഒരു ഫോസ്‌ഫേറ്റ്‌ മാറുമ്പോള്‍ ADPയും രണ്ടു ഫോസ്‌ഫേറ്റു മാറിയാല്‍ AMP യും ഉണ്ടാകും. ഇത്‌ തിരിച്ചും സാധ്യമാണ്‌.', '', NULL),
(148, 'adhesion', 'ഒട്ടിച്ചേരല്‍', 'ആസഞ്‌ജനം. 1. വിഭിന്നതരം തന്മാത്രകള്‍ തമ്മിലുള്ള ഒട്ടിച്ചേരല്‍. 2. വിഭിന്നതരം തന്മാത്രകളുടെ ആകര്‍ഷണഫലമായി പദാര്‍ഥങ്ങള്‍ ഒട്ടിച്ചേരുന്നത്‌.', '', NULL),
(149, 'adhesive', 'അഡ്‌ഹെസീവ്‌', 'ആസഞ്‌ജകം. രണ്ട്‌ പ്രതലങ്ങളെ തമ്മില്‍ ഒട്ടിക്കുന്ന വസ്‌തു. ഉദാ: ഫെവിക്കോള്‍.', '', NULL),
(150, 'adiabatic process', 'അഡയബാറ്റിക്‌ പ്രക്രിയ', 'ഒരു വ്യവസ്ഥയില്‍ നിന്ന്‌ പുറത്തേക്കോ, പുറത്തുനിന്ന്‌ വ്യവസ്ഥയിലേക്കോ താപ കൈമാറ്റം ഇല്ലാതെ നടക്കുന്ന പ്രക്രിയ.', '', NULL),
(151, 'adipic acid', 'അഡിപ്പിക്‌ അമ്ലം', 'HOOC−(CH2)4 −COOH. ഡൈ കാര്‍ബോക്‌സിലിക്‌ അമ്ലം. ക്രിസ്റ്റലീയ രൂപമുള്ള വെളുത്ത വസ്‌തു. ഉരുകല്‍ നില 152 0 C. പോളി അമൈഡുകളുടെ നിര്‍മ്മാണത്തിന്‌ ഉപയോഗിക്കുന്നു. ഉദാ: നൈലോണ്‍ 66 ന്റെ നിര്‍മ്മാണത്തിന്റെ മുഖ്യ ഘടകം.', '', NULL),
(152, 'adipose', 'കൊഴുപ്പുള്ള', 'ഉദാ: ആഡിപ്പോസ്‌ കോശങ്ങള്‍.', '', NULL),
(153, 'Adipose tissue', 'അഡിപ്പോസ്‌ കല', 'ലിപ്പിഡുകള്‍ ശേഖരിച്ചുവെക്കുന്ന വിശേഷവല്‍ക്കരിച്ച സംയോജക കല.', '', NULL),
(154, 'adjacent angles', 'സമീപസ്ഥ കോണുകള്‍', 'രണ്ടു കോണുകള്‍ക്ക്‌ പൊതുവായ ഒരു ഭുജവും ശീര്‍ഷവും ഉണ്ടെങ്കില്‍ അവയെ സമീപസ്ഥ കോണുകള്‍ എന്നു പറയുന്നു.', '', NULL),
(155, 'adjuvant', 'അഡ്‌ജുവന്റ്‌', 'ഔഷധങ്ങളുടെ പ്രവര്‍ത്തനത്തെ സഹായിക്കാന്‍ ചേര്‍ക്കുന്ന പദാര്‍ഥം.', '', NULL),
(156, 'admittance', 'അഡ്‌മിറ്റന്‍സ്‌', 'പ്രവേശ്യത. ഒരു പരിപഥത്തിലൂടെ പ്രത്യാവര്‍ത്തിധാര ( ac) എത്ര കുറഞ്ഞ തടസ്സത്തോടെ കടന്നുപോകുന്നു എന്ന്‌ സൂചിപ്പിക്കുന്ന പദം. കര്‍ണരോധത്തിന്റെ വ്യുല്‍ക്രമത്തിനു തുല്യം.', '', NULL),
(157, 'adnate', 'ലഗ്നം', 'മറ്റൊരുതരം അവയവവുമായി കൂടിച്ചേര്‍ന്ന അവസ്ഥ. ഉദാ: ദളങ്ങളും കേസരങ്ങളും തമ്മില്‍.', '', NULL),
(158, 'adoral', 'അഭിമുഖീയം', 'മുഖത്തിനോട്‌ അടുത്തുള്ള.', '', NULL),
(159, 'adrenal gland', 'അഡ്രീനല്‍ ഗ്രന്ഥി', 'കശേരുകികളില്‍ ഹോര്‍മോണ്‍ ഉത്‌പാദിപ്പിക്കുന്ന ഒരു ഗ്രന്ഥി. മനുഷ്യനിലും സസ്‌തനികളിലും വൃക്കയോടു ചേര്‍ന്ന്‌ ജോടിയായി കാണാം. മെഡുല്ല, കോര്‍ട്ടെക്‌സ്‌ എന്നിങ്ങനെ രണ്ട്‌ ഭാഗങ്ങളുണ്ട്‌. കോര്‍ട്ടെക്‌സില്‍ നിന്ന്‌ ആല്‍ഡോസ്റ്റിറോണ്‍, കോര്‍ട്ടിസോണ്‍, കോര്‍ട്ടിക്കൊസ്റ്റിറോണ്‍ എന്നീ ഹോര്‍മോണുകള്‍ ഉത്‌പാദിപ്പിക്കപ്പെടുന്നു. മെഡുല്ലയില്‍ നിന്ന്‌ സ്രവിക്കപ്പെടുന്ന ഹോര്‍മോണുകള്‍ എപ്പിനെഫ്‌റൈന്‍, നോര്‍ എപ്പിനെഫ്‌റൈന്‍ എന്നിവയാണ്‌.', '', NULL),
(160, 'adrenaline', 'അഡ്രിനാലിന്‍', 'അഡ്രീനല്‍ ഗ്രന്ഥിയുടെ മെഡുല്ലയില്‍ ഉത്‌പാദിപ്പിക്കുന്ന ഒരു ഹോര്‍മോണ്‍.', '', NULL),
(161, 'adsorbate', 'അധിശോഷിതം', '-', 'adsorption ', NULL),
(162, 'adsorbent', 'അധിശോഷകം', '-', 'adsorption ', NULL),
(163, 'adsorption', 'അധിശോഷണം', 'ഏതെങ്കിലും വസ്‌തുവിന്റെ ഉപരിതലത്തിനടുത്തുള്ള പദാര്‍ഥങ്ങള്‍ പ്രസ്‌തുത തലത്തില്‍ പറ്റിപ്പിടിക്കുന്ന പ്രക്രിയ. ഒരു വാതകത്തിന്റെയോ ലായനിയുടെയോ കണികകള്‍ ഒരു ഖരവസ്‌തുവിന്റെയോ ദ്രാവകത്തിന്റെയോ പ്രതലത്തില്‍ പറ്റിപ്പിടിക്കാം. ഉദാ: മരക്കരിക്ക്‌ കാര്‍ബണ്‍ മോണോക്‌സൈഡ്‌, അമോണിയ തുടങ്ങിയ വാതകങ്ങളെ അധിശോഷണം നടത്താന്‍ കഴിയും. ഈ പ്രക്രിയയില്‍ കരി അധിശോഷകം ആണ്‌. കാര്‍ബണ്‍ ഡൈ ഓക്‌സൈഡ്‌ അധിശോഷിതം ആണ്‌.', '', NULL),
(164, 'advection', 'അഭിവഹനം', '1. ദ്രവത്തില്‍ നിന്ന്‌ തിരശ്ചീന തലത്തിലൂടെ സംവേഗം, ഊര്‍ജം, ദ്രവ്യമാനം എന്നിവയുടെ വഹനം. 2. വായുവിന്റെ തിരശ്ചീന ചലനം.', '', NULL),
(165, 'adventitious roots', 'അപസ്ഥാനിക മൂലങ്ങള്‍', 'തായ്‌വേരില്‍ നിന്നല്ലാതെ സസ്യത്തിന്റെ ഇതര ഭാഗങ്ങളില്‍ നിന്ന്‌ വളരുന്ന വേരുകള്‍. ഉദാ: ഏകബീജപത്രികളുടെ വേരുകള്‍.', '', NULL),
(166, 'aeolian', 'ഇയോലിയന്‍', 'വായുവാഹിതം. കാറ്റിന്റെ പ്രവര്‍ത്തന ഫലമായി എന്ന്‌ സൂചിപ്പിക്കുന്ന പദം. ഉദാ: കാറ്റുകൊണ്ട്‌ മണ്ണടിഞ്ഞുണ്ടാകുന്ന മണ്‍കൂമ്പാരങ്ങള്‍ ഇയോലിയന്‍ നിക്ഷേപമാണ്‌. loess നിക്ഷേപങ്ങള്‍ ഇത്തരത്തിലുള്ളവയാണ്‌. eolian എന്നും എഴുതാറുണ്ട്‌.', '', NULL),
(167, 'aerenchyma', 'വായവകല', 'വിസ്‌തൃതമായ വായുസ്ഥലങ്ങളോടു കൂടിയ കല. ജല സസ്യങ്ങളില്‍ ഇതു സാധാരണമാണ്‌. വേരുകളിലൂടെ ഓക്‌സിജന്‍ സ്വീകരണം എളുപ്പമാക്കാനും വെള്ളത്തില്‍ പൊങ്ങിക്കിടക്കാനും സഹായിക്കുന്നു.', '', NULL),
(168, 'aerial', 'ഏരിയല്‍', 'വിദ്യുത്‌കാന്തിക തരംഗങ്ങളെ ചുറ്റുപാടിലേക്ക്‌ പ്രസരിപ്പിക്കാനോ, ചുറ്റുപാടില്‍ നിന്ന്‌ സ്വീകരിക്കാനോ സഹായിക്കുന്ന ഉപാധി. സമാനാര്‍ത്ഥത്തില്‍ പ്രയോഗിക്കുന്ന മറ്റൊരു പദമാണ്‌ ആന്റിന എന്നത്‌.', '', NULL),
(169, 'aerial respiration', 'വായവശ്വസനം', 'വായുവില്‍ നിന്ന്‌ ഓക്‌സിജന്‍ സ്വീകരിക്കുകയും കാര്‍ബണ്‍ ഡൈ ഓക്‌സൈഡ്‌ പുറത്തുവിടുകയും ചെയ്യുന്ന ശ്വസന രീതി.', '', NULL),
(170, 'aerial root', 'വായവമൂലം', 'വായുവില്‍ തൂങ്ങിക്കിടക്കുന്ന വേരുകള്‍. ഉദാ: മരവാഴയുടെ വേരുകള്‍.', '', NULL),
(171, 'aerial surveying', 'ഏരിയല്‍ സര്‍വേ', 'ആകാശത്തുനിന്നെടുക്കുന്ന ഫോട്ടോഗ്രാഫുകള്‍ ഉപയോഗിച്ച്‌ ഭൂസര്‍വേ നടത്തുന്ന സമ്പ്രദായം.', '', NULL),
(172, 'aerobe', 'വായവജീവി', 'വായുവിന്റെ അല്ലെങ്കില്‍ ഓക്‌സിജന്റെ സാന്നിദ്ധ്യത്തില്‍ മാത്രം ജീവിക്കാന്‍ കഴിയുന്നവ.', '', NULL),
(173, 'aerobic respiration', 'വായവശ്വസനം', 'ഓക്‌സിജന്റെ സാന്നിദ്ധ്യത്തില്‍ മാത്രം നടക്കുന്ന ശ്വസനം. ജലത്തിലും വായുവിലും വെച്ച്‌ നടക്കുന്ന ഭൂരിപക്ഷം ജീവജാലങ്ങളുടെയും ശ്വസന രീതി ഇതാണ്‌.', '', NULL),
(174, 'aerodynamics', 'വായുഗതികം', 'വാതകങ്ങളുടെയും വായുവിന്റെയും ചലനം, വായുവിലൂടെ വിമാനം പോലുള്ള വസ്‌തുക്കളുടെ ചലനം ഇവ സംബന്ധിച്ച പഠന ശാഖ.', '', NULL),
(175, 'aerosol', 'എയറോസോള്‍', 'ഖരകണികകളോ, ദ്രവകണികകളോ വാതകത്തില്‍ തങ്ങിനില്‍ക്കുന്ന കൊളോയ്‌ഡ്‌ രൂപം. ഉദാ: പൊടിപടലങ്ങള്‍ കലര്‍ന്ന വായു, പുക.', '', NULL),
(176, 'aerotaxis', 'എയറോടാക്‌സിസ്‌', 'വാതാനുചലനം. ഓക്‌സിജന്റെ ഉത്തേജനം മൂലം ഏകകോശ സസ്യങ്ങള്‍ മുഴുവനുമായോ സസ്യകോശങ്ങള്‍ ഭാഗികമായോ ചലിക്കുന്ന രീതി.', '', NULL),
(177, 'aerotropism', 'എയറോട്രാപ്പിസം', 'വാതാനുവര്‍ത്തനം. വായുവിന്റെ സാന്നിധ്യം അടിസ്ഥാനമാക്കിയുള്ള സസ്യചലനം. വായുവുള്ള ദിശയിലേക്കോ എതിര്‍ ദിശയിലേക്കോ ആവാം.', '', NULL),
(178, 'aestivation', 'പുഷ്‌പദള വിന്യാസം', '(botany) മുകുളാവസ്ഥയില്‍ ദളങ്ങളുടെയും വിദളങ്ങളുടെയും ക്രമീകരണം. വിവിധ തരത്തിലുള്ള ദളവിന്യാസങ്ങള്‍ ഉണ്ട്‌. സസ്യങ്ങളുടെ വര്‍ഗീകരണത്തിന്‌ സഹായിക്കുന്നു.', '', NULL),
(179, 'aestivation', 'ഗ്രീഷ്‌മനിദ്ര', '(zoology) വരണ്ട കാലാവസ്ഥയെ അതിജീവിക്കാന്‍ ചില ജീവികള്‍ സ്വീകരിക്കുന്ന നീണ്ട നിഷ്‌ക്രിയത. ജൈവ പ്രക്രിയകള്‍ വളരെ മന്ദഗതിയിലായി ഒരു നിദ്രാവസ്ഥയിലായിരിക്കും അവ. ഉദാ: ചിലതരം ഒച്ചുകള്‍, പ്രാട്ടോപ്‌റ്റിറസ്‌ എന്ന മത്സ്യം.', '', NULL),
(180, 'afferent', 'അഭിവാഹി', 'കേന്ദ്രഭാഗത്തേക്ക്‌ പ്രവഹിക്കുന്നത്‌. ഉദാ: afferent neuron കേന്ദ്ര നാഡീവ്യൂഹത്തിലേക്ക്‌ സന്ദേശങ്ങള്‍ എത്തിക്കുന്ന നാഡീകോശം.', '', NULL),
(181, 'affine', 'സജാതീയം', 'ഉദാ: affine curvature (സജാതീയ വക്രം)', '', NULL),
(182, 'affinity', 'ബന്ധുത', '(chem) രാസപരമായി കൂടിച്ചേരാനുള്ള മൂലകങ്ങളുടെ ശേഷി. 2. (bio)ഒരു ജീവിവിഭാഗത്തിന്‌ മറ്റുള്ളവയുമായുള്ള പരിണാമപരമായ അടുപ്പം.', '', NULL),
(183, 'agamogenesis', 'അലൈംഗിക ജനനം', 'ഗാമേറ്റുകള്‍ വഴിയല്ലാത്ത പ്രത്യുല്‍പാദനം.', '', NULL),
(184, 'agamospermy', 'അഗമോസ്‌പെര്‍മി', 'ബീജസങ്കലനം കൂടാതെ വിത്തുണ്ടാകുന്ന അവസ്ഥ.', '', NULL),
(185, 'agar', 'അഗര്‍', 'ഒരുതരം ശ്ലേഷ്‌മക പദാര്‍ഥം. ചിലയിനം കടല്‍പ്പായലുകളില്‍ നിന്ന്‌ ഉല്‍പാദിപ്പിക്കുന്ന ഒരു കാര്‍ബോ ഹൈഡ്രറ്റ്‌.', '', NULL),
(186, 'age hardening', 'ഏജ്‌ ഹാര്‍ഡനിംഗ്‌', 'ശുദ്ധീകരിക്കപ്പെട്ടുകഴിയുമ്പോള്‍ ഒരു ലോഹത്തിന്റെയോ കൂട്ടുലോഹത്തിന്റെയോ കാഠിന്യം വര്‍ദ്ധിക്കുന്ന പ്രക്രിയ.', '', NULL),
(187, 'age specific death rate (ASDR)', 'വയസ് അടിസ്ഥാനമായ മരണനിരക്ക്', 'ഒരു പ്രത്യേക പ്രായപരിധിയിലെ മരണത്തിന്റെ എണ്ണവും ജനസംഖ്യയും പരിഗണിച്ചുകൊണ്ട്‌ മരണനിരക്ക്‌ കണക്കാക്കുന്ന രീതി. ഒരുപ്രത്യേക പ്രായപരിധിയില്‍ ഒരു വര്‍ഷത്തിലുണ്ടായ മരണങ്ങളുടെ എണ്ണം  ASDR= x1000 ആ വര്‍ഷത്തിന്റെ മധ്യത്തില്‍ പ്രസ്‌തുത \nപ്രായപരിധിയിലുള്ള ജനസംഖ്യ', '', NULL),
(188, 'agglutination', 'അഗ്ലൂട്ടിനേഷന്‍', 'ആശ്ലേഷണം. ഒന്നായി ഒട്ടിച്ചേരല്‍. ഉദാ: പരസ്‌പരം പൊരുത്തപ്പെടാത്ത രക്തങ്ങള്‍ കൂട്ടിക്കലര്‍ത്തുമ്പോള്‍ ചുവന്ന രക്തകോശങ്ങള്‍ കൂടിച്ചേര്‍ന്ന്‌ കട്ടയാകുന്നത്‌; ശരീരത്തില്‍ പ്രവേശിക്കുന്ന ബാക്‌റ്റീരിയങ്ങളെയോ വൈറസ്സുകളെയോ ആന്റിബോഡികള്‍ ഒട്ടിച്ചു ചേര്‍ക്കുന്നത്‌.', '', NULL),
(189, 'aggradation', 'അധിവൃദ്ധി', 'ഭൂമിയുടെ ഉപരിതലത്തിലുള്ള ശിലാപടലങ്ങള്‍ ബലകൃത-രാസായനിക പ്രക്രിയകളില്‍ വിഘടിച്ചോ പൊടിഞ്ഞോ ഉണ്ടാകുന്ന പദാര്‍ഥങ്ങള്‍ പ്രകൃതിശക്തികളുടെ പ്രവര്‍ത്തനത്താല്‍ നീക്കം ചെയ്യപ്പെട്ട്‌ മറ്റൊരിടത്ത്‌ നിക്ഷേപിക്കപ്പെടുന്ന പ്രക്രിയ.', '', NULL),
(190, 'aggregate', 'പുഞ്‌ജം', '(geo) ശിലാശകലങ്ങളോ ധാതുശകലങ്ങളോ അടിഞ്ഞു കൂടിയുണ്ടാക്കുന്ന ശിലാ പടലം.', '', NULL),
(191, 'aggregate fruit', 'പുഞ്‌ജഫലം', 'ഒരു പുഷ്‌പത്തിലെ സ്വതന്ത്രമായ അനേകം അണ്ഡപര്‍ണ്ണങ്ങള്‍ വളര്‍ന്നുണ്ടാകുന്ന ഫലസമൂഹം. ഉദാ: ആത്ത, അരണമരം എന്നിവയുടെ ഫലങ്ങള്‍.', '', NULL),
(192, 'aglosia', 'എഗ്ലോസിയ', 'നാക്കില്ലാത്ത അവസ്ഥ.', '', NULL),
(193, 'ahmes papyrus', 'അഹ്‌മെസ്‌ പാപ്പിറസ്‌', 'അറിയപ്പെടുന്നതില്‍ വെച്ച്‌ ഏറ്റവും പഴക്കമുള്ള ഈജിപ്‌ഷ്യന്‍ ഗണിത ഗ്രന്ഥം. ക്രി.മു. 1500ന്‌ അടുത്ത്‌ രചിക്കപ്പെട്ടു. Rhind papyrus എന്നും പറയുന്നു.', '', NULL),
(194, 'air', 'വായു', 'ഭൂമിയെ ആവരണം ചെയ്‌തിട്ടുള്ള വാതക മിശ്രിതം. വ്യാപ്‌തമാനത്തില്‍ അതിന്റെ ചേരുവ താഴെ കാണുംവിധം ആണ്‌. നൈട്രജന്‍ 78.08%, ഓക്‌സിജന്‍ 20.95%, ആര്‍ഗണ്‍ 0.93%, കാര്‍ബണ്‍ ഡൈ ഓക്‌സൈഡ്‌ 0.03%, നിയോണ്‍ 0.0018%, ഹീലിയം 0.0005%, ക്രിപ്‌റ്റണ്‍ 0.0001%, സെനോണ്‍ 0.00001%. ഇതുകൂടാതെ ജലബാഷ്‌പം, ധൂളി, പരാഗരേണുക്കള്‍, വളരെ ചെറിയ തോതില്‍ മറ്റുവാതകങ്ങള്‍ എന്നിവയും കാണാം.', '', NULL),
(195, 'air gas', 'എയര്‍ഗ്യാസ്‌', 'കാര്‍ബണ്‍ മോണോക്‌സൈഡിന്റെയും നൈട്രജന്റെയും മിശ്രിതം. താപദീപ്‌തമായ കരിയുടെ മുകളിലൂടെ വായു പായിച്ചാണ്‌ ഇത്‌ നിര്‍മിക്കുന്നത്‌. ആന്തര ദഹന എഞ്ചിനുവേണ്ടിയുള്ള ലോഹപ്പണിയില്‍ നിരോക്‌സീകാരകമായി ഉപയോഗിക്കുന്നു.', '', NULL),
(196, 'ait', 'എയ്‌റ്റ്‌', 'ചെറുദ്വീപ്‌, പ്രത്യേകിച്ച്‌ നദികളിലുള്ളവ. eyot എന്നും എഴുതും.', '', NULL),
(197, 'akaryote', 'അമര്‍മകം', 'മര്‍മം ഇല്ലാത്ത കോശം.', '', NULL),
(198, 'akinete', 'അക്കൈനെറ്റ്‌', 'നിശ്ചേഷ്‌ട ബീജാണു പ്രതികൂല കാലാവസ്ഥ തരണം ചെയ്യാന്‍ ചിലയിനം പായലുകള്‍ നിര്‍മിക്കുന്ന, കനത്ത പുറംചട്ടയോടുകൂടിയ സ്‌പോര്‍.', '', NULL),
(199, 'alar', 'പക്ഷാഭം', 'ചിറക്‌ പോലുള്ള ഉദാ: alar membrane', '', NULL),
(200, 'albedo', 'ആല്‍ബിഡോ', 'ഒരു വസ്‌തുവിന്റെ പ്രകാശ പ്രതിഫലനീയതയെ സൂചിപ്പിക്കുന്ന പദം. പ്രതിഫലിച്ച പ്രകാശോര്‍ജവും വസ്‌തുവില്‍ പതിച്ച പ്രകാശോര്‍ജവും തമ്മിലുള്ള അനുപാതം. ഭൂമിയുടെ ശരാശരി ആല്‍ബിഡോ 0.4 ആണ്‌.', '', NULL),
(201, 'albinism', 'ആല്‍ബിനിസം', 'ശരീരനിറത്തിന്‌ കാരണമാകുന്ന മെലാനിന്‍ ഇല്ലാത്ത അവസ്ഥ. ഒരു ഗുപ്‌ത ജീന്‍ സമയുഗ്മാവസ്ഥയില്‍ വന്നാണ്‌ ഈ ജന്മവൈകല്യം ഉണ്ടാവുന്നത്‌. മനുഷ്യന്‍, എലി, മുയല്‍ തുടങ്ങിയവയില്‍ ആല്‍ബിനിസം കാണപ്പെടുന്നു.', '', NULL),
(202, 'albino', 'ആല്‍ബിനോ', 'ആല്‍ബിനിസം ഉള്ള ജീവി ', 'albinism', NULL),
(203, 'albuminous seed', 'അല്‍ബുമിനസ്‌ വിത്ത്‌', 'ബീജാന്നം ഉള്ള വിത്ത്‌. ഉദാ: നെല്ല്‌.', '', NULL),
(204, 'albumin', 'ആല്‍ബുമിന്‍', 'ജലലേയങ്ങളായ പ്രാട്ടീനുകള്‍. ചൂടാക്കിയാല്‍ ഖരാവസ്ഥ പ്രാപിക്കും. രക്തത്തിന്റെ ഓസ്‌മോട്ടിക്‌ മര്‍ദം നിലനിര്‍ത്തുന്ന പ്രധാന ഘടകം. പാല്‍, മുട്ട എന്നിവയില്‍ ആല്‍ബുമിന്‍ അടങ്ങിയിരിക്കും. ഗോതമ്പിലുള്ള ലൂക്കോസിന്‍ മറ്റൊരു ഉദാഹരണമാണ്‌.', '', NULL),
(205, 'alchemy', 'രസവാദം', 'ആല്‍ക്കെമി. ആധുനിക രസതന്ത്രത്തിന്റെ മുന്നോടി. മൂലക രൂപാന്തരണത്തെക്കുറിച്ചുള്ള ആദ്യകാല വിശ്വാസം. \"അധമ\' ലോഹങ്ങളായ ഈയം, ഇരുമ്പ്‌ മുതലായവയെ സ്വര്‍ണമാക്കി മാറ്റാമെന്നുള്ള ചിന്ത. ഇതിന്‌ സഹായിക്കുന്ന philosopher\'s stone എന്ന വസ്‌തു കണ്ടെത്താനായി ഒട്ടനവധി ഗവേഷണങ്ങള്‍ രസവാദികള്‍ നടത്തിയിരുന്നു. ഈ പരീക്ഷണങ്ങളാണ്‌ ആധുനിക രസതന്ത്രത്തിലേക്ക്‌ നയിച്ചത്‌.', '', NULL),
(206, 'alcohols', 'ആല്‍ക്കഹോളുകള്‍', '−OH ഗ്രൂപ്പ്‌ ഉള്ള കാര്‍ബണിക സംയുക്തങ്ങള്‍ ഉദാ: C2H5−OH ഈഥൈല്‍ ആല്‍ക്കഹോള്‍. CH3−OH- മീഥൈല്‍ ആല്‍ക്കഹോള്‍. പ്രമറി, സെക്കണ്ടറി, ടെര്‍ഷ്യറി എന്നിങ്ങനെ വിവിധതരം ആല്‍ക്കഹോളുകളുണ്ട്‌. ആല്‍ക്കഹോളുകള്‍ നല്ല കാര്‍ബണിക ലായകങ്ങളാണ്‌. ആല്‍ക്കഹോള്‍ കുടുംബത്തില്‍പ്പെട്ട ഈഥൈല്‍ ആല്‍ക്കഹോളാണ്‌ എഥനോള്‍ എന്നറിയപ്പെടുന്നത്‌. വളരെയേറെ വ്യാവസായിക പ്രാധാന്യമുള്ളതാണ്‌ ഈഥൈല്‍ ആല്‍ക്കഹോള്‍. മദ്യത്തില്‍ അടങ്ങിയിരിക്കുന്ന ലഹരി പദാര്‍ഥവും ഇതു തന്നെയാണ്‌.', '', NULL),
(207, 'Aldebaran', 'ആല്‍ഡിബറന്‍', 'ഏറ്റവും ശോഭയുള്ള 15 നക്ഷത്രങ്ങളിലൊന്ന്‌. ഇതൊരു ചുവപ്പ്‌ ഭീമനാണ്‌. ഇടവം രാശി ( taurus) യിലാണ്‌ ഈ നക്ഷത്രം. 65.23 പ്രകാശവര്‍ഷമാണ്‌ നക്ഷത്രത്തിലേക്കുള്ള ദൂരം. ഇത്‌ രോഹിണി ജന്മനക്ഷത്രത്തിന്റെ ഭാഗമാണ്‌.', '', NULL),
(208, 'aldehyde', 'ആല്‍ഡിഹൈഡ്‌', '−CHO ഗ്രൂപ്പ്‌ അടങ്ങിയിട്ടുള്ള കാര്‍ബണിക സംയുക്തം. ഉദാ: അസറ്റാല്‍ഡിഹൈഡ്‌ ഫോര്‍മാല്‍ഡിഹൈഡ്‌. വളരേയേറെ വ്യാവസായിക പ്രാധാന്യമുള്ള സംയുക്തങ്ങളാണ്‌.', '', NULL),
(209, 'aleurone grains', 'അല്യൂറോണ്‍ തരികള്‍', 'വിത്തുകളില്‍ കണ്ടുവരുന്ന ഒരിനം പ്രാട്ടീന്‍ കണികകള്‍.', '', NULL),
(210, 'Aleuroplast', 'അല്യൂറോപ്ലാസ്റ്റ്‌', 'പ്രാട്ടീനുകള്‍ ശേഖരിച്ചുവെച്ച പ്ലാസ്റ്റിഡ്‌.', '', NULL),
(211, 'algae', 'ആല്‍ഗകള്‍', 'പരിണാമശ്രണിയുടെ ഏറ്റവും അടിത്തട്ടിലുള്ള സസ്യവിഭാഗം. ഏകകോശ ആല്‍ഗകള്‍ മുതല്‍ താലോയ്‌ഡ്‌ ആല്‍ഗകള്‍ വരെ ഇവയില്‍ ഉള്‍പ്പെടുന്നു.', '', NULL),
(212, 'algebraic equation', 'ബീജീയ സമവാക്യം', 'ഒരു ബീജീയ ഏകദത്തെ പൂജ്യത്തോടു തുല്യമാക്കി കാണിക്കുന്നതോ, മറ്റൊരു ബീജീയ ഏകദത്തോടു തുല്യമാക്കി കാണിക്കുന്നതോ ആയ സമവാക്യം. ഉദാ: 2 x+y=0, x2+y2=3x+2y−1', '', NULL),
(213, 'algebraic expression', 'ബീജീയ വ്യഞ്‌ജകം', 'ചരങ്ങളും സ്ഥിരസംഖ്യകളും ബീജീയ സംക്രിയകള്‍ കൊണ്ട്‌ ബന്ധിച്ചിരിക്കുന്ന വ്യഞ്‌ജകം. ഉദാ: 2 x+y.', '', NULL),
(214, 'algebraic function', 'ബീജീയ ഏകദം', 'സങ്കലനം, വ്യവകലനം, ഗുണനം, ഹരണം, സംഖ്യയുടെ ഘാതം കാണല്‍, സംഖ്യയുടെ മൂലം കാണല്‍ എന്നീ ക്രിയകള്‍ മാത്രം ചെയ്‌ത്‌ മൂല്യം ലഭിക്കുന്ന ഏകദം.', '', NULL),
(215, 'algebraic number', 'ബീജീയ സംഖ്യ', 'ഗുണോത്തരങ്ങളെല്ലാം ഭിന്നകം ( rational)ആയുള്ള സമീകരണത്തിന്റെ മൂലം. ഉദാ: സമീകരണം x2-2=0 ;മൂലങ്ങള്‍ √2, - √2.', '', NULL),
(216, 'algebraic sum', 'ബീജീയ തുക', 'രണ്ടോ അതിലധികമോ പദങ്ങളുടെ തുക. ഉദാ:-2, 8, 3 ഇവയുടെ ബീജീയ തുക 9 ആണ്‌. x+y, yഇവയുടെ ബീജീയതുക x+2y ആണ്‌.', '', NULL),
(217, 'Algol', 'അല്‍ഗോള്‍', '1. പെര്‍സിയൂസ്‌ നക്ഷത്രഗണത്തില്‍ പ്രകാശ തീവ്രതയില്‍ രണ്ടാം സ്ഥാനത്ത്‌ നില്‍ക്കുന്ന നക്ഷത്രം. പിശാച്‌ നക്ഷത്രം എന്നാണ്‌ പേരിനര്‍ഥം. 2.87 ദിവസങ്ങളിടവിട്ട്‌ അത്‌ നിറം മങ്ങി, ഏകദേശം 10 മണിക്കൂറുകള്‍ക്ക്‌ ശേഷം പൂര്‍വ സ്ഥിതിയിലേക്ക്‌ മടങ്ങിയെത്തുന്നു. അല്‍ഗോള്‍ ഒരു നക്ഷത്ര ത്രയമാണ്‌. അവയില്‍ രണ്ട്‌ നക്ഷത്രങ്ങള്‍ വളരെയടുത്താണ്‌. ഒരു പൊതു കേന്ദ്രത്തെ വലം വയ്‌ക്കുന്ന ഈ നക്ഷത്രങ്ങള്‍ ഇടയ്‌ക്കിടെ പരസ്‌പരം മറയ്‌ക്കുന്നു. നക്ഷത്രത്തിന്റെ പ്രകാശവ്യതിയാനത്തിന്‌ കാരണമിതാണ്‌. 2. ഒരു കമ്പ്യൂട്ടര്‍ ഭാഷ ALGorithmic Oriented Language എന്നതിന്റെ ചുരുക്കമാണ്‌ Algol.', '', NULL),
(218, 'Algorithm', 'അല്‍ഗരിതം', 'ഒരു പ്രശ്‌നത്തിന്റെ നിര്‍ധാരണത്തിനായി വ്യക്തമായി നിര്‍വചിച്ചിട്ടുള്ള ചട്ടങ്ങളുടെയും നിര്‍ദ്ദേശങ്ങളുടെയും ഗണം. അല്‍ഗരിതങ്ങളെ ഫ്‌ളോചാര്‍ട്ട്‌ കൊണ്ട്‌ വിശദീകരിക്കുകയാണ്‌ പതിവ്‌.', '', NULL),
(219, 'alicyclic compound', 'ആലിസൈക്ലിക സംയുക്തം', 'സംവൃത ശൃംഖലയുള്ള അരോമാറ്റികമല്ലാത്ത, കാര്‍ബോചാക്രിക സംയുക്തങ്ങള്‍ ഉദാ: സൈക്ലോഹെക്‌സേന്‍.', '', NULL),
(220, 'alimentary canal', 'അന്നപഥം', 'ജന്തുക്കളില്‍ വായമുതല്‍ മലദ്വാരം വരെയുള്ള നാളി.', '', NULL),
(221, 'aliphatic compound', 'ആലിഫാറ്റിക സംയുക്തങ്ങള്‍', 'വിവൃത ശൃംഖലാ കാര്‍ബണിക യഗൗികങ്ങള്‍. ചില പ്രധാന വിവൃത ശൃംഖലാസംയുക്തങ്ങള്‍ കൊഴുപ്പുകളില്‍ ഉള്ളതുകൊണ്ടാണ്‌ ഇവയെ ആലിഫാറ്റിക സംയുക്തങ്ങള്‍ എന്നുവിളിക്കുന്നത്‌. ഇവ പൂരിതമോ അപൂരിതമോ ആവാം.', '', NULL),
(222, 'alkali', 'ക്ഷാരം', 'ജലവുമായി ചേരുമ്പോള്‍ - OH ഗ്രൂപ്പിനെ വിട്ടുകൊടുക്കാന്‍ കഴിയുന്നതോ, ഒരു രാസപ്രവര്‍ത്തനത്തില്‍ H+ അയോണിനെ സ്വീകരിക്കാന്‍ കഴിയുന്നതോ ഒരു ജോഡി ഇലക്‌ട്രാണുകളെ വിട്ടുകൊടുക്കാന്‍ കഴിയുന്നതോ ആയ പദാര്‍ഥം. ഉദാ: പൊട്ടാസ്യം ഹൈഡ്രാക്‌സൈഡ്‌, സോഡിയം ഹൈഡ്രാക്‌സൈഡ്‌.', '', NULL),
(223, 'alkali metals', 'ആല്‍ക്കലി ലോഹങ്ങള്‍', 'ആവര്‍ത്തന പട്ടികയിലെ ഹൈഡ്രജന്‍ ഒഴികെയുള്ള ഒന്നാം ഗ്രൂപ്പ്‌ മൂലകങ്ങള്‍. ഉയര്‍ന്ന വിദ്യുത്‌ധനത ഉണ്ട്‌. ജലവുമായി പ്രതിപ്രവര്‍ത്തിച്ച്‌ ആല്‍ക്കലികള്‍ ഉല്‍പ്പാദിപ്പിക്കുന്നു. ഉദാ: സോഡിയം, പൊട്ടാസ്യം.', '', NULL),
(224, 'alkalimetry', 'ക്ഷാരമിതി', 'തന്നിരിക്കുന്ന ലായനിയില്‍ എത്ര അമ്ലമുണ്ട്‌ എന്ന്‌ ക്ഷാരം ഉപയോഗിച്ച്‌ കണ്ടുപിടിക്കുന്ന രീതി. വ്യാപ്‌തമാന വിശ്ലേഷണം വഴിയാണ്‌ കണ്ടുപിടിക്കുന്നത്‌.', '', NULL),
(225, 'alkaline earth metals', 'ആല്‍ക്കലൈന്‍ എര്‍ത്‌ ലോഹങ്ങള്‍', 'ആവര്‍ത്തന പട്ടികയില്‍ ഗ്രൂപ്പ്‌ II ലെ മൂലകങ്ങള്‍. ഈ മൂലകങ്ങളെല്ലാം ജലവുമായി പ്രവര്‍ത്തിച്ച്‌ ആല്‍ക്കലി ഉണ്ടാകുന്നു. അതുപോലെ ഇവയുടെ ഓക്‌സൈഡുകളും വെള്ളത്തില്‍ ചേര്‍ന്ന്‌ ആല്‍ക്കലി ഉണ്ടാകുന്നു.', '', NULL),
(226, 'alkaline rock', 'ക്ഷാരശില', 'സോഡിയത്തിന്റെയും പൊട്ടാസിയത്തിന്റെയും അംശം കൂടുതലുള്ള ഒരു ആഗ്നേയ ശില.', '', NULL),
(227, 'alkaloid', 'ആല്‍ക്കലോയ്‌ഡ്‌', 'സസ്യങ്ങളില്‍ നിന്ന്‌ ലഭിക്കുന്ന, ക്ഷാര സ്വഭാവമുള്ളതും ജലത്തില്‍ ലയിക്കാത്തതുമായ കാര്‍ബണിക പദാര്‍ഥങ്ങള്‍. കയ്‌പോ, ചവര്‍പ്പോ ആണ്‌ രുചി. പല പ്രധാന ഔഷധങ്ങളും ആല്‍ക്കലോയിഡുകളാണ്‌. പുകയിലയില്‍ അടങ്ങിയിരിക്കുന്ന ആല്‍ക്കലോയിഡ്‌ ആണ്‌ നിക്കോട്ടിന്‍. കാപ്പിയിലും ചായയിലും ഉള്ള കഫീന്‍ എന്ന ആല്‍ക്കലോയിഡ്‌ ആണ്‌ ഉത്തേജക വസ്‌തുവായി പ്രവര്‍ത്തിക്കുന്നത്‌.', '', NULL),
(228, 'alkane', 'ആല്‍ക്കേനുകള്‍', 'CnH2n+2 എന്ന സാമാന്യരാസസൂത്രമുള്ള പൂരിത ഹൈഡ്രാകാര്‍ബണുകളുടെ സജാതീയ ശ്രണി. ഉദാ: മീഥേന്‍ ( CH4), ഈഥേന്‍ ( C2H6), പ്രാപ്പേന്‍ ( C3H8), ബ്യൂട്ടേന്‍ ( C4H10) തുടങ്ങിയവ.', '', NULL),
(229, 'alkenes', 'ആല്‍ക്കീനുകള്‍', 'CnH2n എന്ന സാമാന്യരാസസൂത്രമുള്ള ഹൈഡ്രാകാര്‍ബണുകളുടെ സജാതീയ ശ്രണി. ഉദാ: ഈഥീന്‍ C2H4, പ്രാപ്പീന്‍ C3H8, ബ്യൂട്ടീന്‍ C4H8 തുടങ്ങിയവ. unsaturated hydrocarbons നോക്കുക.', '', NULL),
(230, 'alkyl group', 'ആല്‍ക്കൈല്‍ ഗ്രൂപ്പ്‌', 'ഒരു ആലിഫാറ്റിക ഹൈഡ്രാകാര്‍ബണില്‍ നിന്ന്‌ ഒരു ഹൈഡ്രജന്‍ ആറ്റം നീക്കിയാല്‍ കിട്ടുന്ന റാഡിക്കല്‍. ഉദാ: CH3− (മീഥൈല്‍)', '', NULL),
(231, 'alkyne', 'ആല്‍ക്കൈന്‍', '-', 'unsaturated hydrocarbons', NULL),
(232, 'allantois', 'അലെന്റോയ്‌സ്‌', 'ആമ്‌നിയോട്ടിക കശേരുകികളുടെ ഭ്രൂണത്തില്‍, പിന്‍കുടലിന്റെ മധ്യഭാഗത്തുനിന്ന്‌ വളരുന്ന സഞ്ചിപോലുള്ള ഭാഗം. സംയോജകകല കൊണ്ട്‌ ആവരണം ചെയ്യപ്പെട്ട ഇതിന്‌ നല്ലവണ്ണം വികസിച്ച രക്തചംക്രമണ സംവിധാനമുണ്ട്‌. ഉരഗങ്ങളിലും പക്ഷികളിലും ഇത്‌ ഭ്രൂണത്തിന്റെ പുറത്തേക്ക്‌ വളരും. ഇതിലൂടെയാണ്‌ ഭ്രൂണത്തിന്റെ ശ്വസനം നടക്കുന്നത്‌. വിസര്‍ജ്യവസ്‌തുക്കള്‍ ഇതിനകത്ത്‌ ശേഖരിക്കപ്പെടുന്നു. സസ്‌തനികളില്‍ അലെന്റോയ്‌സിന്റെ രക്തക്കുഴലുകള്‍ മറുപിള്ളയുടെ രക്തക്കുഴലുകളുമായി ബന്ധപ്പെട്ടിരിക്കും. ശ്വസനത്തിനു പുറമേ, പോഷകാംശങ്ങള്‍ ലഭിക്കുന്നതും വിസര്‍ജ്യവസ്‌തുക്കള്‍ പുറത്തേക്കു കളയുന്നതും ഇതിലൂടെയാണ്‌.', '', NULL),
(233, 'alleles', 'അല്ലീലുകള്‍', 'പര്യായജീനുകള്‍. ഒരു ജീനിന്റെ വ്യത്യസ്‌ത രൂപങ്ങള്‍. ഇവ സമജാത ക്രാമസോമുകളില്‍ ഒരേ ലോക്കസില്‍ സ്ഥിതിചെയ്യുന്നവയും ഒരേ ധര്‍മ്മം നിര്‍വഹിക്കുന്നവയുമായിരിക്കും. ഒരു പര്യായ ജീനിന്‌ മ്യൂട്ടേഷന്‍ സംഭവിച്ച്‌ മറ്റൊരു പര്യായജീനായിത്തീരാം. മനുഷ്യന്റെ ABO രക്തഗ്രൂപ്പുകളെ നിര്‍ണ്ണയിക്കുന്ന IA, IB, I0 എന്നീ ജീനുകള്‍ രക്തഗ്രൂപ്പ്‌ ജീനിന്റെ പര്യായ ജീനുകളാണ്‌. ( I എന്നത്‌ ഇമ്മ്യൂണോ ഗ്ലോബുലിന്‍ എന്ന വാക്കിന്റെ ആദ്യത്തെ അക്ഷരത്തെ പ്രതിനിധീകരിക്കുന്നു.) allelomorphs എന്നും പറയുന്നു.', '', NULL),
(234, 'allergen', 'അലെര്‍ജന്‍', 'ഉയര്‍ന്ന തോതിലുള്ള ഇമ്യൂണ്‍ പ്രതിപ്രവര്‍ത്തനങ്ങള്‍ക്ക്‌ ( immune reaction) കാരണമാവുന്ന ആന്റിജന്‍. ഉദാ: അലര്‍ജിയുണ്ടാക്കുന്ന പൂമ്പൊടികള്‍, ധൂളി മുതലായവ.', '', NULL),
(235, 'allergy', 'അലര്‍ജി', 'ഏതെങ്കിലും പാരിസ്ഥിതിക ഘടകങ്ങളോട്‌ (ഉദാ: പൊടി, പരാഗം, ഭക്ഷണസാധനങ്ങള്‍) ശരീരം കാണിക്കുന്ന അധികരിച്ച സംവേദനം. സാധാരണ രോഗ പ്രതിരോധ വ്യൂഹത്തിന്റെ തന്നെ നിയന്ത്രണംവിട്ടുള്ള പ്രവര്‍ത്തനഫലമായാണ്‌ ഉണ്ടാവുന്നത്‌.', '', NULL),
(236, 'alligator', 'മുതല', 'ഒരിനം ഉരഗം. ക്രാക്കഡീലിയ എന്ന ഓര്‍ഡറില്‍ ഉള്‍പ്പെടുന്നു. വീതിയുള്ളതും നീളം കുറവുള്ളതുമാണ്‌ തല. ചീങ്കണ്ണിയുമായി ശരീരഘടനയില്‍ സാമ്യമുണ്ട്‌. ചൈനയില്‍ കാണുന്ന ഒരു സ്‌പീഷീസ്‌ ഒഴികെ മറ്റെല്ലാം വടക്കേ അമേരിക്കയിലും മധ്യ അമേരിക്കയിലുമാണുള്ളത്‌.', '', NULL),
(237, 'allochromy', 'അപവര്‍ണത', 'ആഗിരണം ചെയ്‌തതില്‍ നിന്ന്‌ വ്യത്യസ്‌ത വര്‍ണമുള്ള പ്രകാശം ഉത്സര്‍ജിക്കുന്ന ഫ്‌ളൂറസന്‍സ്‌.', '', NULL),
(238, 'allochronic', 'അസമകാലികം', 'വ്യത്യസ്‌ത ഭൂവിജ്ഞാനീയ കാലഘട്ടങ്ങളില്‍ സംഭവിക്കുന്നത്‌.', '', NULL),
(239, 'allogenic', 'അന്യത്രജാതം', 'ബാഹ്യകാരണങ്ങള്‍ കൊണ്ടുണ്ടാവുന്നത്‌. ഉദാ: ആവാസ വ്യവസ്ഥയിലേക്ക്‌ ഒരു കുടിയേറ്റ ഇരപിടിയന്‍ വന്നുചേരുമ്പോള്‍ ഉണ്ടാവുന്ന മാറ്റങ്ങള്‍.', '', NULL),
(240, 'allogamy', 'പരബീജസങ്കലനം', 'വ്യത്യസ്‌ത സസ്യങ്ങളില്‍ നിന്നുള്ള പും-സ്‌ത്രീ ബീജങ്ങളുടെ സങ്കലനം.', '', NULL),
(241, 'allomerism', 'സ്ഥിരക്രിസ്റ്റലത', 'രാസഘടനയില്‍ മാറ്റമുണ്ടായാല്‍ പോലും ക്രിസ്റ്റല്‍ രൂപത്തില്‍ മാറ്റമുണ്ടാകാത്ത അവസ്ഥ.', '', NULL);
INSERT INTO `wp_sde` (`id`, `enword`, `word`, `meaning`, `reftext`, `refid`) VALUES
(242, 'allopatry', 'അല്ലോപാട്രി', 'ഒരേ സ്‌പീഷീസില്‍ പെട്ട ജീവികള്‍ വ്യത്യസ്‌ത ഭൂപ്രദേശങ്ങളില്‍ പരസ്‌പരം പ്രത്യുത്‌പാദന ബന്ധമില്ലാതെ അധിവസിക്കുന്ന അവസ്ഥ.', '', NULL),
(243, 'allopolyploidy', 'അപരബഹുപ്ലോയിഡി', 'രണ്ടോ അതിലധികമോ സ്‌പീഷീസുകളുടെ ക്രാമസോം സെറ്റുകള്‍ കൂടിച്ചേര്‍ന്നുണ്ടാകുന്ന ബഹുപ്ലോയിഡി. സ്‌പീഷീസുകള്‍ തമ്മില്‍ സങ്കരം നടക്കുമ്പോഴാണിതുണ്ടാകുന്നത്‌. ഉദാഹരണമായി പ്രാചീന കാലത്ത്‌ കൃഷി ചെയ്യപ്പെട്ടിരുന്ന എമ്മര്‍ ഗോതമ്പും അതിനോട്‌ ബന്ധപ്പെട്ട ആട്ടിന്‍മുഖപ്പുല്ലും ചേര്‍ന്ന്‌ സങ്കരണം നടത്തിയതിന്റെ ഫലമായിട്ടാണ്‌ ഇന്നത്തെ ഗോതമ്പ്‌ സ്‌പീഷീസുണ്ടായത്‌ എന്നു കരുതപ്പെടുന്നു. ഒരു ജീവിയില്‍ ഒരു സെറ്റു ക്രാമസോം നാല്‌ പ്രാവശ്യം ആവര്‍ത്തിച്ചു കാണപ്പെടുന്നതിനെ ടെട്രാപ്ലോയ്‌ഡ്‌ എന്നു പറയുന്നു. രണ്ട്‌ വ്യത്യസ്‌ത സ്‌പീഷീസുകളുടെ ക്രാമസോം സെറ്റുകള്‍ തമ്മില്‍ കൂടിച്ചേര്‍ന്നുണ്ടാകുന്ന സിക്താണ്ഡത്തിലെ ക്രാമസോമുകള്‍ ഇരട്ടിക്കുന്നതു മൂലമുണ്ടാകുന്ന ബഹുപ്ലോയിഡി ആണ്‌ അല്ലോടെട്രാപ്ലോയിഡ്‌.', '', NULL),
(244, 'allosome', 'അല്ലോസോം', 'ലിംഗക്രാമസോമുകള്‍ ഒഴികെയുള്ള ക്രാമസോമുകളുടെ പൊതുനാമം.', '', NULL),
(245, 'allotetraploidy', 'അപ ചതുര്‍പ്ലോയിഡി', 'നാല്‌ സെറ്റ്‌ ക്രാമസോമുകളുള്ള ബഹുപ്ലോയിഡി. ഈ സെറ്റുകള്‍ വ്യത്യസ്‌ത സ്‌പീഷീസുകളില്‍ നിന്ന്‌ വന്നതായിരിക്കും. allopolyploidy നോക്കുക.', '', NULL),
(246, 'allotrope', 'രൂപാന്തരം', 'ഒരേ മൂലകത്തിന്റെ ആറ്റങ്ങളുടെ വിന്യാസക്രമം വ്യത്യസ്‌തമാവുമ്പോള്‍ ഉണ്ടാവുന്ന വിവിധ രൂപങ്ങള്‍. ഭൗതിക ഗുണങ്ങളില്‍ വ്യത്യാസമുണ്ടാകും. രാസഗുണങ്ങളില്‍ വ്യത്യാസമുണ്ടാവില്ല. ക്രിയാശീലത്തില്‍ വ്യത്യാസമുണ്ടാകും. ഈ പ്രതിഭാസത്തെ രൂപാന്തരത്വം എന്നു പറയുന്നു. ഉദാ: കാര്‍ബണിന്റെ രൂപാന്തരങ്ങളാണ്‌ ഗ്രാഫൈറ്റ്‌, വജ്രം, മരക്കരി എന്നിവ.', '', NULL),
(247, 'allotropism', 'രൂപാന്തരത്വം', '-', 'allotrope.', NULL),
(248, 'alloy', 'ലോഹസങ്കരം', 'രണ്ടോ അതിലധികമോ ഘടകമൂലകങ്ങള്‍ ചേര്‍ന്നുണ്ടാകുന്നതും അതിലൊന്നെങ്കിലും ലോഹവുമായ ഖരലായനി. ഉദാ: പിച്ചള (60% Cu, 40% Zn), ഓട്‌ (90% Cu, 10% Zn), സ്റ്റെയിന്‍ലസ്‌ സ്റ്റീല്‍ (74-75% Fe, 18% Cr, 0.2-1.4% C, 8%Ni)', '', NULL),
(249, 'alloy steel', 'സങ്കരസ്റ്റീല്‍', 'കാര്‍ബണ്‍ കൂടാതെ ഒന്നോ അതിലധികമോ മൂലകങ്ങള്‍ ചേര്‍ന്ന ഉരുക്ക്‌. ഇത്തരം ഉരുക്കിന്റെ സവിശേഷ ഗുണധര്‍മ്മങ്ങള്‍ക്ക്‌ കാരണം ഈ മൂലകങ്ങള്‍ ആയിരിക്കും. ഉദാ: സ്റ്റെയിന്‍ലസ്‌ സ്റ്റീല്‍.', '', NULL),
(250, 'alluvium', 'എക്കല്‍', 'നദികള്‍ വഹിച്ചുകൊണ്ടുവരുന്ന ഖരപദാര്‍ത്ഥങ്ങള്‍ നദീതടങ്ങളിലോ പ്രളയ സമതലങ്ങളിലോ തടാകങ്ങള്‍, ഡല്‍റ്റകള്‍ എന്നിവയിലോ അടിഞ്ഞുകൂടിയുണ്ടാവുന്ന നിക്ഷേപങ്ങള്‍. മണ്ണ്‌, മണല്‍, ചരല്‍, ജൈവ വസ്‌തുക്കള്‍ എന്നിവയാണ്‌ ഇങ്ങനെ നിക്ഷേപിക്കപ്പെടുന്നത്‌. ഗംഗാസമതലപ്രദേശം ഇങ്ങനെയുള്ള എക്കല്‍ നിക്ഷേപം വഴി ഉണ്ടായതാണ്‌.', '', NULL),
(251, 'Almagest', 'അല്‍ മജെസ്റ്റ്‌', 'അലക്‌സാന്‍ഡ്രിയയിലെ ടോളമി AD 140 ല്‍ പൂര്‍ത്തിയാക്കിയ ജ്യോതിശ്ശാസ്‌ത്ര-ഗണിത വിജ്ഞാനകോശം. ഇതില്‍ ഒരു നക്ഷത്ര കാറ്റലോഗും അടങ്ങിയിരുന്നു. 17-ാം നൂറ്റാണ്ടിന്റെ തുടക്കം വരെ എല്ലാ അറബിക്‌ ജ്യോതിശാസ്‌ത്രത്തിന്റെയും യൂറോപ്യന്‍ ജ്യോതിശ്ശാസ്‌ത്രത്തിന്റെയും അടിസ്ഥാന ഗ്രന്ഥം ഇതായിരുന്നു.', '', NULL),
(252, 'alnico', 'അല്‍നിക്കോ', 'ഇരുമ്പ്‌, അലൂമിനിയം, നിക്കല്‍, കൊബാള്‍ട്ട്‌, കോപ്പര്‍ എന്നീ ലോഹങ്ങള്‍ അടങ്ങിയ ലോഹസങ്കരങ്ങളുടെ ഒരു കൂട്ടം. സ്ഥിരകാന്തങ്ങള്‍ ഉണ്ടാക്കാന്‍ ഉപയോഗിക്കുന്നു.', '', NULL),
(253, 'Alpha Centauri', 'ആല്‍ഫാസെന്റൌറി', 'ഒരു ഇരട്ട നക്ഷത്രം. സെന്റാറസ്‌ ഗണത്തിലെ ഏറ്റവും ശോഭയുള്ള താരം. സൂര്യനും പ്രാക്‌സിമാ സെന്റൗറിയും കഴിഞ്ഞാല്‍ ഭൂമിയോട്‌ ഏറ്റവും അടുത്തുള്ള നക്ഷത്രം. 4.3 പ്രകാശവര്‍ഷം അകലെയാണ്‌ സ്ഥാനം.', '', NULL),
(254, 'alpha decay', 'ആല്‍ഫാ ക്ഷയം', 'അണുകേന്ദ്രത്തില്‍ നിന്ന്‌ ആല്‍ഫാകണം ഉത്സര്‍ജിക്കപ്പെടുന്ന റേഡിയോ ആക്‌റ്റീവ്‌ വിഘടനം. ഇതുമൂലം കണത്തിന്റെ അണുസംഖ്യയില്‍ രണ്ടിന്റെയും അണുഭാരത്തില്‍ നാലിന്റെയും കുറവുണ്ടാകും.', '', NULL),
(255, 'alpha particle', 'ആല്‍ഫാകണം', '2He4. റേഡിയോ ആക്‌ടീവ്‌ പദാര്‍ഥങ്ങളുടെ അണുകേന്ദ്രത്തില്‍ നിന്ന്‌ ഉത്സര്‍ജിക്കപ്പെടുന്ന കണങ്ങളിലൊന്ന്‌. ഇത്‌ ഹീലിയം അണുകേന്ദ്രമാണ്‌. ആല്‍ഫാ കണധാരയ്‌ക്ക്‌ അല്ലെങ്കില്‍ ആല്‍ഫാ വികിരണം എന്നു പറയുന്നു.', '', NULL),
(256, 'alternation of generations', 'തലമുറകളുടെ ഏകാന്തരണം', 'ഒരിനം ജീവിയില്‍ തന്നെ ലൈംഗികവും അലൈംഗികവുമായ തലമുറകള്‍ ഒന്നിടവിട്ടു പുനരാവര്‍ത്തിക്കപ്പെടുന്ന പ്രക്രിയ. metagenesis എന്നും പറയും. ഉദാ: പന്നല്‍ ചെടിയുടെ ജീവിത ചക്രം.', '', NULL),
(257, 'alternate angles', 'ഏകാന്തര കോണുകള്‍', 'രണ്ടോ അതിലധികമോ രേഖകളെ മറ്റൊരു രേഖ ഖണ്ഡിക്കുമ്പോഴുണ്ടാകുന്നതും ഒരു പ്രത്യേക ക്രമം അനുസരിക്കുന്നതുമായ കോണുകള്‍. ചിത്രത്തില്‍ \" I\', \'m\' എന്നീ രേഖകളെ \" t\' എന്ന രേഖ ഖണ്ഡിക്കുന്നു. 4, 5 എന്നിവ ഏകാന്തര കോണുകളാണ്‌. എകാന്തര കോണുകളായുള്ള മറ്റു ജോഡികള്‍ (3,6), (1,8), (2,7) എന്നിവയാണ്‌. ഇവയില്‍ (4,5), (3,6) എന്നിവയെ ആന്തര ഏകാന്തര കോണുകള്‍ എന്നും (1,8), (2,7) എന്നിവയെ ബാഹ്യ ഏകാന്തര കോണുകള്‍ എന്നും പറയുന്നു.', '', NULL),
(258, 'alternating current', 'പ്രത്യാവര്‍ത്തിധാര', 'ക്രമമായി ദിശ മാറിക്കൊണ്ടിരിക്കുന്ന വൈദ്യുത പ്രവാഹം. ഏറ്റവും ലളിതമായ പ്രത്യാവര്‍ത്തിധാരയെ I=I0Sin 2πft എന്ന സരള ഹാര്‍മോണിക ഫലനം കൊണ്ട്‌ സൂചിപ്പിക്കാം. ഇവിടെ f ആവൃത്തിയും t സമയവുമാണ്‌. നമ്മുടെ വീടുകളില്‍ ഉപയോഗിക്കുന്നത്‌ 50 Hz ആവൃത്തിയുള്ള പ്രത്യാവര്‍ത്തിധാരയാണ്‌.', '', NULL),
(259, 'alternating function', 'ഏകാന്തര ഏകദം', 'രണ്ടു സ്വതന്ത്ര ചരങ്ങളെ തമ്മില്‍ മാറ്റുന്നതിന്റെ ഫലമായി ഫലനത്തിന്റെ മൂല്യത്തിന്റെ ചിഹ്നത്തിന്‌ മാറ്റം വരുന്ന (ധനം ഋണവും ഋണം ധനവുമാകുന്ന) ഏകദം. ഉദാ: x y.', '', NULL),
(260, 'alternating series', 'ഏകാന്തര ശ്രണി', 'ഒന്നിടവിട്ടുള്ള പദങ്ങള്‍ ഒരേ ചിഹ്നത്തോടു കൂടിയതും അടുത്തടുത്തുള്ള ഏതു രണ്ടു പദങ്ങളും വിഭിന്ന ചിഹ്നങ്ങളോടു കൂടിയതുമായ ശ്രണി. V1, V2, V3.. എന്നിവ ധന സംഖ്യകളായാല്‍ V1−V2+V3...+ (-1)n-1vn+... എന്നത് ഒരു ഏകാന്തര ശ്രണിയാണ്‌.', '', NULL),
(261, 'alternator', 'ആള്‍ട്ടര്‍നേറ്റര്‍', 'പ്രത്യാവര്‍ത്തി വൈദ്യുതി ഉത്‌പാദിപ്പിക്കുന്ന ഡൈനാമോ.', '', NULL),
(262, 'altimeter', 'ആള്‍ട്ടീമീറ്റര്‍', 'സമുദ്രനിരപ്പില്‍ നിന്നുള്ള ഉയരം അളക്കാന്‍ ഉപയോഗിക്കുന്ന ഉപകരണങ്ങളുടെ പൊതുവായ പേര്‍.', '', NULL),
(263, 'altitude', 'ഉന്നതി', '(astro) ചക്രവാളത്തില്‍ നിന്ന്‌ ഖഗോളവസ്‌തുവിലേക്കുള്ള കോണീയ ദൂരം.', '', NULL),
(264, 'altitude', 'ഉന്നതി', '(geo) നിര്‍ദ്ദിഷ്‌ടസ്ഥാനത്തിന്‌ സമുദ്രനിരപ്പില്‍ നിന്നുള്ള ഉയരം.', '', NULL),
(265, 'altitude', 'ശീര്‍ഷ ലംബം', '1. (maths) ഒരു ജ്യാമിതീയ രൂപത്തിന്റെ ശീര്‍ഷത്തില്‍ നിന്ന്‌ എതിരെയുള്ള വശത്തേയ്‌ക്ക്‌ വരയ്‌ക്കുന്ന ലംബരേഖാഖണ്ഡം. 2. മുകളില്‍ പറഞ്ഞ ലംബരേഖാഖണ്ഡത്തിന്റെ നീളം.', '', NULL),
(266, 'alto cumulus', 'ആള്‍ട്ടോ ക്യുമുലസ്‌', 'ഒരിനം മേഘം. നിറം വെള്ളയോ ചാരമോ, രണ്ടും കലര്‍ന്നതോ ആയിരിക്കും.', '', NULL),
(267, 'alto stratus', 'ആള്‍ട്ടോ സ്‌ട്രാറ്റസ്‌', 'ഒരിനം മേഘം. നിറം ചാരമോ നീലിമയുള്ളതോ ആവാം. വെളുത്ത നിറം ഒരിക്കലും ഉണ്ടാവില്ല. മിക്കവാറും ആകാശം നിറയെ പടര്‍ന്ന്‌ കാണപ്പെടുന്നു.', '', NULL),
(268, 'alum', 'പടിക്കാരം', 'ഒരു ഖനിജ ലവണം. പലതരമുണ്ട്‌. പൊട്ടാഷ്‌ ആലം വെള്ളം ശുദ്ധീകരിക്കാന്‍ ഉപയോഗിക്കുന്നു. ഒരു ആല്‍ക്കലിലോഹത്തിന്റെയും ഒരു ത്രിസംയോജക ലോഹത്തിന്റെയും ഇരട്ട സള്‍ഫേറ്റ്‌ ആണ്‌. സാധാരണയായി 24 ജലതന്മാത്രകള്‍ പരല്‍ ജലമായി ഉണ്ടാകും. ഉദാ: പടിക്കാരം-അലൂമിനിയം ആലം. K2SO4Al2(SO4)324H2O', '', NULL),
(269, 'alumina', 'അലൂമിന', 'Al2O3. അലൂമിനിയം ഓക്‌സൈഡ്‌. അലൂമിനിയത്തിന്റെ അയിരായ ബോക്‌സൈറ്റിലെ പ്രധാന ഘടകം. പ്രകൃതി ജന്യമായ പല രത്‌നങ്ങളുടെയും അടിസ്ഥാന ഘടകം. കളിമണ്ണിലെ പ്രധാന ഘടകം. അപഘര്‍ഷകം.', '', NULL),
(270, 'aluminate', 'അലൂമിനേറ്റ്‌', 'അലൂമിനിയം ഹൈഡ്രാക്‌സൈഡ്‌ ശക്തിയേറിയ ക്ഷാരലായനിയില്‍ ലയിക്കുമ്പോള്‍ കിട്ടുന്ന ലവണങ്ങള്‍. ഉദാ: സോഡിയം അലൂമിനേറ്റ്‌.', '', NULL),
(271, 'aluminium', 'അലൂമിനിയം', 'ഭൂവല്‍ക്കത്തില്‍ ഏറ്റവും അധികമായി കാണുന്ന ലോഹം. ലഭ്യതയുടെ കാര്യത്തില്‍ മൂലകങ്ങളില്‍ മൂന്നാം സ്ഥാനം. 1827 ല്‍ കണ്ടെത്തി. ആവര്‍ത്തന പട്ടികയില്‍ മൂന്നാം ഗ്രൂപ്പിലെ അംഗം.', '', NULL),
(272, 'aluminium chloride', 'അലൂമിനിയം ക്ലോറൈഡ്‌', 'AlCl3. വെളുത്ത ഖരപദാര്‍ഥം. ഈര്‍പ്പവായുവില്‍ പുകയും.', '', NULL),
(273, 'aluminium potassium sulphate', 'അലൂമിനിയം പൊട്ടാസ്യം സള്‍ഫേറ്റ്‌', 'നിറമില്ലാത്ത പരലുകള്‍', 'alum', NULL),
(274, 'alunite', 'അലൂനൈറ്റ്‌', 'അലൂമിനിയം പൊട്ടാസ്യം സള്‍ഫേറ്റും അലൂമിനിയം ഹൈഡ്രാക്‌സൈഡും അടങ്ങിയ പ്രകൃത്യാ ലഭ്യമായ സംയുക്തം. ആലമുണ്ടാക്കാന്‍ ഉപയോഗിക്കുന്നു.', '', NULL),
(275, 'alveolus', 'ആല്‍വിയോളസ്‌', 'കശേരുകികളുടെ ശ്വാസകോശത്തിലുള്ള സൂക്ഷ്‌മമായ അറകള്‍. ഇവയുടെ വളരെ നേര്‍ത്ത ഭിത്തികളില്‍ ധാരാളം രക്തലോമികകളുണ്ട്‌. ഓക്‌സിജനും കാര്‍ബണ്‍ ഡൈ ഓക്‌സൈഡും കൈമാറ്റം ചെയ്യപ്പെടുന്നത്‌ ആല്‍വിയോളസിലാണ്‌.', '', NULL),
(276, 'amalgam', 'അമാല്‍ഗം', 'മെര്‍ക്കുറിയും മറ്റേതെങ്കിലും ലോഹവും ചേര്‍ന്നുണ്ടാകുന്ന ലോഹസങ്കരം. ഉദാ: സോഡിയം അമാല്‍ഗം. അമാല്‍ഗങ്ങള്‍ ദന്ത ചികിത്സയില്‍ പ്രാധാന്യമുള്ളവയാണ്‌.', '', NULL),
(277, 'amber', 'ആംബര്‍', 'ഒരു അര്‍ധതാര്യ ഫോസില്‍ റസിന്‍. നിറം മിക്കവാറും ഓറഞ്ചോ മഞ്ഞയോ ആയിരിക്കും. മറ്റെന്തെങ്കിലും ഇതില്‍ ഉരസിയാല്‍ വൈദ്യുതാവേശം ഉണ്ടാകുന്നു. ആഭരണങ്ങള്‍ ഉണ്ടാക്കാന്‍ ഉപയോഗിക്കും.', '', NULL),
(278, 'ambient', 'പരഭാഗ', 'ചുറ്റുപാടിലുള്ള താപനില, ശബ്‌ദം, പ്രകാശം തുടങ്ങിയവ സൂചിപ്പിക്കുന്ന പദം. ഉദാ: ambient temperature-പരഭാഗ താപനില', '', NULL),
(279, 'ammonite', 'അമൊണൈറ്റ്‌', 'വംശനാശം സംഭവിച്ച സെഫലോപോഡ്‌ വിഭാഗം.', '', NULL),
(280, 'amenorrhea', 'എമനോറിയ', 'ആര്‍ത്തവം ഇല്ലാത്ത അവസ്ഥ. രണ്ടുവിധം. 1. primary amenorrhea. ആദ്യമായി ഉണ്ടാകേണ്ട പ്രായത്തില്‍ ആര്‍ത്തവം ഇല്ലാതിരിക്കല്‍. 2. secondary amenorrhea ശരിയായ പ്രായത്തില്‍ ഉണ്ടായതിനു ശേഷം അത്‌ പിന്നീട്‌ ക്രമമനുസരിച്ച്‌ ഉണ്ടാകാതിരിക്കല്‍.', '', NULL),
(281, 'amensalism', 'അമന്‍സാലിസം', 'രണ്ട്‌ സ്‌പീഷിസുകള്‍ തമ്മിലുള്ള ബന്ധം. ഇത്‌ ഒന്നിന്‌ വല്ലാത്ത ദോഷമുണ്ടാക്കും. രണ്ടാമത്തേതിന്‌ ഗുണമോ ദോഷമോ ഉണ്ടാകില്ല. മറ്റു സസ്യങ്ങളുടെ വളര്‍ച്ച തടയുന്ന വിഷവസ്‌തുക്കള്‍ ഉല്‍പ്പാദിപ്പിക്കുന്ന സസ്യങ്ങള്‍ ഈ ബന്ധത്തിന്‌ ഉദാഹരണമായെടുക്കാം.', '', NULL),
(282, 'amethyst', 'അമേഥിസ്റ്റ്‌ ', 'പാടലവര്‍ണ്ണമുള്ള ക്വാര്‍ട്ട്‌സിന്റെ രൂപം. രത്‌നക്കല്ലായി ഉപയോഗിക്കുന്നു.', '', NULL),
(283, 'amides', 'അമൈഡ്‌സ്‌', 'കാര്‍ബോക്‌സിലിക്‌ അമ്ലത്തിലെ - OH ഗ്രൂപ്പ്‌ മാറ്റി -NH2 (അമിനോ) എന്ന ക്രിയാത്മക ഗ്രൂപ്പ്‌ ചേര്‍ക്കുമ്പോള്‍ കിട്ടുന്ന രാസ സംയുക്തം. കാര്‍ബോക്‌സിലിക്‌ അമ്ലമോ, അതിന്റെ ക്ലോറൈഡോ അമോണിയയുമായി പ്രതിപ്രവര്‍ത്തിക്കുമ്പോള്‍ കിട്ടുന്ന വസ്‌തു. ഉദാ: അസറ്റമൈഡ്‌', '', NULL),
(284, 'amine', 'അമീന്‍', 'ക്ഷാരസ്വഭാവമുള്ള കാര്‍ബണിക പദാര്‍ഥങ്ങള്‍. ഇവ അമോണിയയുടെ കാര്‍ബണിക വ്യുല്‍പന്നങ്ങളാണ്‌. പ്രമറി അമീന്‍ ( R− NH2), സെക്കണ്ടറി അമീന്‍ ( R2−NH), ടെര്‍ഷ്യറി അമീന്‍ ( R3−N) എന്നിങ്ങനെ മൂന്നുതരം അമീനുകള്‍ ഉണ്ട്‌.', '', NULL),
(285, 'amino acid', 'അമിനോ ആസിഡ്‌', 'അമിനോ ഗ്രൂപ്പും ( −NH2) കാര്‍ബോക്‌സിലിക്‌ ഗ്രൂപ്പും ( −COOH) അടങ്ങിയ, വെള്ളത്തില്‍ ലയിക്കുന്ന കാര്‍ബണിക സംയുക്തം. അനേകം അമിനോ ആസിഡ്‌ യൂണിറ്റുകള്‍ യോജിച്ചാണ്‌ പ്രാട്ടീന്‍ ഉണ്ടാകുന്നത്‌. മനുഷ്യശരീരത്തില്‍ അത്യാവശ്യമായി 20 അമിനോ ആസിഡുകള്‍ ഉണ്ട്‌. ഇവയില്‍ പത്തെണ്ണം ശരീരത്തില്‍ നിര്‍മ്മിക്കപ്പെടാത്തവയാണ്‌. ഇവ ഭക്ഷണത്തിലൂടെ അവശ്യം ലഭിക്കേണ്ടതാണ്‌. പൊതു സൂത്രവാക്യം', '', NULL),
(286, 'amino group', 'അമിനോ ഗ്രൂപ്പ്‌', 'ഏകസംയോജകതയുള്ള - NH2 ഗ്രൂപ്പ്‌.', '', NULL),
(287, 'amitosis', 'എമൈറ്റോസിസ്‌', 'കോശമര്‍മം രണ്ടായി വിഭജിച്ച്‌ പുത്രികാ കോശഭാഗങ്ങളുണ്ടാകുന്ന പ്രക്രിയ. ക്രമഭംഗ കോശവിഭജനത്തിലെപ്പോലെ മൈറ്റോട്ടിക സ്‌പിന്‍ഡിലുകളും കൃത്യമായ ക്രാമസോം പങ്കുവയ്‌ക്കലും ഈ പ്രക്രിയയില്‍ നടക്കുന്നില്ല. പുത്രികാ കോശങ്ങളില്‍ ഒരേ ക്രാമസോം സംഖ്യ നിലനിര്‍ത്താനുള്ള സാധ്യത കുറവാണ്‌. പുഷ്‌പിക്കുന്ന സസ്യങ്ങളുടെ വിത്തിലെ എന്‍ഡോസ്‌പേമിലും സിലിയേറ്റ്‌ പ്രാട്ടോസോവകളുടെ മാക്രാന്യൂക്ലിയസിലും നടക്കുന്നു.', '', NULL),
(288, 'ammonia', 'അമോണിയ', 'നിറമില്ലാത്തതും തീക്ഷ്‌ണഗന്ധമുള്ളതും ജലത്തില്‍ നന്നായി ലയിക്കുന്നതുമായ വാതകം. ഉരുകല്‍ നില -74 0 C. തിളനില -39.9 0 C. പണ്ട്‌ ശീതകമായി ഉപയോഗിച്ചിരുന്നു. രാസവള നിര്‍മ്മാണത്തിനും വളരെയധികം ഉപയോഗിക്കപ്പെടുന്നു. അന്തരീക്ഷ നൈട്രജനും കല്‌ക്കരി വാതകത്തില്‍ നിന്ന്‌ ലഭിക്കുന്ന ഹൈഡ്രജനും തമ്മില്‍ പ്രതിപ്രവര്‍ത്തിപ്പിച്ചാണ്‌ വന്‍തോതില്‍ അമോണിയ നിര്‍മ്മിക്കുന്നത്‌. അമോണിയ ലായനിക്ക്‌ ക്ഷാരസ്വഭാവമുണ്ട്‌.', '', NULL),
(289, 'ammonia liquid', 'ദ്രാവക അമോണിയ', 'നിറമില്ലാത്ത സവിശേഷ തീക്ഷ്‌ണഗന്ധമുള്ള ദ്രാവകം. അമോണിയാ വാതകം ദ്രവീകരിച്ചാണ്‌ ഇത്‌ നിര്‍മ്മിക്കുന്നത്‌. അഭികാരകമായും ലായകമായും ഉപയോഗിക്കുന്നു.', '', NULL),
(290, 'ammonia water', 'അമോണിയ ലായനി', 'അമോണിയയുടെ ജലലായനി. NH3+H2O NH4OH.', '', NULL),
(291, 'ammonium', 'അമോണിയം', 'NH4+. ഒരു അകാര്‍ബണിക കാറ്റയോണ്‍.', '', NULL),
(292, 'ammonium carbonate', 'അമോണിയം കാര്‍ബണേറ്റ്‌', 'വെളുത്ത ക്രിസ്റ്റലീയ ഖരപദാര്‍ഥം. ( NH4)2 CO3 ജലത്തില്‍ ലയിക്കും. തുണികളില്‍ ചായം കയറ്റുന്നതിനും ബേക്കിംഗ്‌ പഡൗറുകളിലും ഉപയോഗിക്കുന്നു.', '', NULL),
(293, 'ammonium chloride', 'നവസാരം', 'NH4Cl. വെളുത്ത ക്രിസ്റ്റലീയ ഖരപദാര്‍ഥം. ഉല്‍പ്പതന സ്വഭാവമുണ്ട്‌. ജലത്തില്‍ ലയിക്കും. ഡ്രസെല്ലുകളിലും ഈയം പൂശുന്നതിനും ചായം മുക്കേണ്ട പരുത്തി തയ്യാറാക്കുന്നതിനും അച്ചടിയിലും ഉപയോഗിക്കുന്നു.', '', NULL),
(294, 'ammonotelic', 'അമോണോടെലിക്‌', 'ശരീരത്തിലെ നൈട്രജന്‍ വിസര്‍ജ്യങ്ങള്‍ അമോണിയ രൂപത്തില്‍ വിസര്‍ജിക്കുന്ന ജീവികള്‍. അധികവും ജലജീവികളാണ്‌. ഉദാ: അനെലിഡ്‌ വിരകള്‍, മത്സ്യങ്ങള്‍ മുതലായവ.', '', NULL),
(295, 'amnesia', 'അംനേഷ്യ', 'ഭാഗികമായോ പൂര്‍ണമായോ ഓര്‍മ ഇല്ലാതാകല്‍. ജരണം, മാനസികരോഗം, ഷോക്കടിക്കല്‍, തലയ്‌ക്കേല്‍ക്കുന്ന ആഘാതം എന്നിവകൊണ്ട്‌ സംഭവിക്കാം.', '', NULL),
(296, 'Amniocentesis', 'ആമ്‌നിയോസെന്റസിസ്‌', 'ആമ്‌നിയോട്ടിക്‌ ദ്രാവകത്തിലെ ഒരു ഭാഗം. പുറത്തെടുത്ത ഗര്‍ഭസ്ഥശിശുവിനെപ്പറ്റി പഠിക്കുന്ന രീതി.', '', NULL),
(297, 'amnion', 'ആംനിയോണ്‍', 'ഭ്രൂണദശയില്‍ കാണുന്ന ദ്രാവകം നിറഞ്ഞ സഞ്ചി. ഇത്‌ ഭ്രൂണത്തിനു ചുറ്റും വളര്‍ന്ന്‌ അവസാനം ഭ്രൂണത്തെ മുഴുവനും അടക്കം ചെയ്യും. കരയില്‍ ജീവിക്കുന്ന കശേരുകികള്‍ക്ക്‌ ഭ്രൂണാവസ്ഥയില്‍ സംരക്ഷണം നല്‍കലാണ്‌ പ്രധാന ധര്‍മ്മം.', '', NULL),
(298, 'amniote', 'ആംനിയോട്ട്‌', 'ഉരഗങ്ങള്‍, പക്ഷികള്‍, സസ്‌തനങ്ങള്‍ എന്നിവ ഉള്‍പ്പെടുന്ന ജന്തുവിഭാഗത്തിന്റെ പൊതുനാമം. ഇവയുടെ ഭ്രൂണവളര്‍ച്ചയില്‍ ആംനിയോണ്‍, കോറിയോണ്‍, അല്ലെന്റോയ്‌സ്‌ എന്നീ ഭ്രൂണ സ്‌തരങ്ങള്‍ ഉണ്ടായിരിക്കും. ഈ സ്‌തരങ്ങളുള്ളതുകൊണ്ടാണ്‌ ഇത്തരം കശേരുകികള്‍ക്ക്‌ കരയില്‍ ജീവിതം സാധ്യമായത്‌.', '', NULL),
(299, 'amniotic fluid', 'ആംനിയോട്ടിക ദ്രവം', 'ഗര്‍ഭാശയത്തില്‍ ഗര്‍ഭസ്ഥ ശിശുവിനെ വലയം ചെയ്‌തിരിക്കുന്ന ദ്രാവകം. ആഘാതം, ഒട്ടിപ്പിടിക്കല്‍ ഇവയില്‍ നിന്ന്‌ ഭ്രൂണത്തെ സംരക്ഷിക്കുകയും നവുള്ളതായി നിലനിര്‍ത്തുകയും ചെയ്യുന്നു. പ്രസവസമയത്ത്‌ ആംനിയോണ്‍ പൊട്ടി ഈ ദ്രവം പുറത്തുപോകുന്നു.', '', NULL),
(300, 'amoebocyte', 'അമീബോസൈറ്റ്‌', 'ബഹുകോശ ജീവികളുടെ ശരീരത്തില്‍ കാണുന്ന സ്വതന്ത്ര സഞ്ചാരി കോശങ്ങള്‍. നിയതമായ ആകൃതിയില്ല. സ്‌പോഞ്ചിന്റെ ശരീരഭിത്തിയിലും ഉയര്‍ന്ന ജീവികളുടെ രക്തത്തിലും അമീബോസൈറ്റുകളുണ്ട്‌.', '', NULL),
(301, 'amorphous', 'അക്രിസ്റ്റലീയം', 'നിശ്ചിത ക്രിസ്റ്റലീയ രൂപമില്ലാത്ത ഖരങ്ങള്‍. ഉദാ: ഗ്ലാസും പോളിമറുകളും.', '', NULL),
(302, 'amorphous carbon', 'അമോര്‍ഫസ്‌ കാര്‍ബണ്‍', 'ഗ്രാഫൈറ്റിന്റെ സൂക്ഷ്‌മ തരികള്‍.', '', NULL),
(303, 'ampere', 'ആമ്പിയര്‍', 'വൈദ്യുതധാരയുടെ SI ഏകകം. ഒരു കൂളോം പ്രതി സെക്കന്റ്‌ എന്ന നിരക്കില്‍ വൈദ്യുത ചാര്‍ജൊഴുകുമ്പോള്‍ ഉണ്ടാകുന്ന വൈദ്യുതിക്ക്‌ തുല്യം എന്ന്‌ പൊതുവില്‍ പറയാം. ഒരു മീറ്റര്‍ അകലത്തില്‍ സമാന്തരമായി വച്ചിരിക്കുന്ന അനന്ത ദൈര്‍ഘ്യവും, അവഗണിക്കാവുന്ന പരിഛേദതല വിസ്‌താരവും ഉള്ള രണ്ട്‌ ഋജുചാലകങ്ങള്‍ക്കിടയില്‍ 2X10-7 ന്യൂട്ടന്‍ ബലം ഉണ്ടാകുവാന്‍ ചാലകങ്ങളിലൂടെ തുല്യ അളവില്‍ ഒഴുകേണ്ട വൈദ്യുതി എന്ന്‌ കൃത്യമായ നിര്‍വചനം. ആന്ദ്രമാരി ആംപിയറുടെ (1775- 1836) ബഹുമാനാര്‍ത്ഥം നല്‍കിയ പേര്‍.', '', NULL),
(304, 'amperometry', 'ആംപിറോമെട്രി', 'വൈദ്യുത പ്രവാഹത്തിന്റെ അളവിനെ ആധാരമാക്കിയുള്ള രാസവിശ്ലേഷണ തന്ത്രം.', '', NULL),
(305, 'amphichroric', 'ഉഭയവര്‍ണ', 'ആംഫിക്രാറിക്‌, അമ്ലത്തില്‍ ഒരു നിറവും ക്ഷാരത്തില്‍ മറ്റൊരു നിറവും തരുന്ന സംയുക്തം.', '', NULL),
(306, 'amphidiploidy', 'ആംഫിഡിപ്ലോയിഡി', 'അല്ലോടെട്രാപ്ലോയിഡ്‌ അവസ്ഥ.', '', NULL),
(307, 'amphimixis', 'ഉഭയമിശ്രണം', 'പും-സ്‌ത്രീബീജ സംയോജനം.', '', NULL),
(308, 'amphiprotic', 'ഉഭയപ്രാട്ടികം', 'ലീനത്തിന്‌ അനുസൃതമായി ക്ഷാരഗുണമോ അമ്ലഗുണമോ പ്രദര്‍ശിപ്പിക്കുന്ന ലായകം. ഉദാ: വെള്ളം ഹൈഡ്രാക്ലോറിക്‌ ആസിഡുമായി പ്രവര്‍ത്തിക്കുമ്പോള്‍ ക്ഷാരഗുണവും അമോണിയയുമായി പ്രവര്‍ത്തിക്കുമ്പോള്‍ അമ്ലഗുണവും കാണിക്കുന്നു. HCl + H2O →  H3O + + Cl- ആസിഡ്‌ ബേസ്‌ ആസിഡ്‌ ബേസ്‌ NH3 + H2O →  NH4+ +OH- ബേസ്‌ ആസിഡ്‌ ആസിഡ്‌ ബേസ്‌', '', NULL),
(309, 'amphoteric', 'ഉഭയധര്‍മി', 'അമ്ലവുമായോ, ക്ഷാരവുമായോ പ്രവര്‍ത്തിച്ച്‌ ലവണമാകാന്‍ കഴിവുള്ളവ. ഉദാ: ZnO. ഇത്‌ അമ്ലങ്ങളിലോ ക്ഷാരങ്ങളിലോ ലയിക്കുമ്പോള്‍ ലവണമുണ്ടാകുന്നു. ZnO+ 2HCl  → ZnCl2 + H2O, ZnO + 2NaOH → Na2 ZnO2+ H2O', '', NULL),
(310, 'amplification factor', 'പ്രവര്‍ധക ഗുണാങ്കം', '1. ട്രയോഡ്‌, ടെട്രാഡ്‌, ട്രാന്‍സിസ്റ്റര്‍ മുതലായ ഘടകങ്ങളുടെ പ്രവര്‍ധക ശേഷി സൂചിപ്പിക്കുന്ന ഒരു സ്ഥിരാങ്കം. 2. ഒരു പ്രവര്‍ധകത്തിന്റെ പ്രവര്‍ധക ക്ഷമത.', '', NULL),
(311, 'amplifier', 'ആംപ്ലിഫയര്‍', 'പ്രവര്‍ധകം, വൈദ്യുത സിഗ്നലുകളുടെ ആയതി വര്‍ദ്ധിപ്പിക്കുന്ന ഉപകരണം. വോള്‍ട്ടേജ്‌ ആംപ്ലിഫയര്‍, പവര്‍ ആംപ്ലിഫയര്‍ എന്നിങ്ങനെ വിവിധ തരത്തില്‍ ഉണ്ട്‌.', '', NULL),
(312, 'amplitude', 'കോണാങ്കം', '(maths) ആര്‍ഗാന്‍ ആരേഖത്തില്‍ സമ്മിശ്ര സംഖ്യയെ പ്രതിനിധീകരിക്കുന്ന സദിശവും വാസ്‌തവികാക്ഷവും തമ്മിലുള്ള കോണ്‍ ( φ) . ഇതിന്‌ argument എന്നും പേരുണ്ട്‌.', '', NULL),
(313, 'amplitude', 'ആയതി', '(phy) ആയാമം, ക്രമമായി ദോലനം ചെയ്യുന്ന ഒരു വസ്‌തുവിന്റെയോ തരംഗത്തിന്റെയോ മാധ്യസ്ഥാനത്തുനിന്നുള്ള പരമാവധി വിസ്ഥാപനം. ചിത്രത്തില്‍ തരംഗത്തിന്റെ ആയാമം A ആണ്‌. പെന്‍ഡുലത്തിന്റെ ആയാമം a.', '', NULL),
(314, 'amplitude modulation', 'ആയാമ മോഡുലനം', '-', 'modulation', NULL),
(315, 'amu', 'ആറ്റോമിക് മാസ് യൂണിറ്റ്', 'atomic mass unit എന്നതിന്റെ ചുരുക്കം.', '', NULL),
(316, 'amyloplast', 'അമൈലോപ്ലാസ്റ്റ്‌', 'ഒരിനം ല്യൂക്കോ പ്ലാസ്റ്റുകള്‍. ഇതില്‍ അന്നജം സംഭരിച്ചിരിക്കുന്നു. ഉദാ: ഉരുളക്കിഴങ്ങിലെ ല്യൂക്കോപ്ലാസ്റ്റുകള്‍.', '', NULL),
(317, 'amylose', 'അമൈലോസ്‌', 'ഒരു ബഹുസാക്കറൈഡ്‌. സ്റ്റാര്‍ച്ചിന്റെ ഘടകം. അയൊഡിന്‍ ലായനിക്ക്‌ നീലനിറം നല്‍കുന്നു.', '', NULL),
(318, 'anabiosis', 'സുപ്‌ത ജീവിതം', '(bio) വരള്‍ച്ചയെ അതിജീവിക്കാന്‍ സുപ്‌താവസ്ഥയില്‍ കഴിയുന്നത്‌. നവുണ്ടായാല്‍ പഴയ അവസ്ഥയിലേക്ക്‌ തിരിച്ചുവരും.', '', NULL),
(319, 'anabolism', 'അനബോളിസം', 'ലളിതമായ തന്മാത്രകളില്‍ നിന്ന്‌ കൂടുതല്‍ സങ്കീര്‍ണമായ തന്മാത്രകള്‍ ഉണ്ടാക്കിയെടുക്കുന്ന പ്രക്രിയ. ഇത്‌ ഉപാപചയ പ്രക്രിയയുടെ ഒരു വശമാണ്‌. catabolism നോക്കുക.', '', NULL),
(320, 'anadromous', 'അനാഡ്രാമസ്‌', 'കടലില്‍ നിന്ന്‌ ശുദ്ധജലത്തിലേക്ക്‌ സഞ്ചരിക്കല്‍. ഉദാ: സാല്‍മണ്‍ മത്സ്യം.', '', NULL),
(321, 'anaemia', 'അനീമിയ', 'ചുവന്ന രക്താണുക്കളുടെ കുറവോ, അവയിലെ ഹീമോഗ്ലോബിന്റെ കുറവോ കൊണ്ട്‌ ഉണ്ടാകുന്ന അനാരോഗ്യാവസ്ഥ. വിളര്‍ച്ച, പെട്ടെന്ന്‌ കിതക്കുകയും ക്ഷീണിക്കുകയും ചെയ്യുക, ആഹാരത്തോടുള്ള താല്‌പര്യക്കുറവ്‌, ¾ാനത മുതലായവ ഈ അവസ്ഥയുടെ ലക്ഷണങ്ങളാണ്‌.', '', NULL),
(322, 'anaerobic respiration', 'അവായവശ്വസനം', 'ഓക്‌സിജന്റെ സഹായമില്ലാതെ നടക്കുന്ന ശ്വസനം.', '', NULL),
(323, 'anafront', 'അനാഫ്രണ്ട്', 'മുകളിലേക്ക് ചൂടുള്ളതോ തണുത്തതോ ആയ വായുപ്രവാഹം ഉള്ള സ്ഥലം. സാധാരണയായി മേഘങ്ങളും മഴയുടെ രൂപവും ഉണ്ടാക്കുന്നു.', '', NULL),
(324, 'analgesic', 'വേദന സംഹാരി', 'വേദന ഇല്ലാതാക്കുന്ന മരുന്ന്', '', NULL),
(325, 'analogous', 'സമധര്‍മ്മ', '(bio) വ്യത്യസ്‌ത സ്‌പീഷീസുകളില്‍ സമാന ധര്‍മ്മം നിര്‍വഹിക്കുന്ന അവയവങ്ങളെ പരാമര്‍ശിക്കുന്ന പദം. അതേസമയം ഉല്‍പത്തിയില്‍ അവ വ്യത്യസ്‌തമായിരിക്കും. പ്രാണികളുടെ ചിറകുകളും പക്ഷികളുടെ ചിറകുകളും ഉദാഹരണമാണ്‌. homologous നോക്കുക.', '', NULL),
(326, 'analogue modulation', 'അനുരൂപ മോഡുലനം', '-', 'modulation', NULL),
(327, 'analysis', 'വിശ്ലേഷണം', 'ഒരു പദാര്‍ഥത്തിന്റെ ചേരുവ കണ്ടുപിടിക്കുന്ന പ്രക്രിയ.', '', NULL),
(328, 'analytical chemistry', 'വിശ്ലേഷണ രസതന്ത്രം', 'രാസവിശ്ലേഷണത്തിന്റെ തത്വങ്ങളും മാര്‍ഗങ്ങളും ഉള്‍ക്കൊള്ളുന്ന രസതന്ത്രശാഖ.', '', NULL),
(329, 'anamorphosis', 'പ്രകായാന്തരികം', 'അനാമോര്‍ഫോസിസ്‌. സസ്യങ്ങളുടെയും ജന്തുക്കളുടെയും ശരീരഘടനയിലും പ്രവര്‍ത്തനത്തിലും പരിണാമഫലമായി സങ്കീര്‍ണത കൂടി വരല്‍.', '', NULL),
(330, 'anaphase', 'അനാഫേസ്‌', 'കോശവിഭജനത്തിലെ ഒരു ഘട്ടം. മെറ്റാഫേസിനും ടീലോഫേസിനും ഇടയ്‌ക്കാണിത്‌. മാതൃകോശത്തിലെ ക്രാമസോമുകള്‍ നീളത്തില്‍ വിഭജിച്ചുണ്ടാകുന്ന പുത്രികാ ക്രാമസോമുകള്‍ വിപരീത ധ്രുവങ്ങളിലേക്ക്‌ സഞ്ചരിക്കുന്നത്‌ ഈ ഘട്ടത്തിലാണ്‌.', '', NULL),
(331, 'anaphylaxis', 'അനാഫൈലാക്‌സിസ്‌', 'ശരീരബാഹ്യ പ്രാട്ടീനോട്‌ ശരീരത്തിന്റെ പ്രതിരോധ പ്രവര്‍ത്തനം. സാധാരണയായി ഇത്‌ അപകടകരമാണ്‌. ആന്റിബയോട്ടിക്കുകള്‍ കുത്തിവച്ചാല്‍ ചില ആളുകള്‍ മരിക്കുന്നത്‌ അനാഫൈലാക്‌സിസ്‌ പ്രവര്‍ത്തനം കൊണ്ടാണ്‌.', '', NULL),
(332, 'anastral', 'അതാരക', 'കോശവിഭജന സമയത്ത്‌ ആസ്റ്ററുകള്‍ ഇല്ലാത്ത അവസ്ഥ.', '', NULL),
(333, 'anatropous', 'പ്രതീപം', 'അണ്ഡത്തിന്റെ സൂക്ഷ്‌മ രന്ധ്രം. ഫ്യൂണിക്കിളിനോട്‌ അടുത്തിരിക്കുന്ന ക്രമീകരണം.', '', NULL),
(334, 'anatropous ovule', 'നമ്രാണ്ഡം', 'തലകീഴായ ബീജാണ്ഡം. ഉദാ: ചെമ്പരത്തി.', '', NULL),
(335, 'AND gate', 'ആന്റ്‌ ഗേറ്റ്', '-', 'logic gates', NULL),
(336, 'androecium', 'കേസരപുടം', 'ഒരു പൂവിലെ കേസരങ്ങള്‍ ചേര്‍ന്നുണ്ടാവുന്ന മണ്ഡലം. flower നോക്കുക.', '', NULL),
(337, 'androgen', 'ആന്‍ഡ്രോജന്‍', 'മനുഷ്യനിലും മറ്റു കശേരുകികളിലും പുരുഷ ലക്ഷണങ്ങള്‍ക്ക്‌ കാരണമാകുന്ന ഹോര്‍മോണുകളുടെ പൊതുനാമം. വൃഷണങ്ങള്‍, അണ്ഡാശയം, അഡ്രീനല്‍ ഗ്രന്ഥി എന്നിവയില്‍ നിന്ന്‌ ഉത്‌പാദിപ്പിക്കപ്പെടുന്നു.', '', NULL),
(338, 'Andromeda', 'ആന്‍ഡ്രോമീഡ', '1. ഉത്തര ഖഗോളത്തിലെ ഒരു നക്ഷത്രമണ്ഡലം. M31 സര്‍പ്പിള ഗാലക്‌സി ഇതിലാണ്‌. 2. M31 എന്ന സര്‍പ്പിള ഗാലക്‌സിയുടെ പേര്‌. ഉത്തരാര്‍ധഗോളത്തില്‍ നഗ്നനേത്രം കൊണ്ട്‌ കാണാവുന്ന ഏക ഗാലക്‌സി ഇതാണ്‌.', '', NULL),
(339, 'anemometer', 'ആനിമോ മീറ്റര്‍', 'വാതമാപി. കാറ്റിന്റെ വേഗതയും ദിശയും അളക്കുന്നതിനുള്ള ഉപകരണം.', '', NULL),
(340, 'anemophily', 'വായുപരാഗണം', 'കാറ്റുവഴിയുള്ള പരാഗണം.', '', NULL),
(341, 'anemotaxis', 'വാതാനുചലനം', 'വായുഗതി പ്രരിതമായുണ്ടാകുന്ന ചലനം.', '', NULL),
(342, 'aneroid barometer', 'ആനിറോയ്‌ഡ്‌ ബാരോമീറ്റര്‍', 'വായുവിന്റെ മര്‍ദം അളക്കുന്നതിനുള്ള ഉപകരണം. ഇതില്‍ രസമോ മറ്റ്‌ ദ്രാവകങ്ങളോ ഉപയോഗിക്കുന്നില്ല. ഭാഗികമായി വായു നീക്കിയ ലോഹപ്പെട്ടികൊണ്ടാണ്‌ ഇതുണ്ടാക്കുന്നത്‌. പുറത്തുള്ള വായുവിന്റെ മര്‍ദമനുസരിച്ച്‌ ഇതിന്റെ പാര്‍ശ്വങ്ങള്‍ പുറത്തേക്ക്‌ വീര്‍ക്കുകയോ അകത്തേക്കു കുഴിയുകയോ ചെയ്യും. ഈ ചലനം ഒരു സ്‌പ്രിംഗും സൂചിയും ഉപയോഗിച്ച്‌ സ്‌കെയിലില്‍ രേഖപ്പെടുത്തുന്ന സംവിധാനമാണിത്‌.', '', NULL),
(343, 'aneuploidy', 'വിഷമപ്ലോയ്‌ഡി', 'ക്രാമസോം സെറ്റുകളില്‍ ഒരു ക്രാമസോം കൂടുതലായോ കുറവായോ ഉള്ള അവസ്ഥ. കോശവിഭജന സമയത്ത്‌ ക്രാമസോമുകളുടെ ക്രമമല്ലാത്ത വിതരണം നടക്കുന്നതു മൂലമാണ്‌ ഈ അവസ്ഥ ഉണ്ടാകുന്നത്‌. ഇത്‌ ജനിതക അസന്തുലിതാവസ്ഥക്ക്‌ കാരണമാകും. ഡണ്‍ൗസ്‌ സിന്‍ഡ്രാം (മംഗോളിസം) എന്ന ജനിതക വൈകല്യത്തിനു കാരണം 21-ാം ക്രാമസോമിന്റെ വിഷമപ്ലോയിഡിയാണ്‌.', '', NULL),
(344, 'angle of centre', 'കേന്ദ്ര കോണ്‍', 'ഒരു ചാപം അത്‌ ഉള്‍ക്കൊള്ളുന്ന വൃത്തത്തിന്റെ കേന്ദ്രത്തിലുണ്ടാക്കുന്ന കോണ്‍.', '', NULL),
(345, 'angle of depression', 'കീഴ്‌കോണ്‍', 'വീക്ഷണ രശ്‌മി കീഴോട്ടാകുമ്പോള്‍, വീക്ഷണ രശ്‌മിയും തിരശ്ചീനരശ്‌മിയും കൂടി നിര്‍ണയിക്കുന്ന കോണ്‍.', '', NULL),
(346, 'angle of dip', 'നതികോണ്‍', 'ഒരു പ്രദേശത്ത്‌ ഭൂകാന്തികതാദിശ തിരശ്ചീന ദിശയില്‍ നിന്ന്‌ എത്ര കീഴോട്ടാണെന്ന്‌ കാണിക്കുന്ന കോണ്‍. ഭൂതലത്തില്‍ എല്ലായിടത്തും ഇത്‌ തുല്യമല്ല. ഏറ്റവും കുറവ്‌ (പൂജ്യം നതി) ഭൂമധ്യരേഖയോടടുത്താണ്‌.', '', NULL),
(347, 'angle of elevation', 'മേല്‍ കോണ്‍', 'വീക്ഷണരശ്‌മി മുകളിലേക്കാകുമ്പോള്‍ വീക്ഷണരശ്‌മിയും തിരശ്ചീനരശ്‌മിയും കൂടി നിര്‍ണയിക്കുന്ന കോണ്‍.൨', '', NULL),
(348, 'angstrom', 'ആങ്‌സ്‌ട്രം', 'വളരെ ചെറിയ നീളം/തരംഗദൈര്‍ഘ്യം അളക്കുന്ന ഏകകം. പ്രതീകം Å. 1Å=10−10m', '', NULL),
(349, 'angular acceleration', 'കോണീയ ത്വരണം', 'കോണീയ പ്രവേഗം മാറുന്നതിന്റെ നിരക്ക്‌. ഏകകം. rads−2.', '', NULL),
(350, 'angular displacement', 'കോണീയ സ്ഥാനാന്തരം', 'കോണീയ വിസ്ഥാപനം. ഒരു നിര്‍ദിഷ്‌ട ബിന്ദുവിനെ ( O) ആധാരമാക്കി ഏതെങ്കിലും ബിന്ദുവിന്റെ സ്ഥാനത്തിന്‌ വരുന്ന മാറ്റം കോണളവില്‍ പറയുന്നത്‌. ചിത്രത്തില്‍ വസ്‌തു Aയില്‍ നിന്ന്‌ B യിലെത്തിയപ്പോള്‍ ഉണ്ടായ കോണീയ സ്ഥാനാന്തരം φ ആണ്‌.', '', NULL),
(351, 'angular frequency', 'കോണീയ ആവൃത്തി', 'ഭ്രമണം /ദോലനം ചെയ്യുന്ന ഒരു വസ്‌തുവിന്‌ ഒരു സെക്കന്റില്‍ ഉണ്ടാകുന്ന കോണീയ വിസ്ഥാപനം. പ്രതീകം ω.ഏകകം rads-1. കോണീയ ആവൃത്തിയും ( ω) ആവൃത്തിയും ( ν) തമ്മിലുള്ള ബന്ധം. ω=2 πν', '', NULL),
(352, 'angular magnification', 'കോണീയ ആവര്‍ധനം', '-', 'magnification', NULL),
(353, 'angular momentum', 'കോണീയ സംവേഗം', '-', 'momentum ', NULL),
(354, 'angular velocity', 'കോണീയ പ്രവേഗം', 'കോണീയ വിസ്ഥാപനത്തിന്റെ നിരക്ക്‌. ഏകകം rads-1.', '', NULL),
(355, 'anhydride', 'അന്‍ഹൈഡ്രഡ്‌', '1. ഒരു തന്മാത്ര ഓക്‌സി അമ്ലത്തില്‍ നിന്ന്‌ ഒരു തന്മാത്ര ജലം നീക്കം ചെയ്‌താല്‍ കിട്ടുന്നതിനെ അതിന്റെ അന്‍ഹൈഡ്രഡ്‌ എന്നു വിളിക്കുന്നു. ഉദാ: H2SO4- H2O = SO3(സള്‍ഫ്യൂറിക്‌ അന്‍ഹൈഡ്രഡ്‌) H2CO3-H2O= CO2(കാര്‍ബണിക അന്‍ഹൈഡ്രഡ്‌). 2. കാര്‍ബണിക രസതന്ത്രത്തില്‍ പറയുന്ന അമ്ല അന്‍ഹൈഡ്രഡുകള്‍ ഉണ്ടാകുന്നത്‌ രണ്ട്‌ അമ്ല തന്‍മാത്രകളില്‍ നിന്ന്‌ ഒരു തന്‍മാത്ര ജലം നീക്കിയിട്ടാണ്‌. ഉദാ: അസറ്റിക്‌ അന്‍ഹൈഡ്രഡ്‌.', '', NULL),
(356, 'anhydrite', 'അന്‍ഹൈഡ്രറ്റ്‌', 'പ്രകൃതിയില്‍ കണ്ടുവരുന്ന കാത്സ്യം സള്‍ഫേറ്റിന്റെ രൂപം.', '', NULL),
(357, 'anhydrous', 'അന്‍ഹൈഡ്രസ്‌', 'രാസസംയുക്തത്തിന്റെ ഒരു തന്മാത്രയില്‍ നിന്ന്‌ ക്രിസ്റ്റല്‍ ജലം നീക്കിയാല്‍ കിട്ടുന്ന വസ്‌തു. ക്രിസ്റ്റലീകരണ സമയത്ത്‌ ആഗിരണം ചെയ്‌ത ജലം നീക്കം ചെയ്‌ത അവസ്ഥയെ സൂചിപ്പിക്കുവാന്‍ ഉപയോഗിക്കുന്നു. ഉദാ: കോപ്പര്‍ സള്‍ഫേറ്റ്‌ ( CuSO4 5H2O), അന്‍ഹൈഡ്രസ്‌ കോപ്പര്‍ സള്‍ഫേറ്റ്‌ ( CuSO4).', '', NULL),
(358, 'aniline', 'അനിലിന്‍', 'ഫിനൈല്‍ അമീന്‍. അമിനോ ബെന്‍സീന്‍ C6H5− NH2. ശുദ്ധ അനിലിന്‍ നിറമില്ലാത്ത സവിശേഷ ഗന്ധമുള്ള ദ്രാവകം. തിളനില 184.4 0 C. നൈട്രാ ബെന്‍സീന്‍ നിരോക്‌സീകരിച്ചാണ്‌ അനിലിന്‍ നിര്‍മിക്കുന്നത്‌. ചായങ്ങള്‍, ഔഷധങ്ങള്‍ മുതലായവയുടെ നിര്‍മാണത്തിന്‌ ഉപയോഗിക്കുന്നു.', '', NULL),
(359, 'animal black', 'മൃഗക്കറുപ്പ്‌', 'മൃഗങ്ങളുടെ എല്ലുകള്‍ അല്ലെങ്കില്‍ കൊമ്പുകള്‍ കരിച്ചുണ്ടാക്കുന്ന അതിസൂക്ഷ്‌മ കാര്‍ബണ്‍ തരികള്‍. ശുദ്ധീകരണ വസ്‌തുവായും വര്‍ണ്ണകമായും ഉപയോഗിക്കുന്നു.', '', NULL),
(360, 'animal charcoal', 'മൃഗക്കരി', 'മൃഗാവശിഷ്‌ടങ്ങള്‍ ഭഞ്‌ജന സേവനത്തിന്‌ വിധേയമാക്കുമ്പോള്‍ കിട്ടുന്ന 10% കാര്‍ബണിന്റെയും 90% അകാര്‍ബണിക വസ്‌തുക്കളുടെയും മിശ്രിതം. ഒരു ശുദ്ധീകരണ വസ്‌തു.', '', NULL),
(361, 'animal kingdom', 'ജന്തുലോകം', 'ജീവലോകത്തിലെ അഞ്ച്‌ ബൃഹത്‌ വിഭാഗങ്ങളില്‍ ഒന്ന ്‌. ബഹുകോശ ജന്തുക്കള്‍ ഉള്‍പ്പെടുന്നു. kingdom animalia എന്ന്‌ വര്‍ഗീകരണ ശാസ്‌ത്രത്തില്‍ പരാമര്‍ശിക്കപ്പെടുന്നു.', '', NULL),
(362, 'animal pole', 'സജീവധ്രുവം', 'ഭ്രൂണ രൂപീകരണം ആരംഭിക്കാന്‍ പോകുന്ന അണ്ഡത്തിലെ ന്യൂക്ലിയസ്സിനോട്‌ അടുത്ത ധ്രുവം. എതിര്‍ധ്രുവത്തെ കായികധ്രുവം ( vegital pole) എന്ന്‌ പറയും. സജീവ ധ്രുവത്തില്‍ പീതകം ( yolk) കുറവായിരിക്കും. കോശവിഭജന പ്രക്രിയകള്‍ ഇവിടെയാണ്‌ കൂടുതല്‍ സജീവം.', '', NULL),
(363, 'anion', 'ആനയോണ്‍', 'ഋണചാര്‍ജുള്ള അയോണ്‍. ഒരു വൈദ്യുത മണ്ഡലത്തില്‍ ഇവ ധന ഇലക്‌ട്രാഡിലേക്കു നീങ്ങുന്നു. ഉദാ: Cl−, (OH)−, SO4-2.', '', NULL),
(364, 'anisaldehyde', 'അനിസാള്‍ഡിഹൈഡ്‌', 'CH3−O−C6H4−CHO. നിറമില്ലാത്ത, എണ്ണരൂപത്തിലുള്ള ദ്രാവകം. സന്ദൗര്യ വസ്‌തുക്കളിലും സുഗന്ധ വസ്‌തുക്കളിലും ഉപയോഗിക്കുന്നു.', '', NULL),
(365, 'anisogamy', 'അസമയുഗ്മനം', 'വ്യത്യസ്‌ത വലിപ്പമുള്ള ആണ്‍-പെണ്‍ ബീജങ്ങളുടെ സംയോജനം.', '', NULL),
(366, 'anisole', 'അനിസോള്‍', 'C6H5−O−CH3. മീഥൈല്‍ ഫിനൈല്‍ ഈഥര്‍. സുഗന്ധമുള്ള നിറമില്ലാത്ത ദ്രാവകം.', '', NULL),
(367, 'anisotonic', 'അനൈസോടോണിക്ക്‌', '-', 'isotonic ', NULL),
(368, 'anisotropy', 'അനൈസോട്രാപ്പി', 'വ്യത്യസ്‌ത ദിശകളില്‍ ചില ഭൗതിക ഗുണങ്ങള്‍ വ്യത്യസ്‌തങ്ങളാവുന്ന മാധ്യമത്തിന്റെ സ്വഭാവം. ഉദാ: ആ സ്വഭാവം പ്രദര്‍ശിപ്പിക്കുന്ന ഒരു വസ്‌തുവിലൂടെ പ്രകാശം സഞ്ചരിക്കുമ്പോള്‍ അതിന്റെ വേഗത വ്യത്യസ്‌ത ദിശകളില്‍ വ്യത്യസ്‌തമായിരിക്കും. isotropy നോക്കുക.', '', NULL),
(369, 'annealing', 'താപാനുശീതനം', 'സ്റ്റീല്‍, ഗ്ലാസ്‌ മുതലായ വസ്‌തുക്കളെ അനുയോജ്യമായ താപനിലവരെ ചൂടാക്കി സാവധാനം തണുപ്പിക്കുന്ന പ്രക്രിയ. ക്രിസ്റ്റല്‍ ഘടനയില്‍ വരുന്ന വൈകല്യങ്ങള്‍ ഇല്ലാതാക്കാനാണ്‌ ഇത്‌ ചെയ്യുന്നത്‌.', '', NULL),
(370, 'annihilation', 'ഉന്മൂലനം', 'കണവും പ്രതികണവും തമ്മില്‍ പ്രതിപ്രവര്‍ത്തിക്കുമ്പോള്‍ പരസ്‌പരം നശിപ്പിച്ച്‌ ഊര്‍ജമാവുന്ന പ്രക്രിയ. ഉദാ: ഇലക്‌ട്രാണ്‍+പോസിട്രാണ്‍ →ഊര്‍ജം. antiparticle നോക്കുക.', '', NULL),
(371, 'annual parallax', 'വാര്‍ഷിക ലംബനം', 'സൂര്യന്‌ ചുറ്റുമുള്ള ഭൂമിയുടെ പരിക്രമണം മൂലം നക്ഷത്രങ്ങളുടെ പ്രത്യക്ഷ സ്ഥാനത്തില്‍ വരുന്ന വ്യത്യാസം. ദൃഗ്‌ഭ്രംശം എന്നും പറയും. ഭൂമിയുടെ പരിക്രമണ പഥത്തിന്റെ വ്യാസം നക്ഷത്രത്തില്‍ സമ്മുഖമാക്കുന്ന കോണ്‍ ആണിത്‌. പ്രാക്‌സിമാ സെന്റൗറിയുടെ വാര്‍ഷിക ലംബനം പരമാവധി 0.71 സെക്കന്റ്‌സ്‌ ഓഫ്‌ ആര്‍ക്‌ ആണ്‌. parallax നോക്കുക.', '', NULL),
(372, 'annual rings', 'വാര്‍ഷിക വലയങ്ങള്‍', 'വളര്‍ച്ചാവലയങ്ങള്‍. വളര്‍ച്ചയെത്തിയ ഒരു വൃക്ഷം കുറുകെ ഛേദിച്ചാല്‍ കാണുന്ന സംകേന്ദ്രവലയങ്ങള്‍. വളര്‍ച്ചയുടെ ഭാഗമായി ഓരോ വര്‍ഷവും സൈലത്തോടു കൂട്ടിച്ചേര്‍ക്കപ്പെടുന്ന ഇവ എണ്ണിയാല്‍ വൃക്ഷത്തിന്റെ ഏകദേശ പ്രായം കണക്കാക്കാം.', '', NULL),
(373, 'annuals', 'ഏകവര്‍ഷികള്‍', 'ഒരു വര്‍ഷത്തിനുള്ളിലോ ഒരു പ്രത്യേക ഋതുവിലോ ജീവിതചക്രം പൂര്‍ത്തിയാക്കുന്ന സസ്യങ്ങള്‍. ഉദാ: നെല്ല്‌.', '', NULL),
(374, 'annular eclipse', 'വലയ സൂര്യഗ്രഹണം', '-', 'eclipse', NULL),
(375, 'anode', 'ആനോഡ്‌', '1. വൈദ്യുത വിശ്ലേഷണത്തില്‍ ധനവോള്‍ട്ടത പ്രയോഗിക്കപ്പെടുന്ന ഇലക്‌ട്രാഡ്‌. 2. ഒരു വൈദ്യുത ഉപകരണത്തിലേക്ക്‌ വിദ്യുത്‌ധാര പ്രവേശിക്കുന്ന ഇലക്‌ട്രാഡ്‌.', '', NULL),
(376, 'anodising', 'ആനോഡീകരണം', 'ഒരു ഓക്‌സൈഡ്‌ സ്‌തരം വൈദ്യുതി വിശ്ലേഷണം വഴി നിക്ഷേപിച്ച്‌ അലൂമിനിയം ലോഹത്തെ ക്ഷാരണത്തില്‍ നിന്നും രക്ഷിക്കുന്ന പ്രക്രിയ.', '', NULL),
(377, 'anomalistic month', 'പരിമാസം', 'പരിക്രമണ പഥത്തില്‍ ഭൂമിയോട്‌ ഏറ്റവും അടുത്തുള്ള സ്ഥാനത്തില്‍ തുടങ്ങി, അവിടെത്തന്നെ മടങ്ങിയെത്താന്‍ ചന്ദ്രന്‌ ആവശ്യമായ ശരാശരി കാലം. 27 ദിവസം, 13 മണിക്കൂര്‍, 18 മിനിറ്റ്‌, 33.2 സെക്കന്റ്‌. Month നോക്കുക.', '', NULL),
(378, 'anomalistic year', 'പരിവര്‍ഷം', 'സൂര്യസമീപക സ്ഥാനത്ത്‌ തുടങ്ങി ഒരുതവണ സൂര്യനെ പരിക്രമണം ചെയ്യാന്‍ ഭൂമിക്ക്‌ ആവശ്യമായ സമയം. 365.25964 ദിവസം. സൂര്യസമീപകം. നേരിയ തോതില്‍ കിഴക്കുദിശയില്‍ സഞ്ചരിക്കുന്നതുകൊണ്ട്‌ ഇത്‌ സൗരവര്‍ഷത്തേക്കാള്‍ (365.24219 ദി) അല്‍പ്പം കൂടുതലാണ്‌. Year നോക്കുക.', '', NULL),
(379, 'anomalous expansion', 'അസംഗത വികാസം', 'താപനില താഴുന്നതനുസരിച്ച്‌ ചില ദ്രാവകങ്ങള്‍ വികസിക്കുന്ന പ്രതിഭാസം. സാധാരണ, താപനില താഴുന്നതനുസരിച്ച്‌ പദാര്‍ഥങ്ങള്‍ സങ്കോചിക്കുകയാണ്‌ പതിവ്‌. എന്നാല്‍ 4 0 C ന്‌ താഴെ 0 0 C വരെ ജലം അസംഗത വികാസം പ്രദര്‍ശിപ്പിക്കുന്നു.', '', NULL),
(380, 'anorexia', 'അനോറക്‌സിയ', 'വിശപ്പില്ലായ്‌മ. ആഹാരത്തിലുള്ള വിരക്തി.', '', NULL),
(381, 'antagonism', 'വിരുദ്ധജീവനം', 'ഒന്നിന്റെ ക്ഷയിക്കലിനോ നാശത്തിനോ ഇടയാക്കുന്ന വിധത്തിലുള്ള രണ്ട്‌ ജീവികളുടെ സഹജീവനം. ഉദാ: പെന്‍സിലിന്‍ പോലുള്ള ആന്റിബയോട്ടിക്ക്‌ നിര്‍മ്മിച്ച്‌ കൂടെയുള്ള ജീവികള്‍ക്ക്‌ നാശം വരുത്തുന്നത്‌.', '', NULL),
(382, 'antarctic', 'അന്റാര്‍ടിക്‌', 'ദക്ഷിണ ധ്രുവീയ മേഖലയെ സൂചിപ്പിക്കുന്ന വിശേഷണ പദം. നാമമായി ഉപയോഗിക്കുമ്പോള്‍ അന്റാര്‍ടിക്‌ വൃത്തത്തിനകത്തായി സ്ഥിതിചെയ്യുന്ന സ്ഥലം.', '', NULL),
(383, 'antarctic circle', 'അന്റാര്‍ട്ടിക്‌ വൃത്തം', 'ദക്ഷിണ അക്ഷാംശം 66 0 32 1 നെ സൂചിപ്പിക്കുന്ന പദം.', '', NULL),
(384, 'anterior', 'പൂര്‍വം', 'ജന്തുശരീരത്തിലെ ശീര്‍ഷ അഗ്രത്തെ സൂചിപ്പിക്കുന്നു. ചലന സമയത്ത്‌ ഈ ഭാഗമാണ്‌ സാധാരണയായി ആദ്യം മുന്നോട്ടു നീങ്ങുക.', '', NULL),
(385, 'antenna ', 'ആന്റിന', '1. (bio) ആന്റിന. പ്രാണികളുടെയും പഴുതാര, കൊഞ്ച്‌ മുതലായവയുടെയും തലയിലെ ഒരു അനുബന്ധ അവയവം. ഇവ പ്രധാനമായും സ്‌പര്‍ശനേന്ദ്രിയങ്ങളായാണ്‌ പ്രവര്‍ത്തിക്കുന്നത്‌. നിശാശലഭങ്ങളില്‍ ഇവ ഘ്രാണേന്ദ്രിയമായും പ്രവര്‍ത്തിക്കും. 2. (phy) ആന്റിന aerial നോക്കുക.', '', NULL),
(386, 'anther', 'പരാഗകോശം', 'പുഷ്‌പകേസരത്തില്‍ പരാഗങ്ങള്‍ സ്ഥിതിചെയ്യുന്ന അറ.', '', NULL),
(387, 'antheridium', 'പരാഗികം', 'ബ്രയോഫൈറ്റ, ടെറിഡോഫൈറ്റ എന്നിവയിലെ പുംലൈംഗികാവയവം. bryophyta നോക്കുക.', '', NULL),
(388, 'antherozoid', 'പുംബീജം', 'പരാഗികത്തില്‍ ഉത്‌പാദിപ്പിക്കപ്പെടുന്ന ചലനശേഷിയുള്ള ആണ്‍ബീജം.', '', NULL),
(389, 'anthocyanin', 'ആന്തോസയാനിന്‍', 'പൂക്കള്‍, ഫലങ്ങള്‍, കാണ്ഡങ്ങള്‍, ഇലകള്‍ എന്നിവയുടെ കോശരസത്തില്‍ കണ്ടുവരുന്ന ഗ്ലൈകോസൈഡ്‌ വര്‍ണകം. ഇത്‌ ചുവപ്പോ, നീലയോ, വയലറ്റോ ആയിരിക്കും. ഇവയാണ്‌ പൂക്കള്‍, തണ്ട്‌ മുതലായവയ്‌ക്ക്‌ വിവിധ നിറങ്ങള്‍ കൊടുക്കുന്നത്‌.', '', NULL),
(390, 'anthozoa', 'ആന്തോസോവ', 'കടല്‍ ആനിമോണുകള്‍, പവിഴപ്പുറ്റു ജീവികള്‍ എന്നിവ ഉള്‍പ്പെടുന്ന ജന്തുവിഭാഗം. സീലെന്റെറേറ്റ എന്ന ഫൈലത്തില്‍ പെടുന്നു.', '', NULL),
(391, 'anthracene', 'ആന്ത്രസിന്‍', 'കോള്‍ട്ടാറില്‍ നിന്നു ലഭിക്കുന്ന ഒരു അരോമാറ്റിക ഹൈഡ്രാ കാര്‍ബണ്‍. ശുദ്ധ ആന്ത്രസിന്‍ നിറമില്ലാത്ത ക്രിസ്റ്റലുകളാണ്‌. ഉരുകല്‍ നില 217 0 C. ചായങ്ങള്‍ നിര്‍മ്മിക്കാന്‍ ഉപയോഗിക്കുന്നു.', '', NULL),
(392, 'anthracite', 'ആന്ത്രാസൈറ്റ്‌', 'ഏറ്റവും പഴക്കമുള്ളതും കാഠിന്യമേറിയതുമായ കല്‍ക്കരി. 95% കാര്‍ബണുണ്ട്‌. ഉയര്‍ന്ന ഇന്ധനമൂല്യമുള്ള കല്‍ക്കരി.', '', NULL),
(393, 'anthropoid apes', 'ആള്‍ക്കുരങ്ങുകള്‍', 'മനുഷ്യനോട്‌ ഏറ്റവുമടുത്ത ബന്ധമുള്ള ജന്തുക്കളായ ചിമ്പാന്‍സി, ഗിബ്ബണ്‍, ഗോറില്ല, ഓറാങ്ങ്‌ എന്നിവ. (മലയാളത്തില്‍ \"ആള്‍ക്കുരങ്ങ്‌\' എന്നു പറയുമ്പോള്‍ തന്നെ മനുഷ്യനുമായുള്ള സാദൃശ്യം സൂചിപ്പിക്കുന്നുണ്ടെങ്കിലും \"എയ്‌പ്‌\' എന്ന വാക്കിന്‌ അങ്ങനെ അര്‍ഥമില്ലാത്തതിനാലാണ്‌ വിശേഷണം വേണ്ടിവന്നത്‌.)', '', NULL),
(394, 'anthropology', 'നരവംശശാസ്‌ത്രം', 'മനുഷ്യസമൂഹങ്ങളുടെ വര്‍ഗീകരണവും വിശകലനവും സംബന്ധിച്ച പഠനങ്ങള്‍ നടത്തുന്ന വിജ്ഞാനശാഖ.', '', NULL),
(395, 'anti auxins', 'ആന്റി ഓക്‌സിന്‍', 'സസ്യങ്ങളില്‍ ഓക്‌സിന്റെ പ്രവര്‍ത്തനത്തെ തടയുന്ന രാസവസ്‌തുക്കള്‍. ഉദാ: 2, 4- dichlorophenoxyaceticacid.', '', NULL),
(396, 'anti clockwise', 'അപ്രദക്ഷിണ ദിശ', 'ക്ലോക്കിലെ സൂചി തിരിയുന്നതിന്റെ വിപരീത ദിശ.', '', NULL),
(397, 'anti vitamins', 'പ്രതിജീവകങ്ങള്‍', 'ജീവകങ്ങളുടെ ജൈവിക പ്രവര്‍ത്തനങ്ങളെ അമര്‍ത്തുന്ന കാര്‍ബണിക സംയുക്തങ്ങള്‍.', '', NULL),
(398, 'antibiotics', 'ആന്റിബയോട്ടിക്‌സ്‌', 'ബാക്‌ടീരിയങ്ങളുടെ പ്രജനനത്തെ തടയുന്ന ചിലയിനം രാസപദാര്‍ഥങ്ങള്‍. ചില ഫംഗസുകള്‍ ഉല്‍പ്പാദിപ്പിക്കുന്നു. 1928 ല്‍ സര്‍ അലക്‌സാണ്ടര്‍ ഫ്‌ളെമിംഗ്‌ (1881- 1955) കണ്ടുപിടിച്ച പെനിസിലിന്‍ ആണ്‌ ആദ്യത്തെ ആന്റിബയോട്ടിക്‌.', '', NULL),
(399, 'antibody', 'ആന്റിബോഡി', 'അന്യവസ്‌തുക്കള്‍ കശേരുകികളുടെ ശരീരത്തില്‍ കടക്കുമ്പോള്‍ അവയ്‌ക്കെതിരായി രക്തത്തില്‍ ഉത്‌പാദിപ്പിക്കപ്പെടുന്ന വസ്‌തു. ഇവ ഗ്ലോബുലിന്‍ പ്രാട്ടീനുകളാണ്‌. ഇമ്യൂണൊഗ്ലോബിലിനുകള്‍ എന്നു വിളിക്കും. ഓരോ തരം പ്രതിവസ്‌തുവും അതിന്റേതായ ആന്റിജനുമായി മാത്രം പ്രതിപ്രവര്‍ത്തിക്കും.', '', NULL),
(400, 'anticatalyst', 'പ്രത്യുല്‍പ്രരകം', 'ഉല്‍പ്രരകങ്ങളുടെ ശേഷി കുറയ്‌ക്കുന്ന പദാര്‍ഥങ്ങള്‍. catalyst നോക്കുക.', '', NULL),
(401, 'antichlor', 'ആന്റിക്ലോര്‍', 'ക്ലോറിനോ ബ്ലീച്ചിംഗ്‌ പൌഡറോ ഉപയോഗിച്ച്‌ ബ്ലീച്ചിംഗ്‌ നടത്തിയ ശേഷം, അധികമുള്ള ക്ലോറിന്‍ നീക്കം ചെയ്യാനുപയോഗിക്കുന്ന വസ്‌തു. ഉദാ: ഹൈഡ്രജന്‍ പെറോക്‌സൈഡ്‌.', '', NULL),
(402, 'anticline', 'അപനതി', ' ഉന്മധ്യ മടക്ക്‌. ശിലാപടലങ്ങള്‍ സമ്മര്‍ദ്ദത്തിന്‌ വിധേയമാകുന്നതിന്റെ ഫലമായി പ്രായം ഏറ്റവും കൂടിയ പടലങ്ങള്‍ ഏറ്റവും ഉള്‍ഭാഗത്ത്‌ വരുന്ന രീതിയില്‍ സൃഷ്‌ടിക്കപ്പെടുന്ന, ഉത്തലഭാഗം മുകളിലേക്കായുള്ള മടക്ക്‌.', '', NULL),
(403, 'Anticlockwise', 'അപ്രദക്ഷിണ ദിശ', 'ക്ലോക്ക് ചലിക്കുന്നതിന്റെ എതിരായി ചലിക്കുന്ന ദിശ. ഇത്തരം ചലനം അപ്രദക്ഷിണ ചലനമെന്ന് പറയാം.', '', NULL),
(404, 'anticodon', 'ആന്റി കൊഡോണ്‍', 'സന്ദേശക RNAയിലെ കൊഡോണുകളുമായി ബന്ധപ്പെടുവാന്‍ ട്രാന്‍സ്‌ഫര്‍ ആര്‍ എന്‍ എയില്‍ ഉള്ള മൂന്ന്‌ ന്യൂക്ലിയോറ്റൈഡുകള്‍. genetic code നോക്കുക.', '', NULL),
(405, 'anticyclone', 'പ്രതിചക്രവാതം', 'അന്തരീക്ഷ വായുവിലുണ്ടാകുന്ന ഗുരുമര്‍ദ വ്യവസ്ഥ. ഗുരുമര്‍ദഭാഗത്ത്‌ നിന്ന്‌ ചുറ്റിലേക്കും വായു കറങ്ങി വീശുന്നു. ഉത്തരാര്‍ധ ഗോളത്തില്‍ പ്രദക്ഷിണ ദിശയിലും ദക്ഷിണാര്‍ധഗോളത്തില്‍ അപ്രദക്ഷിണ ദിശയിലും', '', NULL),
(406, 'antigen', 'ആന്റിജന്‍', 'ഏതെങ്കിലുമൊരു കശേരുകിയുടെ ശരീരത്തില്‍ പ്രവേശിച്ചാല്‍ ആന്റിബോഡി നിര്‍മാണത്തിന്‌ പ്രരിപ്പിക്കാന്‍ കഴിവുള്ള പദാര്‍ഥം. ആന്റിജനുകള്‍ വിഷങ്ങളോ (ഉദാ: പാമ്പിന്റെയോ ഡിഫ്‌ത്തീരിയയ്‌ക്ക്‌ കാരണമായ ബാക്‌ടീരിയയുടെയോ) കോശസ്‌തരത്തിന്മേലുള്ള തന്മാത്രകളോ (ഉദാ: രക്തഗ്രൂപ്പ്‌ ആന്റിജനുകള്‍) ആകാം. സാധാരണയായി ഇവ പ്രാട്ടീനുകളോ, വലിയ കാര്‍ബോഹൈഡ്രറ്റ്‌ തന്മാത്രകളോ ആയിരിക്കും.', '', NULL),
(407, 'antiknock', 'ആന്റിനോക്ക്‌', '-', 'knocking', NULL),
(408, 'antilogarithm', 'ആന്റിലോഗരിതം', '-', 'logarithm', NULL),
(409, 'antimatter', 'പ്രതിദ്രവ്യം', 'പ്രതികണങ്ങളും അവ ചേര്‍ന്നുണ്ടാകുന്ന പദാര്‍ഥവും.', '', NULL),
(410, 'antinode', 'ആന്റിനോഡ്‌', 'നിശ്ചല തരംഗത്തില്‍ പരമാവധി ആയതിയില്‍ കമ്പനം ചെയ്യുന്ന ബിന്ദു. ആയതി പൂജ്യമായ ബിന്ദുക്കളാണ്‌ നോഡുകള്‍.', '', NULL),
(411, 'antioxidant', 'പ്രതിഓക്‌സീകാരകം', 'ഓക്‌സീകരണം തടയാനായി ഒരു വസ്‌തുവിനോടൊപ്പം ചേര്‍ക്കുന്ന പദാര്‍ഥം. പ്രതിഓക്‌സീകാരികള്‍ മനുഷ്യ ശരീരത്തിന്‌ അത്യാവശ്യമാണ്‌. പ്രകൃതിദത്തമായ പച്ചക്കറികള്‍, പഴങ്ങള്‍ ഇവയില്‍ നിന്ന്‌ മനുഷ്യശരീരത്തിന്‌ ലഭിക്കുന്ന പ്രതിഓക്‌സീകാരികളായ തന്മാത്രകള്‍ ആരോഗ്യപരിപാലനത്തില്‍ വലിയ പങ്കു വഹിക്കുന്നുണ്ട്‌.', '', NULL),
(412, 'antiparticle', 'പ്രതികണം', 'ഓരോ മൌലിക കണത്തിനും അതിനോട്‌ ബന്ധപ്പെട്ട്‌ തുല്യ ദ്രവ്യമാനം, സ്‌പിന്‍, ശരാശരി ആയുസ്സ്‌, കാന്തിക ആഘൂര്‍ണം എന്നിവയും തുല്യവും വിപരീതവുമായ വിദ്യുത്‌ ചാര്‍ജും ആന്തരികപാരിറ്റിയും ഉള്ള ഒരു കണം ഉണ്ടായിരിക്കും. ഇതാണ്‌ ആ കണത്തിന്റെ പ്രതികണം. കണവും പ്രതികണവും ചേര്‍ന്നാല്‍ ഉന്മൂലനം നടക്കുന്നു.', '', NULL),
(413, 'antipodes', 'ആന്റിപോഡുകള്‍', 'ഭൂമിയുടെ ഒരു വശത്തും അതിന്‌ നേരെ എതിര്‍വശത്തും ഉള്ള രണ്ടു ബിന്ദുക്കള്‍.', '', NULL),
(414, 'antiporter', 'ആന്റിപോര്‍ട്ടര്‍', 'രണ്ട്‌ വ്യത്യസ്‌ത വസ്‌തുക്കള്‍ കോശസ്‌തരത്തിലൂടെ എതിര്‍ദിശകളില്‍ ഒരേ സമയത്തോ അല്ലാതെയോ കടത്തുന്ന ചാനല്‍ പ്രാട്ടീന്‍.', '', NULL),
(415, 'antipyretic', 'ആന്റിപൈററ്റിക്‌', 'പനിശമനി, പനി കുറയ്‌ക്കുന്ന മരുന്ന്‌.', '', NULL),
(416, 'antisense DNA', 'ആന്റിസെന്‍സ്‌ ഡി എന്‍ എ', 'RNAയിലേക്ക്‌ പകര്‍ത്തിയ DNA ഭാഗം.', '', NULL),
(417, 'antisense RNA', 'ആന്റിസെന്‍സ്‌ ആര്‍ എന്‍ എ', 'പ്രവര്‍ത്തന ക്ഷമമായ RNAക്ക്‌ പൂര്‍ണ്ണമായോ ഭാഗികമായോ പൂരകമായ RNA.', '', NULL),
(418, 'antiseptic', 'രോഗാണുനാശിനി', 'രോഗങ്ങള്‍ക്ക്‌ ഹേതുവായ രോഗാണുക്കളുടെ വളര്‍ച്ചയെ തടയുന്ന സംയുക്തങ്ങള്‍.', '', NULL),
(419, 'antiserum', 'പ്രതിസീറം', 'പ്രത്യേക പ്രതിവസ്‌തുക്കളടങ്ങിയ രക്തസീറം.', '', NULL),
(420, 'antitoxin', 'ആന്റിടോക്‌സിന്‍', '-', 'toxin', NULL),
(421, 'antitrades', 'പ്രതിവാണിജ്യവാതങ്ങള്‍', 'വാണിജ്യ വാതങ്ങളുടെ മുകളിലൂടെ അവയ്‌ക്കെതിര്‍ദിശയില്‍ പൂര്‍വ്വാഭിമുഖമായി വീശുന്ന കാറ്റുകള്‍.', '', NULL),
(422, 'antivenum', 'പ്രതിവിഷം', 'പാമ്പുവിഷവും ചിലയിനം ചിലന്തിവിഷവും നിര്‍വീര്യമാക്കുന്ന സിറം.', '', NULL),
(423, 'antler', 'മാന്‍ കൊമ്പ്‌', 'മാനുകളുടെ തലയോടില്‍ നിന്ന്‌ പൊങ്ങിനില്‍ക്കുന്ന കൊമ്പുപോലെയുള്ള വളര്‍ച്ച. കൊമ്പില്‍ നിന്ന്‌ വ്യത്യസ്‌തമായി പലപ്പോഴും ഇവയ്‌ക്ക്‌ ശിഖരങ്ങളുണ്ടായിരിക്കും. കൊല്ലത്തിലൊരിക്കല്‍ കൊഴിഞ്ഞുപോകും. റെയിന്‍ഡിയറിലൊഴികെ മറ്റെല്ലാ മാനുകളിലും ആണിനു മാത്രമേ കൊമ്പുണ്ടായിരിക്കുകയുള്ളൂ.', '', NULL),
(424, 'anura', 'അന്യൂറ', 'ക്ലാസ്‌ ആംഫീബിയയിലെ വാലില്ലാത്ത ഉഭയ വാസികള്‍ ഉള്‍പ്പെടുന്ന ഓര്‍ഡര്‍.', '', NULL),
(425, 'anus', 'ഗുദം', 'അന്നപഥത്തില്‍ നിന്ന്‌ ദഹിക്കാത്ത ഭക്ഷണ പദാര്‍ഥങ്ങളും വിസര്‍ജ്യവസ്‌തുക്കളുമെല്ലാം പുറത്തേക്കു കളയുവാനുള്ള ദ്വാരം.', '', NULL),
(426, 'anvil', 'അടകല്ല്‌', '-', '', NULL),
(427, 'anvil cloud', 'ആന്‍വില്‍ മേഘം', 'മുകള്‍ഭാഗം പരന്ന്‌ ആന്‍വിലിന്റെ (അടകല്ല്‌) ആകൃതിയില്‍ കാണപ്പെടുന്ന ഇടിമേഘം, അഥവാ കുമുലോ നിംബസ്‌ മേഘം.', '', NULL),
(428, 'aorta', 'മഹാധമനി', 'ഹൃദയത്തിന്റെ ഇടതു വെന്‍ട്രിക്കിളില്‍ നിന്ന്‌ പുറപ്പെടുന്ന വലിയ ധമനി.', '', NULL),
(429, 'apastron', 'താരോച്ചം', 'അന്യോന്യം പരിക്രമണം ചെയ്യുന്ന ഇരട്ട നക്ഷത്രങ്ങള്‍/നക്ഷത്ര-ഗ്രഹയുഗ്മം ഏറ്റവും അകലെ ആയിരിക്കുന്ന സ്ഥാനം. cf periastron.', '', NULL),
(430, 'apatite', 'അപ്പറ്റൈറ്റ്‌', 'കാല്‍സ്യത്തിന്റെ ഫോസ്‌ഫേറ്റ്‌. ചിലപ്പോള്‍ ഫ്‌ളൂറൈഡ്‌, ക്ലോറൈഡ്‌, ഹൈഡ്രാക്‌സിന്‍, കാര്‍ബണേറ്റ്‌ എന്നിവയും അടങ്ങിയിരിക്കും. അവസാദ ഫോസ്‌ഫേറ്റ്‌ ശിലകളുടെ മുഖ്യ ഘടകം.', '', NULL),
(431, 'aperture', 'അപെര്‍ച്ചര്‍', '1. ഒരു പ്രകാശിക ഉപകരണത്തില്‍ (ഉദാ: ക്യാമറ) പ്രകാശരശ്‌മി കടന്നുപോകാന്‍ അനുവദിക്കുന്ന ദ്വാരം. 2. ലെന്‍സിന്റെയോ ദര്‍പ്പണത്തിന്റെയോ സഫല വ്യാസവും ഫോക്കസ്‌ ദൂരവും തമ്മിലുള്ള അനുപാതം. 3. ലെന്‍സിന്റെയോ ദര്‍പ്പണത്തിന്റെയോ വ്യാസം.', '', NULL),
(432, 'apex', 'ശിഖാഗ്രം', 'ഏതെങ്കിലും രേഖയെയോ തലത്തെയോ അടിസ്ഥാനമാക്കി ഏറ്റവും ദൂരെയുള്ള ബിന്ദു. ഉദാ: ത്രികോണത്തില്‍ ആധാരമാക്കി എടുക്കുന്ന വശത്തിന്‌ എതിരെയുള്ള ശീര്‍ഷം.', '', NULL),
(433, 'aphelion', 'സരോച്ചം', 'സൂര്യനെ ദീര്‍ഘവൃത്തത്തില്‍ ചുറ്റുന്ന ഒരു ഗ്രഹം സൂര്യന്‌ ഏറ്റവും അകലെ ആയിരിക്കുന്ന സ്ഥാനം. cf perihelion.', '', NULL),
(434, 'apical dominance', 'ശിഖാഗ്ര പ്രാമുഖ്യം', 'സസ്യങ്ങളില്‍ അഗ്രമുകുളം പാര്‍ശ്വമുകുളങ്ങളുടെ വളര്‍ച്ചയെ തടസ്സപ്പെടുത്തുന്ന അവസ്ഥ. അഗ്രഭാഗത്ത്‌ ഓക്‌സിനുകള്‍ എന്ന്‌ പറയുന്ന സസ്യഹോര്‍മോണുകള്‍ കൂടുതലുണ്ടാവുന്നതാണ്‌ ഇതിന്‌ കാരണം.', '', NULL),
(435, 'apical meristem', 'അഗ്രമെരിസ്റ്റം', 'സസ്യങ്ങളിലെ വേരിന്റെയും കാണ്ഡത്തിന്റെയും അഗ്രഭാഗത്തായി തീവ്രമായി കോശവിഭജനം നടക്കുന്ന ഭാഗം.', '', NULL),
(436, 'Apiculture', 'തേനീച്ചവളര്‍ത്തല്‍', 'കൂടുകളില്‍ ശാസ്‌ത്രീയമായി തേനീച്ചകളെ വളര്‍ത്തല്‍.', '', NULL),
(437, 'APL', 'എപിഎല്‍', 'ഒരു കംപ്യൂട്ടര്‍ പ്രാഗ്രാമിങ്ങ്‌ ഭാഷ. A Programming Language എന്നതിന്റെ ചുരുക്കം.', '', NULL),
(438, 'aplanospore', 'എപ്ലനോസ്‌പോര്‍', 'അലൈംഗിക പ്രത്യുത്‌പാദന വേളയില്‍ ചില ആല്‍ഗകളിലും ഫംഗസുകളിലും രൂപപ്പെടുന്ന ചലനശേഷിയില്ലാത്ത സ്‌പോറുകള്‍.', '', NULL),
(439, 'apocarpous', 'വിയുക്താണ്ഡപം', 'അണ്ഡപര്‍ണങ്ങള്‍ വേറിട്ടു നില്‍ക്കുന്ന അവസ്ഥ.', '', NULL),
(440, 'Apoda', 'അപോഡ', 'ക്ലാസ്‌ ആംഫീബിയയുടെ ഒരു വിഭാഗം. കാലുകളില്ലാത്ത ഉഭയവാസികള്‍ ഇതില്‍പെടുന്നു. മണ്ണില്‍ തുരന്നാണ്‌ ഇവ ജീവിക്കുന്നത്‌. ഉദാ: ഇക്തിയോഫിസ്‌.', '', NULL),
(441, 'Apoenzyme', 'ആപോ എന്‍സൈം', 'എന്‍സൈമിന്റെ പ്രാട്ടീന്‍ മോയിറ്റി. ഇതാണ്‌ എന്‍സൈമിന്റെ വിശേഷ സ്വഭാവത്തിന്‌ അടിസ്ഥാനം.', '', NULL),
(442, 'apogamy', 'അപബീജയുഗ്മനം', 'ബീജസങ്കലനമില്ലാതെ ഗാമറ്റോഫൈറ്റ്‌ കലയില്‍ നിന്ന്‌ നേരിട്ടുള്ള ഭ്രൂണ രൂപീകരണം.', '', NULL),
(443, 'apogee', 'ഭൂ ഉച്ചം', '1. ഭൂകേന്ദ്ര പരിക്രമണപഥത്തില്‍, (ഉദാ: ഉപഗ്രഹപഥത്തില്‍) ഭൂ കേന്ദ്രത്തില്‍ നിന്ന്‌ ഏറ്റവും അകലെയുള്ള സ്ഥാനം. 2. ചന്ദ്രന്റെ പരിക്രമണ പഥത്തില്‍ അത്‌ ഭൂമിയില്‍ നിന്ന്‌ ഏറ്റവും അകലെ എത്തുന്ന സ്ഥാനം. apsides നോക്കുക.', '', NULL),
(444, 'apomixis', 'അസംഗജനം', 'ബീജസങ്കലനമോ, ഊനഭംഗമോ കൂടാതെ നടക്കുന്ന പ്രത്യുത്‌പാദനം. അയോഗജനി, അരേണുജനി, അസംഗജനി തുടങ്ങിയവ ഇതില്‍പ്പെടുന്നു.', '', NULL),
(445, 'apophylite', 'അപോഫൈലൈറ്റ്‌', 'ബാസാള്‍ട്ട്‌, മറ്റ്‌ ആഗ്നേയശിലകള്‍ എന്നിവയില്‍ കലര്‍ന്ന്‌ കാണപ്പെടുന്ന സിയോലൈറ്റും അമിഗ്‌ഡൈലും ചേര്‍ന്ന ഒരു ദ്വിതീയ ധാതു. പൊട്ടാസിയത്തിന്റെയും കാല്‍സ്യത്തിന്റെയും ഹൈഡ്രറ്റഡ്‌ ഫ്‌ളൂറോസിലിക്കേറ്റാണ്‌ ഇത്‌.', '', NULL),
(446, 'apophysis', 'അപോഫൈസിസ്‌', 'കശേരുകികളുടെ അസ്ഥികളില്‍ പേശികള്‍ ബന്ധിച്ചിരിക്കുന്ന ഭാഗം.', '', NULL),
(447, 'Apoplast', 'അപോപ്ലാസ്റ്റ്‌', 'സസ്യങ്ങളില്‍ എല്ലാ കോശഭിത്തികളും അവയിലെ ജലവും അടങ്ങിയ അന്യോന്യം ബന്ധപ്പെട്ട വ്യൂഹം. ജലത്തിന്റെയും അതില്‍ ലയിച്ച ലായകങ്ങളുടെയും നീക്കം ആപോപ്ലാസ്റ്റിക്‌ മാര്‍ഗം എന്നാണറിയപ്പെടുന്നത്‌. (കോശഭിത്തിയില്‍ സെല്ലുലോസ്‌ നാരുകളും അവയ്‌ക്കിടയ്‌ക്ക്‌ ജലവുമാണുള്ളത്‌)', '', NULL),
(448, 'apospory', 'അരേണുജനി', 'സ്‌പോറോഫൈറ്റിക കലയില്‍ നിന്ന്‌ കായിക വര്‍ധനയിലൂടെ ഗാമിറ്റോഫൈറ്റ്‌ വളര്‍ച്ച പ്രാപിക്കുന്ന പ്രക്രിയ.', '', NULL),
(449, 'apothecium', 'വിവൃതചഷകം', 'ചില ഫംഗസുകളിലും ലൈക്കനുകളിലും കാണുന്ന കപ്പിന്റെയോ സോസറിന്റെയോ ആകൃതിയിലുള്ള, സ്‌പോറു ണ്ടാകുന്ന അവയവം.', '', NULL),
(450, 'appalachean orogeny', 'അപ്പലേച്യന്‍ പര്‍വതനം', 'ഡിവോണിയന്‍ കാലം മുതല്‍ പെര്‍മിയന്‍ വരെ വടക്കു കിഴക്കന്‍ അമേരിക്കയിലുണ്ടായ വലന കാലഘട്ടം.', '', NULL),
(451, 'apparent expansion', 'പ്രത്യക്ഷ വികാസം', '-', 'expansion of liquids', NULL),
(452, 'apparent magnitude', 'പ്രത്യക്ഷ കാന്തിമാനം', '-', 'magnitude', NULL),
(453, 'appendage', 'ഉപാംഗം', 'ഒരു ജീവിയുടെ ശരീരത്തില്‍ നിന്ന്‌ പുറത്തേക്ക്‌ തള്ളി നില്‍ക്കുന്ന ഭാഗം. കൈകാലുകളായോ തുഴകളായോ ഇവ പലവിധത്തില്‍ രൂപപ്പെട്ടിരിക്കും.', '', NULL),
(454, 'Appleton layer', 'ആപ്പിള്‍ടണ്‍ സ്‌തരം', 'അയണോസ്‌ഫിയറി ലെ F2 സ്‌തരത്തിന്റെ മറ്റൊരു പേര്‌.', '', NULL),
(455, 'apposition', 'സ്‌തരാധാനം', 'കോശഭിത്തി സ്ഥൂലീകരിക്കുന്നതിനു വേണ്ടി സെല്ലുലോസ്‌, ലിഗ്നിന്‍ തുടങ്ങിയ പദാര്‍ഥങ്ങള്‍ പാളികളായി നിക്ഷിപ്‌തമാകുന്ന പ്രക്രിയ.', '', NULL),
(456, 'approximation', 'ഏകദേശനം', 'പൂര്‍ണമായും കൃത്യമല്ലാത്തതും, സന്ദര്‍ഭത്തിന്‌ യോജിച്ച കൃത്യതയുള്ളതുമായ ഫലം. 3.14, 3.141, 22/7 എന്നിവയെല്ലാം πയുടെ മൂല്യത്തിന്റെ ഏകദേശനങ്ങളാണ്‌. സൂര്യനില്‍ നിന്നു വരുന്ന പ്രകാശ രശ്‌മികളെ സമാന്തര രശ്‌മികളായി കരുതുന്നതും ഭൂമിക്ക്‌ ഗോളാകൃതിയാണെന്നു കരുതുന്നതും ഏകദേശനമാണ്‌.', '', NULL),
(457, 'aprotic', 'എപ്രാട്ടിക്‌', 'പ്രാട്ടോണ്‍ കൈമാറ്റം അനുവദിക്കാത്ത ലായകത്തെ കുറിക്കുന്നത്‌. ഉദാ: അസറ്റോണ്‍, ബെന്‍സീന്‍.', '', NULL),
(458, 'aprotic solvent', 'അപ്രാട്ടിക ലായകം', 'പ്രാട്ടോണ്‍ ത്യജിക്കുകയോ, സ്വീകരിക്കുകയോ ചെയ്യാത്ത ലായകം.', '', NULL),
(459, 'apsides', 'ഉച്ച-സമീപകങ്ങള്‍', 'ഭാരകേന്ദ്രത്തിനു ചുറ്റും കറങ്ങുന്ന രണ്ട്‌ വാനവസ്‌തുക്കള്‍ (ഉദാ: ഇരട്ട നക്ഷത്രങ്ങള്‍; ഒരു നക്ഷത്രവും അതിന്റെ ഗ്രഹവും) പരസ്‌പരം ഏറ്റവും അടുത്തും ( periapsis) ഏറ്റവും അകലെയും ( apo apsis) വരുന്ന സ്ഥാനങ്ങള്‍. ഇവ തമ്മില്‍ യോജിപ്പിച്ച്‌ വരയ്‌ക്കുന്ന നേര്‍രേഖയാണ്‌ ആപ്‌സൈഡ്‌സ്‌ രേഖ ( line of apsides). പഥം ദീര്‍ഘവൃത്തമാണെങ്കില്‍ ഇത്‌ മുഖ്യാക്ഷം ആയിരിക്കും.', '', NULL),
(460, 'aqua fortis', 'അക്വാ ഫോര്‍ട്ടിസ്‌', 'സാന്ദ്ര നൈട്രിക്‌ അമ്ലം (HNO3)', '', NULL),
(461, 'aqua ion', 'അക്വാ അയോണ്‍', 'ദായകബന്ധനം വഴി ജലതന്മാത്രകള്‍ കാറ്റയോണുമായി ബന്ധിക്കപ്പെട്ട സങ്കീര്‍ണ്ണ അയോണ്‍.', '', NULL),
(462, 'aquaporins', 'അക്വാപോറിനുകള്‍', 'ചുവന്ന രക്താണുക്കളിലും പ്രാക്‌സിമല്‍ കണ്‍വല്യുട്ടഡ്‌ ട്യൂബുകളിലും കോശങ്ങളിലും പ്ലാസ്‌മാ സ്‌തരത്തിന്റെ ഭാഗമായ ഒരുതരം പ്രാട്ടീന്‍. ജലത്തിന്റെ പാരഗമ്യത വളരെയധികം കൂട്ടുന്നു.', '', NULL),
(463, 'aqua regia', 'രാജദ്രാവകം', 'ഗാഢനൈട്രിക്‌ ആസിഡും ഗാഢഹൈഡ്രാക്ലോറിക്‌ ആസിഡും 1:3 എന്ന അനുപാതത്തില്‍ കലര്‍ത്തിയ മിശ്രിതം. ഉല്‍കൃഷ്‌ട ലോഹങ്ങളായ സ്വര്‍ണം, പ്ലാറ്റിനം മുതലായവയെ ലയിപ്പിക്കാന്‍ ഉപയോഗിക്കുന്നു.', '', NULL),
(464, 'Aquarius', 'കുംഭം', 'ഒരു സൗരരാശി. ഈ രാശിയിലുള്ള നക്ഷത്രങ്ങളെ ചേര്‍ത്താല്‍ ഒരാള്‍ കുടത്തില്‍ നിന്ന്‌ വെള്ളമൊഴിക്കുന്ന രൂപം കിട്ടും എന്നാണ്‌ സങ്കല്‍പ്പം. സൂര്യന്‍ ഈ രാശിയിലായിരിക്കുമ്പോഴാണ്‌ കുംഭമാസക്കാലം.', '', NULL),
(465, 'aqueous', 'അക്വസ്‌', 'ജലലായനി സംബന്ധിച്ചത്‌.', '', NULL),
(466, 'aqueous chamber', 'ജലീയ അറ', 'കശേരുകികളുടെ കണ്ണില്‍ ലെന്‍സിനും കോര്‍ണിയയ്‌ക്കും ഇടയ്‌ക്കുള്ള ഭാഗം. ഇതിനകത്താണ്‌ അക്വസ്‌ ഹ്യൂമര്‍.', '', NULL),
(467, 'aqueous humour', 'അക്വസ്‌ ഹ്യൂമര്‍', 'കശേരുകികളുടെ കണ്ണില്‍, കോര്‍ണിയയുടെയും ലെന്‍സിന്റെയും ഇടയിലുള്ള ദ്രാവകം.', '', NULL),
(468, 'aquifer', 'അക്വിഫെര്‍', 'ധാരാളം സുഷിരങ്ങളടങ്ങിയതും ഭൂജലത്തെ കടത്തിവിടാന്‍ കഴിയുന്നതുമായ ശിലാപാളികള്‍.', '', NULL),
(469, 'arboreal', 'വൃക്ഷവാസി', 'വൃക്ഷങ്ങളില്‍ വസിക്കുന്ന ശീലമുള്ളവ.', '', NULL),
(470, 'arboretum', 'വൃക്ഷത്തോപ്പ്‌', 'ഗവേഷണത്തിനും പഠനത്തിനുമുള്ള വൃക്ഷത്തോപ്പ്‌.', '', NULL),
(471, 'arc', 'ചാപം', 'വൃത്തപരിധിയുടേയോ മറ്റേതെങ്കിലും വക്രത്തിന്റെയോ ഒരു ഭാഗം.', '', NULL),
(472, 'arc of the meridian', 'രേഖാംശീയ ചാപം', 'ഭൂമിയുടെ ആകൃതി, വലുപ്പം എന്നിവ കൃത്യമായി കണ്ടുപിടിക്കുന്നതിന്‌ രേഖാംശങ്ങളിലൂടെയുള്ള അളവ്‌', '', NULL),
(473, 'archaeozoic', 'ആര്‍ക്കിയോസോയിക്‌', 'പ്രികാംബ്രിയനിലെ മൂന്നു കാലഘട്ടങ്ങളില്‍ രണ്ടാമത്തേത്‌. ജീവന്‍ ഉല്‍ഭവിച്ചതും ജീവികള്‍ ആദ്യമായി പരിണാമ വികാസം പ്രാപിച്ചതും ഇക്കാലത്താണ്‌ എന്ന്‌ കരുതപ്പെടുന്നു. ഈ പദം ഭൂവിജ്ഞാനീയ കാലഘട്ട വര്‍ഗീകരണ പദ്ധതിയില്‍പ്പെട്ട ഒന്നല്ല.', '', NULL),
(474, 'Archean', 'ആര്‍ക്കിയന്‍', 'ഏറ്റവും പ്രാചീനമായ മഹാകല്‌പം. ഭൂമിയുടെ ഉല്‍പ്പത്തി മുതല്‍ 250 കോടി വര്‍ഷങ്ങള്‍ക്കു മുമ്പുവരെയുള്ള കാലഘട്ടം. ഇക്കാലത്ത്‌ ഭൂമിയില്‍ ജീവനുണ്ടായിരുന്നില്ല.', '', NULL),
(475, 'archegonium', 'അണ്ഡപുടകം', 'ബ്രയോഫൈറ്റ, ടെറിഡോഫൈറ്റ എന്നിവയിലെ സ്‌ത്രീ ലൈംഗിക അവയവം. ചിത്രം bryophyta നോക്കുക.', '', NULL),
(476, 'archenteron', 'ഭ്രൂണാന്ത്രം', 'ചില ജന്തുക്കളുടെ ഭ്രൂണ വളര്‍ച്ചയില്‍ ഗാസ്‌ട്രുല എന്ന ഘട്ടത്തില്‍ ഭ്രൂണത്തിനുള്ളില്‍ കാണപ്പെടുന്ന പൊള്ളയായ ഭാഗം. പ്രഢൗജീവിയുടെ അന്നപഥമായി ഇത്‌ മാറും.', '', NULL),
(477, 'archesporium', 'രേണുജനി', 'സ്‌പോറുകള്‍ക്ക്‌ ജന്മം നല്‍കുന്ന കോശനിര.', '', NULL),
(478, 'archipelago', 'ആര്‍ക്കിപെലാഗോ', 'സമുദ്രത്തില്‍ കാണപ്പെടുന്ന ദ്വീപുകളുടെ കൂട്ടം. ദ്വീപ്‌ സമൂഹം.', '', NULL),
(479, 'arctic', 'ആര്‍ട്ടിക്‌', '66 0 32 1 ഉത്തര അക്ഷാംശത്തിനും ഉത്തരധ്രുവത്തിനുമിടയിലുള്ള പ്രദേശം.', '', NULL),
(480, 'arctic circle', 'ആര്‍ട്ടിക്‌ വൃത്തം', 'ഉത്തര അക്ഷാംശം 66 0 32 1 നെ സൂചിപ്പിക്കുന്ന വൃത്തം.', '', NULL),
(481, 'are', 'ആര്‍', 'വിസ്‌തീര്‍ണത്തിന്റെ ഒരു ഏകകം. മെട്രിക്‌ സമ്പ്രദായത്തിലുള്ളത്‌. 100 m2 ന്‌ തുല്യം. 100 ആര്‍ ആണ്‌ ഒരു ഹെക്‌ടര്‍.', '', NULL),
(482, 'Arecibo observatory', 'അരേസീബോ ഒബ്‌സര്‍വേറ്ററി', 'ലോകത്തിലെ ഏറ്റവും വലിയ സിംഗിള്‍ ഡിഷ്‌ റേഡിയോ ടെലിസ്‌കോപ്പ്‌. തെക്കേ അമേരിക്കന്‍ രാജ്യമായ പ്യൂര്‍ട്ടോറെക്കോയിലെ അരേസിബോയില്‍ സ്ഥിതി ചെയ്യുന്നു. ടെലിസ്‌കോപ്പിന്റെ 350 മീറ്റര്‍ വ്യാസമുള്ള ഡിഷ്‌ ഒരു പഴയ അഗ്നിപര്‍വത ഗര്‍ത്തത്തിലാണ്‌ സ്ഥാപിച്ചിരിക്കുന്നത്‌. അന്യഗ്രഹജീവികള്‍ക്കു വേണ്ടിയുള്ള അന്വേഷണ പദ്ധതിയായ സെറ്റി ( Seti) യുടെ ഭാഗമായും ഈ റേഡിയോ ദൂരദര്‍ശിനി ഉപയോഗിക്കപ്പെടുന്നുണ്ട്‌.', '', NULL),
(483, 'arenaceous rock', 'മണല്‍പ്പാറ', 'മണല്‍നിര്‍മ്മിത പാറ', '', NULL),
(484, 'areolar tissue', 'എരിയോളാര്‍ കല', 'നാക്കും അടിയിലുള്ള ഭാഗങ്ങളും തമ്മില്‍ യോജിപ്പിക്കുന്ന ഫൈബറുകളടങ്ങിയ, അയഞ്ഞ ജാലിക രൂപത്തിലുള്ള സംയോജകകല.', '', NULL),
(485, 'argand diagram', 'ആര്‍ഗന്‍ ആരേഖം', 'സമ്മിശ്ര സംഖ്യകളെ ജ്യാമിതീയമായി പ്രതിനിധാനം ചെയ്യാനുള്ള ഒരു ദ്വിമാന-കാര്‍ട്ടീഷ്യന്‍ നിര്‍ദേശാങ്ക വ്യവസ്ഥ. x+iy എന്ന സമ്മിശ്ര സംഖ്യയെ x, y എന്നിവ നിര്‍ദേശാങ്കങ്ങളായുള്ള ബിന്ദുകൊണ്ട്‌ പ്രതിനിധാനം ചെയ്യുന്നു. വാസ്‌തവിക ഭാഗത്തെ x നിര്‍ദേശാങ്കമായും അധികല്‌പിതഭാഗത്തെ y നിര്‍ദേശാങ്കമായും അടയാളപ്പെടുത്തുന്ന ബിന്ദു സമ്മിശ്ര സംഖ്യയെ കുറിക്കുന്നു.', '', NULL),
(486, 'arid zone', 'ഊഷരമേഖല', 'സസ്യജാലങ്ങള്‍ക്ക്‌ വളരാന്‍ കഴിയാത്ത, ഈര്‍പ്പമില്ലാത്ത അവസ്ഥ.', '', NULL),
(487, 'Aries', 'മേടം', 'ഒരു സൌരരാശി. ഈ രാശിയിലെ നക്ഷത്രങ്ങള്‍ ചേര്‍ത്താല്‍ ഒരു ചെമ്മരിയാടിന്റെ രൂപം സങ്കല്‍പ്പിക്കാം. സൂര്യന്‍ ഈ രാശിയിലായിരിക്കുമ്പോഴാണ്‌ മേടമാസക്കാലം.', '', NULL),
(488, 'aril', 'പത്രി', 'ചിലയിനം വിത്തുകളില്‍ ബാഹ്യാവരണത്തിനു പുറത്തായി ഭാഗികമായോ പൂര്‍ണ്ണമായോ കാണുന്ന മറ്റൊരാവരണം. ഉദാ: ജാതിപത്രി', '', NULL),
(489, 'arithmetic and logic unit', 'ഗണിത-യുക്തിപര ഘടകം', 'കംപ്യൂട്ടറിന്റെ കേന്ദ്രത്തിലെ സുപ്രധാനമായ ഒരു ഭാഗം. ഗണിതക്രിയകളും യുക്തിക്രിയകളും ചെയ്യുന്നത്‌ ഈ ഭാഗത്താണ്‌. ALU എന്ന്‌ ചുരുക്കം.', '', NULL),
(490, 'arithmetic progression', 'സമാന്തര ശ്രണി', 'ഒരു ശ്രണിയിലെ ഒരു പദത്തില്‍ നിന്ന്‌ അതിന്റെ മുന്‍പദം കുറച്ചു കിട്ടുന്നത്‌ ഒരു സ്ഥിര സംഖ്യയായാല്‍ അതിനെ സമാന്തര ശ്രണിയെന്നു പറയാം. ഈ സ്ഥിരസംഖ്യയെ പൊതു വ്യത്യാസം എന്നു പറയുന്നു. ഉദാ: 1, 3, 5, 7.... ഇതില്‍ പൊതു അന്തരം 2 ആണ്‌.', '', NULL),
(491, 'aromatic', 'അരോമാറ്റിക്‌', 'ഗുണത്തില്‍ ബെന്‍സീനോട്‌ സാദൃശ്യമുള്ളതോ ഘടനയില്‍ ബെന്‍സീന്‍ സദൃശവലയമുള്ളതോ ആയ കാര്‍ബണിക സംയുക്തങ്ങള്‍ക്കുള്ള പൊതുവായ നാമം. ഇവയില്‍ ചിലതിനെല്ലാം സുഗന്ധം ഉണ്ട്‌.', '', NULL),
(492, 'aromatic compounds', 'അരോമാറ്റിക സംയുക്തങ്ങള്‍', 'ബെന്‍സീന്‍ വളയം അടങ്ങിയ സംയുക്തങ്ങള്‍ അഥവാ ബെന്‍സീനു സമാനമായ ഗുണധര്‍മങ്ങളുള്ള സംയുക്തങ്ങള്‍. ടൊളുവിന്‍, അനിലിന്‍, ടി എന്‍ ടി, ഫിനോള്‍ എന്നിവ വ്യാവസായിക പ്രാധാന്യമുള്ള അരോമാറ്റിക്‌ സംയുക്തങ്ങളാണ്‌.', '', NULL),
(493, 'aromatic hydrocarbons', 'ആരോമാറ്റിക് ഹൈഡ്രോകാര്‍ബണ്‍സ്', 'അരോമാറ്റിക സംയുക്തങ്ങള്‍ക്ക്‌ പ്രത്യേകമായുള്ള ഗുണധര്‍മ്മങ്ങള്‍ ഉണ്ട്‌. (4 n+2)π ഇലക്‌ട്രാണുകള്‍ ഉള്ള സംവൃത കാര്‍ബണിക സംയുക്തങ്ങള്‍ അരോമാറ്റികമായിരിക്കുമെന്ന്‌ ഹുക്കല്‍ നിയമം പറയുന്നു ( n=1, 2, 3,.... etc.)', '', NULL),
(494, 'aromaticity', 'അരോമാറ്റിസം', 'അരോമാറ്റിക സംയുക്തങ്ങള്‍ക്ക്‌ പ്രത്യേകമായുള്ള ഗുണധര്‍മ്മങ്ങള്‍. (4 n+2)π ഇലക്‌ട്രാണുകള്‍ ഉണ്ടായിരിക്കും. ഒന്നിടവിട്ട്‌ πരാസ ബന്ധങ്ങളുള്ള പരന്ന ചാക്രിക ഘടന ഉണ്ടായിരിക്കും. ഇതില്‍ n=1, 2, 3 ഇങ്ങനെയുള്ള സംഖ്യകളായിരിക്കും.', '', NULL),
(495, 'armature', 'ആര്‍മേച്ചര്‍', '1. മോട്ടോര്‍, ഡൈനാമോ തുടങ്ങിയ വൈദ്യുതയന്ത്രങ്ങളിലെ പ്രധാന വിദ്യുത്‌ധാര വഹിക്കുന്ന കമ്പിച്ചുരുള്‍ ഉള്‍പ്പെടുന്ന ഭാഗം. 2. റിലേ, ഇലക്‌ട്രിക്‌ ബെല്‍ തുടങ്ങിയ വൈദ്യുത യാന്ത്രിക ഉപകരണങ്ങളിലെ ചലിക്കുന്ന ഭാഗം.', '', NULL),
(496, 'array', 'അണി', 'ഒരു പട്ടികയില്‍, സംഖ്യകളെയോ വിവരങ്ങളെയോ ക്രമപ്പെടുത്തി എഴുതുന്ന രീതി. Arrhenius equation അരീനിയസ്‌ സമവാക്യം. ഒരു രാസ അഭിക്രിയയുടെ വിശിഷ്‌ട സ്ഥിരാംഗവും താപനിലയും തമ്മിലുള്ള ബന്ധം സൂചിപ്പിക്കുന്ന സമവാക്യം. k = Ae −E/RT k- വിശിഷ്‌ട സ്ഥിരാംഗം, T-കെല്‍വിന്‍ താപനില, R-വാതക സ്ഥിരാംഗം, A-ഒരു സ്ഥിരാംഗം, E-ഉത്തേജിത ഊര്‍ജം', '', NULL),
(497, 'arrester', 'രോധി', 'ഉദാ: lightning arrester.', '', NULL),
(498, 'arrow diagram', 'ആരോഡയഗ്രം', 'അമ്പടയാളമുപയോഗിച്ച്‌ ക്രാസ്‌ പ്രാഡക്‌ട്‌, ബന്ധങ്ങള്‍, ഏകദങ്ങള്‍ എന്നിവ അടയാളപ്പെടുത്തുന്ന രീതി.', '', NULL),
(499, 'arsine', 'ആര്‍സീന്‍', 'AsH3. നിറമില്ലാത്ത വിഷവാതകം. ജലം, ക്ലോറോഫോം, ബെന്‍സീന്‍ എന്നീ ലായകങ്ങളില്‍ ലയിക്കും.', '', NULL),
(500, 'arteriole', 'ധമനിക', 'ധമനികളുടെ സൂക്ഷ്‌മശാഖകള്‍. ചിലവ നേരിട്ടു ചെറിയ സിരകളുമായി ചേരും. ബാക്കിയുള്ളവ കാപ്പില്ലറികളായി വിഭജിക്കും.', '', NULL),
(501, 'artery', 'ധമനി', 'ഹൃദയത്തില്‍ നിന്ന്‌ ശരീരത്തിന്റെ വിവിധ ഭാഗങ്ങളിലേക്ക്‌ ശുദ്ധരക്തം വഹിച്ചുകൊണ്ടുപോകുന്ന രക്തക്കുഴല്‍. ശ്വാസകോശ ധമനിയൊഴിച്ച്‌ മറ്റുള്ള ധമനികളിലെല്ലാം ശുദ്ധരക്തമാണുള്ളത്‌.', '', NULL),
(502, 'artesian basin', 'ആര്‍ട്ടീഷ്യന്‍ തടം', 'ഭൂവല്‍ക്കത്തില്‍ സൂക്ഷ്‌മരന്ധ്ര ശിലകളുടെ അടിത്തട്ട്‌. സൂക്ഷ്‌മ രന്ധ്രങ്ങളില്ലാത്ത ശിലകള്‍ക്കിടയില്‍ അകപ്പെട്ടു കിടക്കുന്ന ഈ പാളികള്‍ ജലവാഹികളായിരിക്കും.', '', NULL),
(503, 'artesian well', 'ആര്‍ട്ടീഷ്യന്‍ കിണര്‍', 'ആര്‍ട്ടീഷ്യന്‍ തടത്തോളം ചെന്നെത്തുന്ന കിണര്‍.', '', NULL),
(504, 'artificial radio activity', 'കൃത്രിമ റേഡിയോ ആക്‌റ്റീവത', 'ഉയര്‍ന്ന ആവൃത്തിയുള്ള വിദ്യുത്‌കാന്തിക തരംഗങ്ങളുപയോഗിച്ചോ, ഇലക്‌ട്രാണ്‍, ന്യൂട്രാണ്‍ മുതലായ കണികകള്‍ കൂടിയ ഊര്‍ജത്തിലുപയോഗിച്ചോ ചില വസ്‌തുക്കളില്‍ കൃത്രിമ റേഡിയോ ആക്‌ടീവത ഉണ്ടാക്കുന്നു.', '', NULL),
(505, 'aryl', 'അരൈല്‍', 'അരോമാറ്റിക റാഡിക്കല്‍. ഉദാ: ഫിനൈല്‍ ( C6H5). അരോമാറ്റിക ഹൈഡ്രാകാര്‍ബണുകളില്‍ നിന്ന്‌ ഒരു ഹൈഡ്രജന്‍ നീക്കിയാല്‍ കിട്ടുന്ന റാഡിക്കല്‍.', '', NULL),
(506, 'Aschelminthes', 'അസ്‌കെല്‍മിന്തസ്‌', 'ഉരുളന്‍ വിരകള്‍, റോട്ടിഫെറുകള്‍ മുതലായ ജീവികള്‍ ഉള്‍പ്പെടുന്ന ഫൈലം.', '', NULL);
INSERT INTO `wp_sde` (`id`, `enword`, `word`, `meaning`, `reftext`, `refid`) VALUES
(507, 'ASLV', 'എ എസ്‌ എല്‍ വി. ', 'ഓഗ്മെന്റഡ്‌ സാറ്റലൈറ്റ്‌ ലോഞ്ച്‌ വെഹിക്കിള്‍ എന്നതിന്റെ ചുരുക്കം . പി എസ്‌ എല്‍ വിയുടെ മുന്നോടിയായി ഇസ്രാ നിര്‍മിച്ച ഉപഗ്രഹ വിക്ഷേപണ വാഹനം. നമ്മുടെ രാജ്യം തദ്ദേശീയമായി വികസിപ്പിച്ച ഇന്ധനമായ ഹൈഡ്രാക്‌സില്‍ ടെര്‍മിനേറ്റഡ്‌ പോളി ബ്യൂട്ടാസീന്‍ ( HTPB) ആദ്യമായി ഉപയോഗിച്ചത്‌ എ എസ്‌ എല്‍ വിയില്‍ ആണ്‌. 23.5 മീറ്റര്‍ ഉയരവും 39 ടണ്‍ ഭാരവുമുള്ള വിക്ഷേപണ വാഹനമായിരുന്നു എ എസ്‌ എല്‍ വി.', '', NULL),
(508, 'ASCII', 'ആസ്‌കി', 'American Standard Code for Information Interchange എന്നതിന്റെ ചുരുക്കം.', '', NULL),
(509, 'ascomycetes', 'ആസ്‌കോമൈസീറ്റ്‌സ്‌', 'ആസ്‌കസ്‌ എന്നു വിളിക്കുന്ന സഞ്ചിയില്‍ സ്‌പോറുകള്‍ ഉത്‌പാദിപ്പിക്കുന്ന ഫംഗസുകള്‍. ഉദാ: യീസ്റ്റ്‌.', '', NULL),
(510, 'ascorbic acid', 'അസ്‌കോര്‍ബിക്‌ അമ്ലം', 'C6H8O6. ജീവകം C\' യുടെ രാസനാമം. പച്ചക്കറികളിലും നാരങ്ങയിലും ജീവകം C ധാരാളമായിട്ടുണ്ട്‌. പല്ല്‌, എല്ല്‌ എന്നിവയുടെ വളര്‍ച്ചയ്‌ക്ക്‌ ഇത്‌ അനിവാര്യമാണ്‌. ഇതിന്‌ രോഗാണു സംക്രമണത്തെ ചെറുക്കാനുള്ള കഴിവുണ്ട്‌. ഇത്‌ പ്രകൃതിദത്തമായ ഒരു പ്രതി ഓക്‌സീകാരിയാണ്‌. മനുഷ്യന്‌ ഇതിന്റെ കുറവു കൊണ്ട്‌ സ്‌കര്‍വി എന്ന രോഗം ഉണ്ടാകുന്നു.', '', NULL),
(511, 'ascospore', 'ആസ്‌കോസ്‌പോര്‍', 'ആസ്‌കോമൈസീറ്റ്‌ ഫംഗസുകളുടെ ആസ്‌കസുകളില്‍ ഉണ്ടാവുന്ന സ്‌പോറുകള്‍.', '', NULL),
(512, 'ascus', 'ആസ്‌കസ്‌', 'ആസ്‌കോമൈസീറ്റ്‌ ഫംഗസുകളില്‍ സ്‌പോറുകള്‍ ഉത്‌പാദിപ്പിക്കുന്നതിനുള്ള സഞ്ചിപോലുള്ള ഭാഗം. സാധാരണയായി ഒരു ആസ്‌കസ്‌ എട്ടു സ്‌പോറുകള്‍ ഉത്‌പാദിപ്പിക്കുന്നു. ASDR-Age Specific Death Rateനോക്കുക.', '', NULL),
(513, 'aseptic', 'അണുരഹിതം', 'അണുജീവികളൊന്നുമില്ലാത്തത്‌.', '', NULL),
(514, 'asexual reproduction', 'അലൈംഗിക പ്രത്യുത്‌പാദനം', 'ഭിന്ന ലൈംഗിക കോശങ്ങളുടെ കോശമര്‍മസംയോജനം ഉള്‍ക്കൊള്ളാതെ നടക്കുന്ന പ്രത്യുത്‌പാദനം.', '', NULL),
(515, 'asphalt', 'ആസ്‌ഫാല്‍റ്റ്‌', 'എണ്ണപ്പാടങ്ങളില്‍ ബാഷ്‌പീകരണ ഫലമായി ഉണ്ടാകുന്ന ബിറ്റൂമിന്‍ നിക്ഷേപം. കാലിഫോര്‍ണിയയിലെ ടാര്‍പൂള്‍, ട്രിനിഡാഡിലെ പിച്ച്‌ ലേക്ക്‌ എന്നിവ ഉദാഹരണം.', '', NULL),
(516, 'assay', 'അസ്സേ', 'വളരെ സൂക്ഷ്‌മ അളവില്‍ ഏതെങ്കിലും പ്രത്യേക വസ്‌തു ലായനിയില്‍ ഉണ്ടോ എന്നറിയാനുള്ള ടെസ്റ്റ്‌.', '', NULL),
(517, 'association', 'അസോസിയേഷന്‍', '-', 'clusters of stars', NULL),
(518, 'associative law', 'സഹചാരി നിയമം', 'ഗണിതക്രിയകളെ സംബന്ധിക്കുന്ന ഒരു നിയമം. (x*y)*z=x*(y*z) എന്ന സമവാക്യം പാലിക്കപ്പെടുന്നുവെങ്കിൽ സംക്രിയ ആ ഗണത്തിൽ സാഹചര്യനിയമം പാലിക്കുന്നുവെന്ന് പറയുന്നു. രണ്ടിലധികം രാശികളിന്മേല്‍ ഒരു ഗണിതക്രിയ പ്രയോഗിക്കേണ്ടപ്പോള്‍, രാശികള്‍ ഏതു ക്രമത്തില്‍ ക്രിയയ്‌ക്ക്‌ എടുക്കുന്നു എന്നത്‌ ഫലത്തെ ബാധിക്കുന്നില്ലെങ്കില്‍ ക്രിയ സഹചാരിയാണ്‌. ഉദാ: ധനസംഖ്യകളുടെ സങ്കലനം, ധനസംഖ്യകളുടെ ഗുണനം. എന്നാല്‍ ധനസംഖ്യകളുടെ വ്യവകലനം, ഹരണം എന്നിവ സഹചാരിയല്ല.', '', NULL),
(519, 'aster', 'ആസ്റ്റര്‍', 'കോശവിഭജന സമയത്ത്‌ സെന്‍ട്രിയോളുകള്‍ക്ക്‌ ചുറ്റുമായി നക്ഷത്രം പോലെ കാണുന്ന വസ്‌തു. കേന്ദ്രബിന്ദുവില്‍ നിന്ന്‌ എല്ലാ വശത്തേക്കും വികിരണം ചെയ്യുന്ന മൈക്രാ ട്യൂബുകളുടെ രശ്‌മികളാണിത്‌. ഉയര്‍ന്ന തരം സസ്യങ്ങളില്‍ ഇവയില്ല.', '', NULL),
(520, 'asteroids', 'ഛിന്ന ഗ്രഹങ്ങള്‍', 'സൌരയൂഥത്തില്‍, ചൊവ്വയ്‌ക്കും വ്യാഴത്തിനും ഇടയ്‌ക്കു കാണപ്പെടുന്ന ഗ്രഹശകലങ്ങള്‍. ഒരു ഗ്രഹമായി കൂടിച്ചേരാന്‍ കഴിയാതെ പോയ ദ്രവ്യമാണെന്ന്‌ കരുതപ്പെടുന്നു. സിറെസ്‌, ജൂനോ, പല്ലാസ്‌, വെസ്റ്റ എന്നിവയാണ്‌ പ്രധാനപ്പെട്ടവ. ഇവയില്‍ സിറെസിന്‌ ആയിരത്തിനടുത്ത്‌ കിലോമീറ്റര്‍ വ്യാസമുണ്ട്‌.', '', NULL),
(521, 'asthenosphere', 'അസ്‌തനോസ്‌ഫിയര്‍', 'ലിതോസ്‌ഫിയറിനും മെസോസ്‌ഫിയറിനും ഇടയിലുള്ള ഭൂവല്‍ക്കത്തിലെ താരതമ്യേന ദുര്‍ബലമായ ഒരു പാളി. 50 മുതല്‍ 240 വരെ കിലോമീറ്റര്‍ കനമുണ്ടായിരിക്കും. മാഗ്മയുടെ ഉറവിടമാണ്‌ എന്ന്‌ വിശ്വസിക്കപ്പെടുന്നു.', '', NULL),
(522, 'astigmatism', 'അബിന്ദുകത', 'ഒരു നേത്രവൈകല്യം. ഒരേ അകലത്തിലുള്ള തിരശ്ചീനമായ ഒരു വസ്‌തുവിനെയും ലംബമായ മറ്റൊരു വസ്‌തുവിനെയും ഒരേ സമയം വ്യക്തമായി കാണാന്‍ കഴിയാത്ത അവസ്ഥ.', '', NULL),
(523, 'astro biology', 'സൌരേതരജീവശാസ്‌ത്രം', '-', 'exobiology', NULL),
(524, 'Astrolabe', 'അസ്‌ട്രാലാബ്‌', 'സമയവും കാലയളവും കണക്കാക്കുന്നതിനുള്ള ഒരു ആദ്യകാല ഉപകരണം. സൂര്യചന്ദ്രന്മാരുടെയും നക്ഷത്രങ്ങള്‍, ഗ്രഹങ്ങള്‍ എന്നിവയുടെയും സ്ഥാനം കണ്ടെത്തി പ്രാദേശിക സമയം നിര്‍ണയിക്കാന്‍ ഈ ഉപകരണം ഉപയോഗിച്ചിരുന്നു.', '', NULL),
(525, 'astrometry', 'ജ്യോതിര്‍മിതി', 'നക്ഷത്രങ്ങളുടെയും മറ്റു പ്രപഞ്ചവസ്‌തുക്കളുടെയും സ്ഥാനവും ചലനവും അളക്കുന്ന നിരീക്ഷണ ജ്യോതിശ്ശാസ്‌ത്രശാഖ.', '', NULL),
(526, 'astronomical unit', 'സൌരദൂരം', 'വലിയ ദൂരങ്ങള്‍ക്കുള്ള ഒരു ഏകകം. സൂര്യനും ഭൂമിക്കും ഇടയിലുള്ള ശരാശരി അകലത്തിനു തുല്യമാണ്‌. ഒരു സൌരദൂരം= 1.496 x1011m. AU എന്നു ചുരുക്കം.', '', NULL),
(527, 'astrophysics', 'ജ്യോതിര്‍ ഭൌതികം', 'നക്ഷത്രങ്ങളുടെയും മറ്റു പ്രപഞ്ചവസ്‌തുക്കളുടെയും രൂപീകരണവും പരിണാമവും പഠനവിധേയമാക്കുന്ന ജ്യോതിശ്ശാസ്‌ത്രശാഖ.', '', NULL),
(528, 'asymmetric carbon atom', 'അസമമിത കാര്‍ബണ്‍ അണു', 'കൈറാല്‍ കാര്‍ബണ്‍ എന്നും വിശേഷിപ്പിക്കും. കാര്‍ബണ്‍ മറ്റ്‌ ആറ്റങ്ങളുമായോ ഗ്രൂപ്പുകളുമായോ ചേരുമ്പോള്‍ നാലു രാസബന്ധങ്ങളുണ്ടാകുന്നു. ഇങ്ങനെ നാലു വ്യത്യസ്‌തങ്ങളായ ആറ്റങ്ങളുമായോ, ഗ്രൂപ്പുകളുമായോ ചേര്‍ന്നുണ്ടാകുന്ന തന്മാത്രയിലെ കാര്‍ബണ്‍ ആണ്‌ അസമമിത കാര്‍ബണ്‍ ആറ്റം. കൈറാല്‍ കാര്‍ബണുകളുള്ള തന്മാത്രകള്‍ക്ക്‌ ചില പ്രത്യേക ഗുണധര്‍മ്മങ്ങള്‍ ഉണ്ടായിരിക്കും.', '', NULL),
(529, 'asymptote', 'അനന്തസ്‌പര്‍ശി', 'വക്രത്തെ തുടര്‍ച്ചയായി സമീപിച്ചുകൊണ്ടിരിക്കുന്നതും എന്നാല്‍ പരിമിത ദൂരത്തിനുള്ളില്‍ അതിനെ സ്‌പര്‍ശിക്കാത്തതുമായ രേഖ. വക്രത്തിന്‌ അനന്തദൂരത്തുള്ള ഒരു ബിന്ദുവില്‍ വരയ്‌ക്കുന്ന സ്‌പര്‍ശരേഖയാണിത്‌ എന്ന്‌ പറയാം. ചിത്രത്തില്‍ സ്‌പര്‍ശരേഖ വക്രത്തെ സ്‌പര്‍ശിക്കുന്നത്‌ അനന്തതയിലാണ്‌.', '', NULL),
(530, 'atlas', 'അറ്റ്‌ലസ്‌', '(biology) നാല്‍ക്കാലി കശേരുകികളുടെ തലയോടിനോട്‌ അടുത്തുള്ള ആദ്യ കശേരു. തലയോടിനെ താങ്ങിനിര്‍ത്തുന്നതിനും തല ഇരുവശത്തേക്കും തിരിക്കുവാനും യോജിച്ച ഘടനയാണ്‌ ഇതിന്‌.', '', NULL),
(531, 'atlas', 'അറ്റ്‌ലസ്‌', ' (geography) അറ്റ്‌ലസ്‌, ഭൂപടപുസ്‌തകം. ഭൂപടങ്ങളുടെ സമാഹാരം.', '', NULL),
(532, 'atmosphere', 'അന്തരീക്ഷം', '1. വാതകങ്ങളും നീരാവിയും അടങ്ങിയ, ഭൂമിയെ പൊതിഞ്ഞിരിക്കുന്ന ആവരണം. ഗുരുത്വാകര്‍ഷണത്താലാണ്‌ അന്തരീക്ഷം നിലനില്‍ക്കുന്നത്‌. ഭൂമിയില്‍ നിന്നുള്ള ഉയരത്തിനാനുപാതികമായി വാതകങ്ങളുടെയും നീരാവിയുടെയും അനുപാതത്തിലും, അന്തരീക്ഷത്തിന്റെ ഭൗതിക ഗുണങ്ങളിലും വ്യതിയാനം ഉണ്ടാകുന്നു. ഇതിലെ വാതകങ്ങളുടെ ആകെ ദ്രവ്യമാനത്തിന്റെ 75%വും നീരാവിയുടെ 90%വും ഏറ്റവും അടിത്തട്ടായ ട്രാപോസ്‌ഫിയറിലാണ്‌ ഉള്ളത്‌. ഇത്‌ ഭൂമിയുടെ നിരപ്പില്‍ നിന്ന്‌ 10-16 കി. മീ ഉയരത്തില്‍ വരെയുണ്ട്‌. അതിനു മുകളിലത്തെ തട്ടായ സ്‌ട്രാറ്റോസ്‌ഫിയര്‍ (16-50 കി. മീ. ഉയരം വരെ) ഓസോണിന്റെ സഹായത്താല്‍ ജീവജാലങ്ങള്‍ക്ക്‌ ദോഷകരമായ അള്‍ട്രാവയലറ്റ്‌ രശ്‌മികളെ ആഗിരണം ചെയ്യുന്നു. അതിനു മുകളിലായി മീസോസ്‌ഫിയറും (50-85 കി.മീ) അതിനു മുകളിലായി അയണോസ്‌ഫിയറും (85-500-600 കി. മീ). പൊതുവായ വാതക അനുപാതം നൈട്രജന്‍ 78.08%, ഓക്‌സിജന്‍ 20.95%, കാര്‍ബണ്‍ ഡൈ ഓക്‌സൈഡ്‌ .035%, ആര്‍ഗണ്‍ 0.93% ഇവയെ കൂടാതെ നിയോണ്‍, ഹീലിയം, ഹൈഡ്രജന്‍, കാര്‍ബണ്‍ മോണോക്‌സൈഡ്‌, നൈട്രജന്‍ ഓക്‌സൈഡുകള്‍, ക്ലോറോഫ്‌ളൂറോ കാര്‍ബണ്‍, സള്‍ഫര്‍ ഡൈ ഓക്‌സൈഡ്‌ മുതലായവയും ഉണ്ട്‌. 2. പൊതുവേ ഏതൊരു ഖഗോളവസ്‌തുവിനെയും പൊതിഞ്ഞിരിക്കുന്ന വാതകാവരണത്തെ അന്തരീക്ഷമെന്ന്‌ പറയാം. 3. അന്തരീക്ഷമര്‍ദ്ദത്തിന്റെ ഒരു യൂണിറ്റ്‌. 0.76 മീ. രസയൂപത്തിന്റെ മര്‍ദത്തിന്‌ തുല്യം.', '', NULL),
(533, 'atoll', 'എറ്റോള്‍', 'സമുദ്രത്തില്‍ കാണപ്പെടുന്ന, നടുവില്‍ തടാകം ഉള്ള വലയാകൃതിയിലുള്ള പവിഴപ്പുറ്റുകള്‍. തമ്മില്‍ ബന്ധം ഉണ്ടാകണമെന്നില്ല. പദത്തിന്റെ ഉത്ഭവം \"ഇതള്‍\' എന്ന മലയാള പദത്തില്‍ നിന്നാണ്‌. മാലി ദ്വീപിനുള്ള പഴയ മലയാള പേരാണിത്‌. സാധാരണ സമുദ്രനിരപ്പിന്‌ മുകളില്‍ കാണുകയില്ല.', '', NULL),
(534, 'atom', 'ആറ്റം', 'ഒരു മൂലകത്തിന്റെ എല്ലാ ഗുണവിശേഷങ്ങളും അടങ്ങിയിരിക്കുന്ന ഏറ്റവും സൂക്ഷ്‌മ ഘടകം.', '', NULL),
(535, 'atom bomb', 'ആറ്റം ബോംബ്‌', 'ശൃംഖലാ അണുവിഘടനം വഴി വമ്പിച്ച ഊര്‍ജം വളരെ പെട്ടെന്ന്‌ ലഭ്യമാക്കുന്ന നശീകരണോപാധി. വന്‍തോതില്‍ നാശനഷ്‌ടങ്ങളുണ്ടാക്കുവാന്‍ ആറ്റംബോംബ്‌ വിസ്‌ഫോടനത്തിന്‌ കഴിയും.', '', NULL),
(536, 'atomic clock', 'അണുഘടികാരം', 'വളരെയധികം കൃത്യതയുള്ള ഒരു സമയമാപിനി. തറനിലയിലുള്ള സീസിയം ആറ്റങ്ങളുടെ രണ്ട്‌ ഹൈപ്പര്‍ ഫൈന്‍ ഊര്‍ജനിലകള്‍ തമ്മിലുള്ള സംക്രമണ ഫലമായുള്ള വികിരണത്തിന്റെ 9,192,631,770 കമ്പനങ്ങള്‍ക്കുവേണ്ട സമയമാണ്‌ ഒരു സെക്കന്റ്‌.', '', NULL),
(537, 'atomic heat', 'അണുതാപം', 'ഒരു ഗ്രാം ആറ്റത്തിന്റെ താപധാരിത. മൂലകത്തിന്റെ വിശിഷ്‌ടതാപത്തെ അണുഭാരം കൊണ്ട്‌ ഗുണിച്ചു കിട്ടുന്നതാണ്‌ ഇത്‌.', '', NULL),
(538, 'atomic mass unit', 'അണുഭാരമാത്ര', 'ആറ്റത്തിന്റെയും തന്മാത്രയുടെയും പ്രാഥമിക കണങ്ങളുടെയും മറ്റും ദ്രവ്യമാനത്തിന്റെ ഒരു ഏകകം. amu എന്ന്‌ ചുരുക്കം. 1 amu = 12C ആറ്റത്തിന്റെ ദ്രവ്യമാനത്തിന്റെ 12-ല്‍ ഒരു ഭാഗം എന്നെടുത്തിരിക്കുന്നു. 1.66X10-27 കിലോഗ്രാമിന്‌ തുല്യം.', '', NULL),
(539, 'atomic number', 'അണുസംഖ്യ', 'ഒരു മൂലകത്തെ മറ്റൊന്നില്‍ നിന്ന്‌ വേര്‍തിരിച്ച്‌ പറയുന്ന മുഖ്യ സംഖ്യ. ആവര്‍ത്തന പട്ടികയില്‍ മൂലകങ്ങളെ വര്‍ഗീകരിച്ചിരിക്കുന്നത്‌ ഇതിന്റെ അടിസ്ഥാനത്തിലാണ്‌. അണുകേന്ദ്രത്തിലെ പ്രാട്ടോണുകളുടെ എണ്ണമാണ്‌ അണുസംഖ്യ.', '', NULL),
(540, 'atomic pile', 'ആറ്റമിക പൈല്‍', 'ഇന്ധനവും ഗ്രാഫൈറ്റ്‌ മോഡറേറ്ററും അട്ടിയായി ക്രമീകരിച്ച്‌ പ്രവര്‍ത്തിപ്പിച്ചിരുന്ന ആദ്യകാല അണു റിയാക്‌ടര്‍.', '', NULL),
(541, 'atomicity', 'അണുകത', 'ഒരു മൂലക തന്മാത്രയില്‍ അടങ്ങിയിട്ടുള്ള ആറ്റങ്ങളുടെ എണ്ണം.', '', NULL),
(542, 'ATP', 'എ ടി പി', 'Adenosine Triphosphate എന്നതിന്റെ ചുരുക്കം. ശരീരത്തില്‍ ഊര്‍ജകറന്‍സിയായി പ്രവര്‍ത്തിക്കുന്നു.', '', NULL),
(543, 'atrium', 'ഏട്രിയം ഓറിക്കിള്‍', '1. കശേരുകികളുടെ ഹൃദയത്തിന്റെ മേലറ. മത്സ്യങ്ങളില്‍ ഒരു ഏട്രിയവും മറ്റുള്ളവയില്‍ രണ്ട്‌ ഏട്രിയങ്ങളുമാണുള്ളത്‌. രണ്ട്‌ ഏട്രിയങ്ങള്‍ക്കിടയ്‌ക്ക്‌ ഒരു ഭിത്തിയുണ്ട്‌. 2. ആംഫിയോക്‌സസ്‌, ട്യൂണിക്കേറ്റുകള്‍ ഇവയില്‍ ഗില്‍പഴുതുകളുടെ പുറത്തായി ഉള്ള പ്രത്യേക അറ. ഇത്‌ പുറത്തേക്ക്‌ തുറക്കുന്നു.', '', NULL),
(544, 'atropine', 'അട്രാപിന്‍', 'നിറമില്ലാത്തതും വിഷപ്രഭാവമുള്ളതും ജലത്തില്‍ ലയിക്കാത്തതുമായ ഒരു ക്രിസ്റ്റലീയ ആല്‍ക്കലോയ്‌ഡ്‌ C17H23NO3. അട്രാപ്പാ ബലഡോണ എന്ന ശാസ്‌ത്രനാമമുള്ള ചെടിയില്‍ നിന്ന്‌ ലഭിക്കുന്നു. ഉദര സംബന്ധമായ രോഗങ്ങള്‍ക്കും കണ്ണിനുള്ള ചില മരുന്നുകളിലും ഉപയോഗിക്കുന്നു. smooth muscles ന്റെ അനിയന്ത്രിതമായ ചലനത്താല്‍ ഉണ്ടാകുന്ന ശക്തമായ വേദന കുറയ്‌ക്കാനായുള്ള ഔഷധമായാണ്‌ പ്രധാന ഉപയോഗം ( antispasmodic)', '', NULL),
(545, 'attenuation', 'ക്ഷീണനം', 'തീവ്രതയില്‍ വരുന്ന കുറവ്‌. ഉദാ: acoustic attenuation- ശബ്‌ദ ക്ഷീണനം.', '', NULL),
(546, 'atto', 'അറ്റോ', '1018എന്നു സൂചിപ്പിക്കുന്ന ഉപസര്‍ഗം.', '', NULL),
(547, 'attrition', 'അട്രീഷന്‍', 'കാറ്റ്‌ വഹിച്ചുകൊണ്ടു വരുന്ന ഖരകണികകള്‍ പരസ്‌പരം കൂട്ടിമുട്ടി പൊടിയാക്കപ്പെടുന്ന പ്രവര്‍ത്തനം.', '', NULL),
(548, 'AU', 'എ യു ', 'Astronomical Unit എന്നതിന്റെ ചുരുക്കം.', '', NULL),
(549, 'audio frequency', 'ശ്രവ്യാവൃത്തി', 'മനുഷ്യകര്‍ണങ്ങള്‍ക്ക്‌ ശ്രവിക്കാന്‍ കഴിയുന്ന ആവൃത്തി സംഘാതം. ഇത്‌ 20Hz - 20000 Hz ന്‌ ഇടയിലാണ്‌. AF എന്ന്‌ ചുരുക്കം. ഇതേ ആവൃത്തിയുള്ള വിദ്യുത്‌ സിഗ്നലുകളെ ഉച്ചഭാഷിണി ഉപയോഗിച്ച്‌ നേരിട്ട്‌ ശബ്‌ദതരംഗങ്ങളാക്കി മാറ്റാം. അതിനാല്‍ അവയെ AF തരംഗങ്ങള്‍ എന്നും പറയാറുണ്ട്‌.', '', NULL),
(550, 'auditory canal', 'ശ്രവണ നാളം', 'ബാഹ്യകര്‍ണ്ണത്തില്‍ നിന്നോ ശരീരോപരിതലത്തില്‍ നിന്നോ മധ്യകര്‍ണ്ണത്തിലേക്കുള്ള നാളം. ശബ്‌ദവീചികള്‍ ഇതിലൂടെ സഞ്ചരിച്ച്‌ കര്‍ണ്ണസ്‌തരത്തിലെത്തുന്നു.', '', NULL),
(551, 'aureole', 'ഓറിയോള്‍', '(geography) ഒരു ആഗ്നേയ ശിലാപടലത്തെ വലയം ചെയ്‌ത്‌ സ്ഥിതിചെയ്യുന്നതും അതുകൊണ്ടുതന്നെ കായാന്തരണം സംഭവിച്ചതുമായ ശിലാമേഖല.', '', NULL),
(552, 'aureole', 'പരിവേഷം', '(astronomy) സൂര്യനോ ചന്ദ്രനോ ചുറ്റും ചിലപ്പോള്‍ ദൃശ്യമാകുന്ന പ്രഭാവലയം.', '', NULL),
(553, 'auricle', 'ഓറിക്കിള്‍', 'ഹൃദയത്തിന്റെ മേലറ. atrium നോക്കുക.', '', NULL),
(554, 'aurora', 'ധ്രുവദീപ്‌തി', 'ധ്രുവപ്രദേശത്തോടു ചേര്‍ന്ന്‌ രാത്രികാലങ്ങളില്‍ ഉന്നതാന്തരീക്ഷത്തില്‍ കാണപ്പെടുന്ന വര്‍ണശബളമായ പ്രകാശം. ബാഹ്യാകാശത്തുനിന്ന്‌, പ്രത്യേകിച്ച്‌ സൂര്യനില്‍ നിന്ന്‌ വരുന്ന ചാര്‍ജിത കണങ്ങള്‍ ഭൂമിയുടെ കാന്തിക മണ്ഡലത്തില്‍ അകപ്പെട്ട്‌ ധ്രുവങ്ങളിലെത്തി അവിടുത്തെ വായു തന്മാത്രകളുമായി പ്രതിപ്രവര്‍ത്തിക്കുന്നതാണ്‌ ധ്രുവദീപ്‌തിക്ക്‌ കാരണം. ദക്ഷിണ ധ്രുവപ്രദേശത്ത്‌ കാണുന്നതിനെ aurora australis എന്നും ഉത്തര ധ്രുവപ്രദേശത്ത്‌ കാണുന്നതിനെ aurora borealis എന്നും പറയുന്നു.', '', NULL),
(555, 'autecology', 'സ്വപരിസ്ഥിതിവിജ്ഞാനം', 'ഒരു സ്‌പീഷീസിന്റെ പരിസ്ഥിതി വിജ്ഞാനം. ജീവസമൂഹത്തിന്റെ പരിസ്ഥിതി വിജ്ഞാനത്തില്‍ നിന്ന്‌ വ്യത്യസ്‌തമാണിത്‌.', '', NULL),
(556, 'autoclave', 'ഓട്ടോ ക്ലേവ്‌', '1. ഉരുക്കുകൊണ്ട്‌ നിര്‍മ്മിച്ചിട്ടുള്ള ഒരു പാത്രം. രാസ അഭിക്രിയകളും ശുദ്ധീകരണവും നടത്താന്‍ ഉപയോഗിക്കുന്നു. 2. ശസ്‌ത്രക്രിയോപകരണങ്ങള്‍, കുത്തിവെക്കാനുള്ള സൂചി, കള്‍ച്ചര്‍ മാധ്യമം എന്നിവയെ അണുമുക്തമാക്കാന്‍ ഉപയോഗിക്കുന്ന സ്റ്റീല്‍ കൊണ്ടുള്ള ഉപകരണം. നീരാവി കൂടിയ മര്‍ദത്തില്‍ പ്രയോഗിച്ചാണ്‌ ഇതു സാധിക്കുന്നത്‌. നീരാവി ഉത്‌പാദിപ്പിച്ച്‌ കൂടിയ മര്‍ദത്തില്‍ പ്രത്യേക അറയില്‍ നിലനിര്‍ത്താനാവശ്യമായ രീതിയില്‍ നിര്‍മ്മിച്ചെടുത്ത സ്റ്റീലിന്റെ ഉപകരണമാണിത്‌.', '', NULL),
(557, 'auto-catalysis', 'സ്വ-ഉല്‍പ്രരണം', 'ഒരു രാസപ്രവര്‍ത്തനത്തിലെ ഉല്‍പന്നം തന്നെ ഉല്‍പ്രരക മായി പ്രവര്‍ത്തിക്കുന്നത്‌. ഉദാ: പൊട്ടാസിയം പെര്‍മാംഗനേറ്റും ഓക്‌സാലിക്‌ ആസിഡും തമ്മില്‍ അമ്ലമാധ്യമത്തിലുള്ള പ്രവര്‍ത്തനം. ഇവിടെ ഉല്‍പന്നങ്ങളില്‍ ഒന്നായ മാംഗനീസ്‌ അയോണുകള്‍ ( Mn2+) ആണ്‌ സ്വ ഉല്‍പ്രരകം.', '', NULL),
(558, 'autoecious', 'ഏകാശ്രയി', 'ജീവിതചക്രം ഒരേ അതിഥി സസ്യത്തില്‍ തന്നെ പൂര്‍ത്തിയാക്കുന്ന പരാദം. ഉദാ: റസ്റ്റ്‌ ഫംഗസ്‌.', '', NULL),
(559, 'autogamy', 'സ്വയുഗ്മനം', 'ഒരേ ജനിതക ഘടനയുള്ള ലിംഗകോശങ്ങള്‍ തമ്മിലുള്ള യുഗ്മനം.', '', NULL),
(560, 'auto immunity', 'ഓട്ടോ ഇമ്മ്യൂണിറ്റി', 'ശരീരത്തില്‍ത്തന്നെയുള്ള ചില പ്രത്യേക പ്രാട്ടീനുകളുമായി പ്രതിപ്രവര്‍ത്തിച്ച്‌ സ്വപ്രതിവസ്‌തുക്കള്‍ ഉണ്ടാകുന്ന അവസ്ഥ. ഇതിന്റെ കാരണം മനസ്സിലായിട്ടില്ല.', '', NULL),
(561, 'autolysis', 'സ്വവിലയനം', 'കോശങ്ങളിലെ ആന്തരിക എന്‍സൈമുകളുടെ പ്രവര്‍ത്തനം കൊണ്ട്‌ അവയിലെ തന്നെ കോശഘടനകളെ വിഘടിപ്പിക്കുന്ന പ്രക്രിയ.', '', NULL),
(562, 'autonomous nervous system', 'സ്വതന്ത്ര നാഡീവ്യൂഹം', 'ശരീരത്തിലെ അനിച്ഛാ പ്രവര്‍ത്തനങ്ങളെ നിയന്ത്രിക്കുന്ന നാഡീവ്യൂഹം. ഇവ മസ്‌തിഷ്‌കത്തിന്റെ ബോധപൂര്‍വ്വമായ നിയന്ത്രണത്തിന്റെ കീഴിലല്ല. അനുകമ്പാനാഡികള്‍ ( Sympathetic nerves), പാരാനുകമ്പാനാഡികള്‍ ( parasympathetic nerves) എന്നീ രണ്ട്‌ ഉപവിഭാഗങ്ങളുണ്ട്‌. പചനവ്യൂഹം, രക്തചംക്രമണ വ്യൂഹം എന്നിവയിലെ ഗ്രന്ഥികളും നാഡികളും ഇവയുടെ നിയന്ത്രണത്തിലാണ്‌.', '', NULL),
(563, 'autopolyploidy', 'സ്വബഹുപ്ലോയിഡി', 'ഡിപ്ലോയ്‌ഡ്‌ ക്രാമസോം ഗണത്തിന്റെ ഇരട്ടിപ്പുമൂലം ഉത്ഭവിക്കുന്ന ബഹുപ്ലോയിഡി. ഇതിലെ ക്രാമസോമുകളെല്ലാം ഒരേ സ്‌പീഷീസിലേതു തന്നെ ആയിരിക്കും.', '', NULL),
(564, 'autoradiography', 'ഓട്ടോ റേഡിയോഗ്രഫി', 'റേഡിയോ പ്രസരണ ശേഷിയുള്ള, ലേബല്‍ ചെയ്‌ത വസ്‌തു ഫിലിമിനോടൊപ്പം വെച്ച്‌ പ്രതിരൂപത്തിന്റെ ചിത്രമെടുക്കുന്ന സാങ്കേതികവിദ്യ.', '', NULL),
(565, 'autosomes', 'അലിംഗ ക്രാമസോമുകള്‍', 'ഒരു ജീവിയുടെ ലൈംഗികേതര ക്രാമസോമുകള്‍.', '', NULL),
(566, 'autotomy', 'സ്വവിഛേദനം', 'ശരീരത്തിന്റെ ഒരു ഭാഗം സ്വയം മുറിച്ചുകളയല്‍. പരഭോജികളില്‍ നിന്ന്‌ രക്ഷപ്പെടാനുള്ള ഒരു അനുകൂലനമാണിത്‌. നഷ്‌ടപ്പെട്ട ശരീരഭാഗം വീണ്ടും വളരുന്നു. ഉദാ: പല്ലി വാല്‍ മുറിച്ചു കളയുന്നത്‌.', '', NULL),
(567, 'autotrophs', 'സ്വപോഷികള്‍', 'സ്വയം ഭക്ഷണം ഉത്‌പാദിപ്പിക്കാന്‍ കഴിവുള്ള ജീവികള്‍. രാസസംശ്ലേഷണം വഴിയോ പ്രകാശസംശ്ലേഷണം വഴിയോ ഭക്ഷണം ഉത്‌പാദിപ്പിക്കുന്നവയാണിവ.', '', NULL),
(568, 'auxanometer', 'ദൈര്‍ഘ്യമാപി', 'വളരുന്ന സസ്യഭാഗങ്ങളുടെ ദൈര്‍ഘ്യ വര്‍ദ്ധനവ്‌ അളക്കാനുള്ള ഉപകരണം.', '', NULL),
(569, 'auxins', 'ഓക്‌സിനുകള്‍', 'സസ്യങ്ങളുടെ വളര്‍ച്ച നിയന്ത്രിക്കുന്ന ഒരു വിഭാഗം ഹോര്‍മോണുകള്‍.', '', NULL),
(570, 'auxochrome', 'ഓക്‌സോക്രാം', 'വര്‍ണങ്ങളെ സ്വയം ആഗിരണം ചെയ്യുകയില്ലെങ്കിലും, ഒരു ക്രാമോഫോര്‍ ആഗിരണം ചെയ്‌ത പ്രകാശത്തിന്റെ തരംഗദൈര്‍ഘ്യത്തില്‍ മാറ്റം വരുത്താന്‍ കഴിയുന്നതോ, വര്‍ണത്തിന്റെ തീവ്രത വര്‍ദ്ധിപ്പിക്കുന്നതോ ആയ ഒരു ഗ്രൂപ്പ്‌. ഉദാ: −NH2, −OH.', '', NULL),
(571, 'avalanche', 'അവലാന്‍ഷ്‌', 'ഹിമപാതം, 1. പര്‍വതങ്ങളില്‍ നിന്നുള്ള വന്‍തോതിലുള്ള ഹിമപാതം. ചെറിയ അളവില്‍ മുകളില്‍ നിന്നു തുടങ്ങുന്ന ഹിമപാതം താഴെ എത്തുമ്പോഴേക്ക്‌ അതിഭീമമായി വളര്‍ന്നിരിക്കും. 2. ഇതിന്‌ സമാനമായ അയണീകരണ പ്രക്രിയ. ഒരു അയണീകരണം അനുകൂലമായ സാഹചര്യത്തില്‍ അനേകം അയണീകരണങ്ങള്‍ക്ക്‌ കാരണമാവുന്നു. ഉദാ: ഗീഗര്‍ കണ്ടൗറിന്റെ പ്രവര്‍ത്തനം.', '', NULL),
(572, 'Avogadro number', 'അവഗാഡ്രാ സംഖ്യ', 'ഒരു മോള്‍ പദാര്‍ഥത്തിലെ തന്മാത്രകളുടെ എണ്ണം. 6.02255x1023', '', NULL),
(573, 'awn', 'ശുകം', 'പുല്ലുവര്‍ഗത്തില്‍പ്പെട്ട സസ്യങ്ങളിലെ പൂക്കളില്‍ കണിശകകളുടെ ബാഹ്യപര്‍ണങ്ങളില്‍ കാണുന്ന മുള്ളന്‍ രോമം പോലുള്ള ഘടന.', '', NULL),
(574, 'axil', 'കക്ഷം', 'ഇലഞെട്ടും കാണ്ഡവും തമ്മിലുണ്ടാവുന്ന കോണ്‍.', '', NULL),
(575, 'axillary bud', 'കക്ഷമുകുളം', 'ഇലകള്‍ തണ്ടിനോട്‌ ചേരുന്ന കക്ഷങ്ങളിലുള്ള മുകുളം.', '', NULL),
(576, 'axiom', 'സ്വയംസിദ്ധ പ്രമാണം', 'തെളിവു നല്‍കാതെ തന്നെ സ്വീകരിക്കപ്പെടുന്ന പ്രസ്‌താവന. എല്ലാ ഗണിത ശാഖകളിലുമുണ്ട്‌ അംഗീകൃത ആക്‌സ്യങ്ങള്‍. ഉദാ: (1) ഒരേ വസ്‌തുവിന്‌ തുല്യമായ വസ്‌തുക്കള്‍ പരസ്‌പരം തുല്യങ്ങളായിരിക്കും. (2) മുഴുവനേക്കാള്‍ ചെറുതാണ്‌ ഭാഗികം. തുടങ്ങിയവ.', '', NULL),
(577, 'axis', 'അക്ഷം', '1. (maths) നിര്‍ദേശാങ്ക വ്യവസ്ഥയിലെ ആധാര രേഖകളില്‍ ഒന്ന്‌. 2 (phy) ഒരു വസ്‌തുവിന്റെ സ്വയംഭ്രമണത്തിനോ പരിക്രമണത്തിനോ ആധാരമായ നേര്‍രേഖ.', '', NULL),
(578, 'axis of ordinates', 'കോടി അക്ഷം', 'കാര്‍ടീഷ്യന്‍ നിര്‍ദേശാങ്ക വ്യവസ്ഥയിലെ y അക്ഷം.', '', NULL),
(579, 'axolotl', 'ആക്‌സലോട്ട്‌ല്‍', 'മെക്‌സിക്കന്‍ തടാകത്തില്‍ കാണപ്പെടുന്ന ആംബ്ലിസ്റ്റോമ എന്ന ഉഭയജീവിയുടെ ലാര്‍വ. ഇതിന്‌ പ്രത്യുത്‌പാദന ശേഷിയുണ്ട്‌. neoteny കാണുക.', '', NULL),
(580, 'axon', 'ആക്‌സോണ്‍', 'ഒരു നാഡീകോശത്തിന്റെ നാരുപോലുള്ള നീണ്ട വളര്‍ച്ച. ഇതുവഴിയാണ്‌ നാഡീകോശത്തില്‍ നിന്ന്‌ നാഡീ ആവേഗങ്ങള്‍ പ്രസരിക്കുന്നത്‌. ചിത്രം neurone നോക്കുക.', '', NULL),
(581, 'Axoneme', 'ആക്‌സോനീം', 'ഒരു സ്‌തരത്താല്‍ ചുറ്റപ്പെട്ട സൂക്ഷ്‌മനാളികളുടെ കൂട്ടം. സ്‌തരം പ്ലാസ്‌മാസ്‌തരത്തിന്റെ തുടര്‍ച്ചയായിരിക്കും. സീലിയങ്ങള്‍ക്ക്‌ ഈ ഘടനയാണുള്ളത്‌.', '', NULL),
(582, 'azeotrope', 'അസിയോട്രാപ്‌', 'ചില ദ്രാവകങ്ങള്‍ പ്രത്യേക അളവില്‍ ചേരുമ്പോള്‍ ഉണ്ടാകുന്ന സ്ഥിരതിളനിലയുള്ള മിശ്രിതം. തിളപ്പിച്ചാല്‍ കിട്ടുന്ന ബാഷ്‌പ ഘടകങ്ങളുടെ അനുപാതവും, ദ്രാവക മിശ്രിതത്തിന്റെ അനുപാതവും ഒന്നു തന്നെയാകയാല്‍ സ്വേദനം വഴി ഇവയെ വേര്‍തിരിക്കാനാവില്ല. ഉദാ: 99.5% വീര്യമുള്ള സ്‌പിരിറ്റ്‌ അഥവാ റെക്‌ടിഫൈഡ്‌ സ്‌പിരിറ്റ്‌.', '', NULL),
(583, 'azeotropic distillation', 'അസിയോട്രാപ്പിക്‌ സ്വേദനം', 'അസിയോട്രാപ്പിക്‌ മിശ്രിതങ്ങളെ വേര്‍തിരിക്കുവാനുള്ള മാര്‍ഗം. ഉദാ: 96% എഥനോളില്‍ നിന്ന്‌ ജലം നീക്കി ശുദ്ധ എഥനോള്‍ ഉണ്ടാക്കാന്‍ ബെന്‍സീന്‍ ചേര്‍ത്ത്‌ സ്വേദനം ചെയ്യുന്നത്‌ അസിയോട്രാപ്പിക്‌ സ്വേദനമാണ്‌.', '', NULL),
(584, 'azide', 'അസൈഡ്‌', 'ഹൈഡ്രാസോയിക്‌ അമ്ലത്തിന്റെ ലവണം. അസൈഡ്‌ ഗ്രൂപ്പു( N3) കള്‍ കൊണ്ടുള്ള ഒരു സംയുക്തം.', '', NULL),
(585, 'azimuth', 'അസിമുത്‌', 'ഒരു ആധാര സ്ഥാനത്തു നിന്ന്‌ ഉയരത്തിലുള്ള ഒരു വസ്‌തുവിലേക്കുള്ള തിരശ്ചീന കോണീയ അകലം.', '', NULL),
(586, 'azimuthal projection', 'ശീര്‍ഷതല പ്രക്ഷേപം', '-', 'map projections', NULL),
(587, 'azo compound', 'അസോ സംയുക്തം', 'അസോ ഗ്രൂപ്പ്‌ ( N=N) അടങ്ങിയിട്ടുള്ള സംയുക്തം.', '', NULL),
(588, 'azo dyes', 'അസോ ചായങ്ങള്‍', 'അസോ ഗ്രൂപ്പ്‌ ഉള്ള ചായങ്ങള്‍.', '', NULL),
(589, 'Azoic', 'ഏസോയിക്‌', 'ഭൂമിയില്‍ ജീവന്‍ ഉത്ഭവിക്കുന്നതിനു മുമ്പുള്ള കാലഘട്ടത്തെ സൂചിപ്പിക്കുന്നു.', '', NULL),
(590, 'azulene', 'അസുലിന്‍', 'ഒരുതരം സസ്യത്തില്‍ നിന്നും ലഭിക്കുന്ന നീലനിറമുള്ള വര്‍ണ വസ്‌തു.', '', NULL),
(591, 'babs', 'ബാബ്‌സ്‌', 'Blind Approach Beacon System എന്നതിന്റെ ചുരുക്കം അന്ധസമീപന ബീക്കണ്‍ വ്യവസ്ഥ.', '', NULL),
(592, 'B-lymphocyte', 'ബി-ലിംഫ്‌ കോശം', 'അസ്ഥിമജ്ജയില്‍ നിന്ന്‌ സൃഷ്‌ടിക്കപ്പെടുന്ന ഒരുതരം ലിംഫ്‌ കോശങ്ങള്‍. ഇവയില്‍ നിന്നാണ്‌ രക്തത്തിലെ ആന്റിബോഡി ഉത്‌പാദിപ്പിക്കുന്ന പ്ലാസ്‌മാ കോശങ്ങള്‍ ഉണ്ടാകുന്നത്‌.', '', NULL),
(593, 'Babo\'s law', 'ബാബോ നിയമം', 'ഒരു ലേയം ഒരു ദ്രാവകത്തില്‍ ലയിക്കുമ്പോള്‍ ദ്രാവകത്തിന്റെ ബാഷ്‌പമര്‍ദം കുറയുമെന്ന്‌ പ്രസ്‌താവിക്കുന്ന നിയമം. ബാഷ്‌പമര്‍ദത്തില്‍ ഉണ്ടാകുന്ന കുറവ്‌ ലയിച്ച ലേയത്തിന്റെ അളവിന്‌ ആനുപാതികമായിരിക്കും.', '', NULL),
(594, 'Bacillariophyta', 'ബാസില്ലേറിയോഫൈറ്റ', 'ഡയാറ്റമുകള്‍ എന്നറിയപ്പെടുന്ന ഏകകോശ ആല്‍ഗകളുടെ വിഭാഗം.', '', NULL),
(595, 'bacillus', 'ബാസിലസ്‌', 'ദണ്ഡാകൃതിയുള്ള ബാക്‌ടീരിയങ്ങളുടെ പൊതുവായ പേര്‌. ഉദാ: ബാസിലസ്‌ തുറിഞ്ചന്‍സിസ്‌. ഇതില്‍ നിന്നാണ്‌ Bt ജീന്‍ കിട്ടുന്നത്‌.', '', NULL),
(596, 'back cross', 'പൂര്‍വ്വസങ്കരണം', 'ഒരു സങ്കരജീവിയും അതിന്റെ ജനകങ്ങളില്‍ ഒന്നുമായുള്ള സങ്കരണം. ഇതില്‍ തന്നെ ഗുപ്‌തസ്വഭാവമുള്ള ജനകവുമായുള്ള സങ്കരണത്തെ ടെസ്റ്റ്‌ ക്രാസ്‌ എന്ന്‌ പറയും.', '', NULL),
(597, 'back emf', 'ബാക്ക്‌ ഇ എം എഫ്‌', 'ഒരു പരിപഥത്തിലെ വൈദ്യുത പ്രവാഹത്തിന്‌ വ്യതിയാനം ഉണ്ടാവുമ്പോള്‍, ഈ വ്യതിയാനത്തെ എതിര്‍ക്കത്തക്ക വിധം പരിപഥത്തില്‍ പ്രരിതമാകുന്ന വിദ്യുത്‌ ചാലക ബലം.', '', NULL),
(598, 'back ground radiations', 'പരഭാഗ വികിരണങ്ങള്‍', '-', 'Big Bang', NULL),
(599, 'backing', 'ബേക്കിങ്‌', 'കാറ്റിന്റെ ദിശയില്‍ അപ്രദക്ഷിണമായുണ്ടാകുന്ന മാറ്റം. ഉദാ: വടക്കുനിന്ന്‌ വടക്കുപടിഞ്ഞാറോട്ട്‌.', '', NULL),
(600, 'backward reaction', 'പശ്ചാത്‌ ക്രിയ', 'ഒരു ഉഭയദിശാ പ്രവര്‍ത്തനത്തിലെ ഉത്‌പന്നങ്ങള്‍ പ്രതിപ്രവര്‍ത്തിച്ച്‌ അഭികാരകങ്ങള്‍ ഉണ്ടാകുന്ന പ്രവര്‍ത്തനം. ഉദാ: CO+H2O CO2+H2. ഈ പ്രവര്‍ത്തനത്തില്‍ അഭികാരകങ്ങളായ കാര്‍ബണ്‍ മോണോക്‌സൈഡും നീരാവിയും പ്രവര്‍ത്തിച്ച്‌ ഉത്‌പന്നങ്ങളായ കാര്‍ബണ്‍ ഡൈ ഓക്‌സൈഡും ഹൈഡ്രജനും ഉണ്ടാകുന്നത്‌ പുരോ പ്രവര്‍ത്തനം. ഈ ഉത്‌പന്നങ്ങള്‍ തമ്മില്‍ പ്രവര്‍ത്തിച്ച്‌ അഭികാരകങ്ങള്‍ ഉണ്ടാകുന്നത്‌ പശ്ചാത്‌ പ്രവര്‍ത്തനം.', '', NULL),
(601, 'bacteria', 'ബാക്‌ടീരിയ', 'ന്യൂക്ലിയസ്‌ ഇല്ലാത്ത ഒരുകൂട്ടം ഏകകോശ ജീവികള്‍. ഇവയില്‍ സ്വതന്ത്ര ജീവികളും പരാദങ്ങളും മൃതോപജീവികളും ഉണ്ട്‌. ദണ്ഡ്‌, സര്‍പ്പിളം, ഗോളം എന്നിങ്ങനെ വിവിധ ആകൃതിയില്‍ ഉണ്ട്‌. പലതും രോഗകാരികളാണ്‌. ജൈവാവശിഷ്‌ടങ്ങളുടെ ജീര്‍ണനത്തില്‍ ബാക്‌ടീരിയങ്ങള്‍ക്ക്‌ കാര്യമായ പങ്കുണ്ട്‌.', '', NULL),
(602, 'bacteriocide', 'ബാക്‌ടീരിയാനാശിനി', 'ബാക്‌ടീരിയങ്ങളെ നശിപ്പിക്കുന്ന പദാര്‍ഥം.', '', NULL),
(603, 'bacterio chlorophyll', 'ബാക്‌ടീരിയോ ക്ലോറോഫില്‍', 'chlorophyll', 'chlorophyll', NULL),
(604, 'Bacteriology', 'ബാക്‌ടീരിയാവിജ്ഞാനം', 'ബാക്‌ടീരിയങ്ങളെപ്പറ്റിയുള്ള പഠനം.', '', NULL),
(605, 'bacteriophage', 'ബാക്‌ടീരിയാഭോജി', 'ബാക്‌ടീരിയങ്ങളിലെ പരാദങ്ങളായ വൈറസുകള്‍. ഇവയ്‌ക്ക്‌ ബഹുഭുജങ്ങളുള്ള തലയും വാലും വാല്‍തന്തുക്കളുമുണ്ടായിരിക്കും. ബാക്‌ടീരിയത്തിന്റെ കോശഭിത്തിയില്‍ പറ്റിപ്പിടിച്ച്‌ വൈറസ്സിന്റെ ജനിതകപദാര്‍ഥം അകത്തേക്ക്‌ കുത്തിവെച്ചാണ്‌ സംക്രമിക്കുന്നത്‌.', '', NULL),
(606, 'badlands', 'ബേഡ്‌ലാന്റ്‌സ്‌', 'വരള്‍ച്ചയും സരന്ധ്രമായ മണ്ണും ഒരുമിച്ചുള്ള സ്ഥലം. ഇവിടെ മണ്ണൊലിപ്പ്‌ മൂലം ആഴമേറിയ താഴ്‌വരകളും ഉയര്‍ന്ന ചെങ്കുത്തായ കുന്നുകളും സാധാരണമാണ്‌.', '', NULL),
(607, 'baggasse', 'കരിമ്പിന്‍ചണ്ടി', 'താപവൈദ്യുതി ഉല്‍പ്പാദനത്തിനും സെല്ലുലോസ്‌ നാരുകള്‍ ഉണ്ടാക്കാനും ഉപയോഗിക്കുന്നു.', '', NULL),
(608, 'Baily\'s beads', 'ബെയ്‌ലി മുത്തുകള്‍', 'സൂര്യഗ്രഹണസമയത്ത്‌ സൂര്യബിംബം പൂര്‍ണമായി മറയുന്നതിന്‌ തൊട്ടുമുമ്പ്‌ ചുറ്റും പ്രകാശം മുത്തുകള്‍ പോലെ കാണപ്പെടുന്നത്‌. ചന്ദ്രന്റെ വക്കിലെ നിമ്‌ന തലങ്ങളില്‍ക്കൂടി പ്രകാശം വരുന്നതാണ്‌ ഇതിന്‌ കാരണം. ഈ പ്രതിഭാസത്തെ വിശദീകരിച്ച ഇംഗ്ലീഷ്‌ ശാസ്‌ത്രജ്ഞനായ ഫ്രാന്‍സിസ്‌ ബെയ്‌ലിയില്‍ നിന്നാണ്‌ ഈ പേര്‌.', '', NULL),
(609, 'baking Soda', 'അപ്പക്കാരം', 'സോഡിയം ഹൈഡ്രജന്‍ കാര്‍ബണേറ്റ്‌ അഥവാ സോഡിയം ബൈകാര്‍ബണേറ്റ്‌ ( NaHCO3). ബേക്കിംഗിന്‌ വ്യാപകമായി ഉപയോഗിക്കുന്നു. കാര്‍ബണ്‍ഡയോക്‌സൈഡിന്റെ നല്ല സ്രാതസ്സ്‌ ആയതുകൊണ്ട്‌ തീ കെടുത്താനും തുണി, ടാനിങ്‌, കടലാസ്‌ നിര്‍മാണം തുടങ്ങിയ മേഖലകളിലും ഉപയോഗിക്കപ്പെടുന്നു.', '', NULL),
(610, 'balanced equation', 'സമതുലിത സമവാക്യം', 'ദ്രവ്യ സംരക്ഷണ നിയമമനുസരിച്ച്‌ ഒരു രാസപ്രവര്‍ത്തനത്തില്‍ ദ്രവ്യം നശിക്കുകയോ പുതുതായി നിര്‍മിക്കപ്പെടുകയോ ചെയ്യുന്നില്ല. ഇതുപ്രകാരം ഒരു രാസസമവാക്യത്തില്‍ അഭികാരകങ്ങളുടെ എത്ര ആറ്റങ്ങള്‍ ഉണ്ടോ അത്രയും ആറ്റങ്ങള്‍ ഉല്‍പന്നങ്ങളിലും ഉണ്ടാവണം. ഇതനുസരിച്ച്‌ എഴുതുന്ന രാസസമവാക്യമാണ്‌ സമതുലിത സമവാക്യം. ഉദാ: H2 + O2  → H2O (രാസ സമവാക്യം) 2H2 + O2  → 2H2O (സമതുലിത സമവാക്യം)', '', NULL),
(611, 'ball clay', 'ബോള്‍ ക്ലേ', 'ഏറെ പ്ലാസ്‌തികമായ, ശുദ്ധമായ കളിമണ്ണ്‌. ചൈനാ കളിമണ്ണിന്റെ പുനഃപ്രവര്‍ത്തനം വഴി രൂപം കൊള്ളുന്നു. കുംഭാരന്മാരുടെ കളിമണ്ണ്‌ ( potters clay) എന്നും പറയും', '', NULL),
(612, 'ballistic galvanometer', 'ബാലിസ്റ്റിക്‌ ഗാല്‍വനോമീറ്റര്‍', 'ഒരു വൈദ്യുത അളവുപകരണം. ചാര്‍ജും ക്ഷണിക വൈദ്യുത പ്രവാഹവും അളക്കാന്‍ ഉപയോഗിക്കുന്നു.', '', NULL),
(613, 'ballistics', 'പ്രക്ഷേപ്യശാസ്‌ത്രം', 'മിസൈലുകള്‍, തോക്ക്‌, കവണബോംബ്‌, റോക്കറ്റ്‌ തുടങ്ങിയവയില്‍ നിന്നുള്ള പ്രക്ഷേപ്യങ്ങളുടെ ചലനത്തെയും സ്വഭാവത്തെയും സംബന്ധിച്ച ശാസ്‌ത്രം. ഇത്തരം പ്രക്ഷേപ്യങ്ങളുണ്ടാക്കുന്ന ഫലങ്ങളെക്കുറിച്ചുള്ള പഠനവും ഇതിന്റെ ഭാഗമാണ്‌. കൂടുതല്‍ ഫലപ്രദമായ ആയുധങ്ങള്‍ ഉണ്ടാക്കുവാനും വെടിമരുന്ന്‌ ആയുധങ്ങള്‍ കൊണ്ടുള്ള കുറ്റകൃത്യങ്ങള്‍ തെളിയിക്കുവാനും ഈ ശാസ്‌ത്രശാഖ സഹായകമാണ്‌.', '', NULL),
(614, 'ball mill', 'ബാള്‍മില്‍', 'പദാര്‍ഥങ്ങള്‍ പൊടിക്കാന്‍ ഉപയോഗിക്കുന്ന മില്‍. ഉള്‍വശത്ത്‌ കല്ല്‌ പാകിയ ഉരുക്ക്‌ വീപ്പയ്‌ക്കുള്ളില്‍ ഉരുക്കുഗോളങ്ങള്‍ നിറച്ചിരിക്കുന്നു. ഈ വീപ്പയിലെ ഗോളങ്ങള്‍ ഉരുണ്ടുമറിയുന്നതുവഴിയാണ്‌ പദാര്‍ഥങ്ങള്‍ പൊടിയുന്നത്‌.', '', NULL),
(615, 'ball lightning', 'അശനിഗോളം', 'ഗോളാകൃതിയിലുള്ള ഇടിമിന്നല്‍. വേഗത്തില്‍ സഞ്ചരിച്ചുകൊണ്ട്‌ ഒരു മിനുട്ട്‌ വരെ നിലനില്‍ക്കും.', '', NULL),
(616, 'balloon sonde', 'ബലൂണ്‍ സോണ്ട്‌', 'കാലാവസ്ഥാ നിരീക്ഷണത്തിനുള്ള ഉപകരണങ്ങളടങ്ങിയ ബലൂണ്‍. ഉപര്യന്തരീക്ഷത്തിലേക്കയക്കുന്ന ബലൂണ്‍ ക്രമേണ പൊട്ടുകയും വിവരങ്ങള്‍ ശേഖരിച്ച ഉപകരണങ്ങള്‍ പാരച്യൂട്ടുവഴി സാവധാനം താഴോട്ടിറങ്ങിവരുകയും ചെയ്യുന്നു. റേഡിയോ ട്രാന്‍സ്‌മിറ്ററുകളും ഇങ്ങനെ അയക്കാറുണ്ട്‌.', '', NULL),
(617, 'ball stone', 'ബോള്‍ സ്റ്റോണ്‍', 'അടരുകളില്ലാത്ത ചുണ്ണാമ്പുകല്‍ പടലം. പവിഴപ്പുറ്റുകോളനികളാണ്‌ ഇങ്ങനെ രൂപം കൊള്ളുന്നത്‌.', '', NULL),
(618, 'Balmer series', 'ബാമര്‍ ശ്രണി', 'ഹൈഡ്രജന്‍ ആറ്റത്തില്‍ ഇലക്‌ട്രാണുകള്‍ ഉയര്‍ന്ന ഊര്‍ജനിലകളില്‍ നിന്നും രണ്ടാമത്തെ ഊര്‍ജനിലയിലേക്ക്‌ പതിക്കുമ്പോള്‍ ഉണ്ടാകുന്ന സ്‌പെക്‌ട്ര രേഖകളുടെ ശ്രണി. ഈ ശ്രണിയിലെ ആവൃത്തികളെ 1/λ= R(1/22-1/n2) എന്ന പൊതു സമീകരണം കൊണ്ട്‌ സൂചിപ്പിക്കാം. R=റിഡ്‌ബര്‍ഗ്‌ സ്ഥിരാങ്കം, n-3, 4, 5..., λ =തരംഗദൈര്‍ഘ്യം.', '', NULL),
(619, 'band spectrum', 'ബാന്‍ഡ്‌ സ്‌പെക്‌ട്രം', '-', 'spectrum', NULL),
(620, 'banded structure', 'ബാന്റഡ്‌ സ്‌ട്രക്‌ചര്‍', 'ടെക്‌സ്‌ചറിലോ സംരചനയിലോ മാറ്റം വരുന്നതിലൂടെ സംഭവിക്കുന്ന, ആഗ്നേയശിലകളുടെയും കായാന്തരിത ശിലകളുടെയും ഘടന.', '', NULL),
(621, 'bandwidth', 'ബാന്‍ഡ്‌ വിഡ്‌ത്ത്‌', '1. ഒരു പ്രവര്‍ധകത്തിന്റെ പ്രവര്‍ധകക്ഷമത ആവൃത്തിയെ ആശ്രയിച്ചിരിക്കുന്നു. പ്രവര്‍ധകക്ഷമത I0 യില്‍ നിന്ന്‌ 0.707 I0ആയി കുറയുന്ന ആവൃത്തി f1ഉം f2ഉം ആണെങ്കില്‍ f2-f1 ആണ്‌ പ്രവര്‍ധകത്തിന്റെ ബാന്‍ഡ്‌ വിഡ്‌ത്‌. 2. ഒരു ആന്റിന ഏറ്റവും ഫലപ്രദമായി സ്വീകരിക്കുന്ന സിഗ്നല്‍ ഫ്രീക്വന്‍സി സംഘാതം. 3. interference നോക്കുക.', '', NULL),
(622, 'bar', 'ബാര്‍', 'മര്‍ദത്തിന്റെ സി ജി എസ്‌ ഏകകം. സാധാരണ അന്തരീക്ഷ മര്‍ദം 1.013 ബാര്‍ ആണ്‌. കൂടുതല്‍ പ്രചാരമുള്ള ഏകകം മില്ലിബാര്‍ ആണ്‌. 1 മില്ലിബാര്‍=10 -3 ബാര്‍.', '', NULL),
(623, 'bar eye', 'ബാര്‍ നേത്രം', 'പഴ ഈച്ചകളുടെ ചില ഇനങ്ങളില്‍ കാണുന്ന വരപോലെ ഇടുങ്ങിയ കണ്ണുകള്‍. X ക്രാമസോമിലുള്ള ഒരു ജീനിന്‌ മ്യൂട്ടേഷന്‍ വന്നാണ്‌ ഇതുണ്ടാവുന്നത്‌.', '', NULL),
(624, 'barbituric acid', 'ബാര്‍ബിട്യൂറിക്‌ അമ്ലം', 'മലോനൈല്‍യൂറിയ. വെളുത്തപൊടി. ഉരുകല്‍ നില 248 0 C. മരുന്നുകളും പ്ലാസ്റ്റിക്കുകളും നിര്‍മ്മിക്കാന്‍ ഉപയോഗിക്കുന്നു.', '', NULL),
(625, 'barbs', 'ബാര്‍ബുകള്‍', 'തൂവലിലെ നടുക്കുള്ള അക്ഷത്തിന്റെ ഇരുവശത്തേക്കുമായി ക്രമീകരിക്കപ്പെട്ടിരിക്കുന്ന നാരുകള്‍.', '', NULL),
(626, 'barbules', 'ബാര്‍ബ്യൂളുകള്‍', 'തൂവലിലെ ബാര്‍ബുകളു ടെ ഇരുവശത്തുമുള്ള കൊളുത്തുപോലെയുള്ള സൂക്ഷ്‌മങ്ങളായ നാരുകള്‍. അടുത്തടുത്ത ബാര്‍ബുകള്‍ ബാര്‍ബ്യൂളുകളുടെ സഹായത്താല്‍ കൂട്ടിക്കൊളുത്തുന്നതു വഴിയാണ്‌ തൂവലിന്‌ ദൃഢതയുണ്ടാകുന്നത്‌.', '', NULL),
(627, 'barchan', 'ബര്‍ക്കന്‍', 'ചന്ദ്രക്കലാകൃതിയിലുള്ള ഒറ്റപ്പെട്ട മണല്‍ക്കൂനകള്‍.', '', NULL),
(628, 'Barff process', 'ബാര്‍ഫ്‌ പ്രക്രിയ', 'ഇരുമ്പിനെ തുരുമ്പില്‍ നിന്ന്‌ രക്ഷിക്കാനായി ലോഹത്തെ നീരാവിയില്‍ ചൂടാക്കി Fe3O4 എന്ന ഓക്‌സൈഡിന്റെ ഒരു സ്‌തരം ഇരുമ്പിന്റെ ഉപരിതലത്തില്‍ രൂപപ്പെടുത്തുന്ന പ്രക്രിയ.', '', NULL),
(629, 'Barford test', 'ബാര്‍ഫോര്‍ഡ്‌ ടെസ്റ്റ്‌', 'ഒരു ലായനിയില്‍ മോണോസാക്കറൈഡിന്റെ സാന്നിധ്യം നിര്‍ണയിക്കുന്നതിന്‌ ഉപയോഗിക്കുന്ന ടെസ്റ്റ്‌. ടെസ്റ്റ്‌ ലായനിയിലേക്ക്‌ അസറ്റിക്‌ അമ്ലവും കോപ്പര്‍ അസറ്റേറ്റും ചേര്‍ത്ത്‌ ചൂടാക്കുമ്പോള്‍ കുപ്രസ്‌ ഓക്‌സൈഡിന്റെ ചുവന്ന അവക്ഷിപ്‌തം ഉണ്ടാകുന്നു.', '', NULL),
(630, 'barite', 'ബെറൈറ്റ്‌', 'BaSO4. പ്രകൃത്യാ ലഭിക്കുന്ന ബേരിയം സള്‍ഫേറ്റ്‌.', '', NULL),
(631, 'bark', 'വല്‍ക്കം', 'വേരുകളിലും കാണ്ഡത്തിലും സൈലത്തിന്‌ പുറമെയുള്ള പാളി.', '', NULL),
(632, 'barn', 'ബാണ്‍', 'ചെറിയ വിസ്‌തീര്‍ണം അളക്കാനുള്ള യൂണിറ്റ്‌. ഒരു ബാണ്‍=10 -28 m2.', '', NULL),
(633, 'baroreceptor', 'മര്‍ദഗ്രാഹി', 'മര്‍ദ വ്യത്യാസത്തോട്‌ പ്രതികരിക്കുന്ന സംവേദന കോശങ്ങള്‍.', '', NULL),
(634, 'barogram', 'ബാരോഗ്രാം', 'മര്‍ദാരേഖം. ബാരോഗ്രാഫില്‍ നിന്ന്‌ ലഭിക്കുന്ന ആരേഖം.', '', NULL),
(635, 'barograph', 'ബാരോഗ്രാഫ്‌', 'മര്‍ദത്തിന്റെ വ്യതിയാനങ്ങള്‍ ആരേഖം ചെയ്യുന്ന ബാരോമീറ്റര്‍.', '', NULL),
(636, 'barometer', 'ബാരോമീറ്റര്‍', 'മര്‍ദമാപി. അന്തരീക്ഷമര്‍ദമളക്കുവാന്‍ ഡിസൈന്‍ ചെയ്‌ത ഉപകരണം. ഉദാ: രസ ബാരോമീറ്റര്‍, അനിറോയ്‌ഡ്‌ ബാരോമീറ്റര്‍.', '', NULL),
(637, 'barometric pressure', 'ബാരോമെട്രിക്‌ മര്‍ദം', 'അന്തരീക്ഷ മര്‍ദം ( atmospheric pressure) എന്നതിന്‌ തുല്യമായ പ്രയോഗം.', '', NULL),
(638, 'barometric tide', 'ബാരോമെട്രിക്‌ ടൈഡ്‌', 'അന്തരീക്ഷ വേലി. സൂര്യചന്ദ്രന്മാരുടെ ആകര്‍ഷണം മൂലം അന്തരീക്ഷ മര്‍ദത്തിലുണ്ടാകുന്ന ദൈനംദിന വ്യതിയാനങ്ങള്‍.', '', NULL),
(639, 'barometry', 'ബാരോമെട്രി', 'മര്‍ദമാപന പഠനം, വിവിധ തരം ബാരോമീറ്ററുകളിലെ പിശകുകളും അവ തിരുത്തുന്ന മാര്‍ഗങ്ങളും ഉള്‍പ്പെടെ അന്തരീക്ഷ മര്‍ദത്തെക്കുറിച്ചുള്ള പഠനം.', '', NULL),
(640, 'barotoxis', 'മര്‍ദാനുചലനം', '-', '', NULL),
(641, 'Barr body', 'ബാര്‍ ബോഡി', 'പെണ്‍ സസ്‌തനി കോശങ്ങളുടെ കോശമര്‍മത്തില്‍ ഇന്റര്‍ ഫേസില്‍ ദൃശ്യമാകുന്ന ഒരു വസ്‌തു. ഇത്‌ സാന്ദ്രീകൃതാവസ്ഥയിലുള്ള ഒരു x ക്രാമസോം ആണ്‌. പുരുഷകോശങ്ങളില്‍ ഈ വസ്‌തുവില്ല. 1949 ല്‍ മുറെബാര്‍ ആണ്‌ ഇത്‌ കണ്ടെത്തിയത്‌. ടേണേഴ്‌സ്‌ സിന്‍ഡ്രാം ഉള്ള, ഒരു x മാത്രമുള്ള സ്‌ത്രീകളില്‍ ബാര്‍ബോഡി ഉണ്ടാവില്ല. അതേ സമയം ക്ലീന്‍ ഫെല്‍ടറുടെ സിന്‍ഡ്രാമുള്ള ആണുങ്ങളുടെ കോശത്തില്‍ ഒരു ബാര്‍ബോഡി ഉണ്ടാവാറുണ്ട്‌.', '', NULL),
(642, 'barrier reef', 'ബാരിയര്‍ റീഫ്‌', 'തീരത്തുനിന്നകലെ, തീരത്തിന്‌ ഏതാണ്ട്‌ സമാന്തരമായി സ്ഥിതി ചെയ്യുന്ന പവിഴപ്പുറ്റു നിരയാണ്‌ ബാരിയര്‍ റീഫ്‌.', '', NULL),
(643, 'bary centre', 'കേന്ദ്രകം', 'ഒരു നക്ഷത്ര ദ്വയ (binary star) ത്തിന്റെയോ നക്ഷത്ര-ഗ്രഹ സംവിധാനത്തിന്റെയോ പൊതുഭാരകേന്ദ്രം. ബാരിസെന്റര്‍ കേന്ദ്രമാക്കിയാണ്‌ അവ അന്യോന്യം കറങ്ങുന്നത്‌.', '', NULL),
(644, 'baryons', 'ബാരിയോണുകള്‍', 'അര്‍ധ സംഖ്യാ സ്‌പിന്‍ (1/2, 3/2, 5/2...) ഉള്ള ഹാഡ്രാണുകള്‍. ഉദാ: പ്രാട്ടോണ്‍, ന്യൂട്രാണ്‍, ലാംഡാ, സിഗ്‌മ, ഒമേഗ തുടങ്ങിയവ. എല്ലാ ബാരിയോണുകള്‍ക്കും ബാരിയോണ്‍ നമ്പര്‍ +1 ഉം പ്രതിബാരിയോണുകള്‍ക്ക്‌ -1 ഉം ആയിരിക്കും. എല്ലാ പ്രതിപ്രവര്‍ത്തനങ്ങളിലും ബാരിയോണ്‍ നമ്പര്‍ സംരക്ഷിക്കപ്പെടുന്നു.', '', NULL),
(645, 'barysphere', 'ബാരിസ്‌ഫിയര്‍', '1. ഭൂമിയുടെ അകക്കാമ്പ്‌. 2. മേന്റിലും അകക്കാമ്പുമടക്കം ഭമൗാന്തര്‍ഭാഗത്തെ പൊതുവേ പരാമര്‍ശിക്കാനും ഈ പദമുപയോഗിക്കാറുണ്ട്‌.', '', NULL),
(646, 'basal body', 'ബേസല്‍ വസ്‌തു', 'ഫ്‌ളാജല്ല, സീലിയ എന്നിവയുടെ അക്ഷതന്തുവുമായി ബന്ധപ്പെട്ടുകിടക്കുന്ന സെന്‍ട്രിയോളിന്റെ ഘടനയോടുകൂടിയ വസ്‌തു.', '', NULL),
(647, 'basal metabolic rate', 'അടിസ്ഥാന ഉപാപചയനിരക്ക്‌', 'BMR എന്ന്‌ ചുരുക്കരൂപം. രക്തചംക്രമണം, ശ്വസനം തുടങ്ങിയ ശരീര പ്രവര്‍ത്തനങ്ങള്‍ നിലനിര്‍ത്താന്‍ ആവശ്യമായ ഏറ്റവും കുറഞ്ഞ ഊര്‍ജവിനിയോഗ നിരക്ക്‌.', '', NULL),
(648, 'basalt', 'ബസാള്‍ട്ട്‌', 'നേര്‍ത്ത തരികളുള്ള ബഹിര്‍ജന്യ ആഗ്നേയശില. ഫെല്‍സ്‌പാറും പൈറോക്‌സിനുമാണ്‌ പ്രധാന ഘടകങ്ങള്‍. ഒലിവൈന്‍, മാഗ്നറ്റൈറ്റ്‌, അപറ്റൈറ്റ്‌ എന്നിവയും കാണാറുണ്ട്‌.', '', NULL),
(649, 'basanite', 'ബസണൈറ്റ്‌', 'പ്ലാജിയോക്ലാസ്‌, ഒഗൈറ്റ്‌, ഒലിവിന്‍ ഫോല്‍സ്‌പാഥയ്‌ഡ്‌ എന്നിവയടങ്ങിയ ഒരിനം ബാസാള്‍ട്ടികശില', '', NULL),
(650, 'base', 'ബേസ്‌', '1. (chem) അരീനിയസ്‌ സിദ്ധാന്തമനുസരിച്ച്‌ ജലത്തില്‍ ലയിക്കുമ്പോള്‍ ഹൈഡ്രാക്‌സൈഡ്‌ അയോണുകള്‍ ( OH_) നല്‌കുന്ന പദാര്‍ഥം. ഉദാ: KOH→ K++OH_ഈ OH_ അയോണുകള്‍ ആസിഡുകളില്‍ നിന്ന്‌ H+ അയോണുകള്‍ സ്വീകരിച്ച്‌ ജലമുണ്ടാക്കുന്നു. അതിനാല്‍ പ്രാട്ടോണ്‍ സ്വീകാരിയാണ്‌ ബേസ്‌ എന്നു പറയാം.', '', NULL),
(651, 'base', 'ആധാരം', '(maths) ജ്യാമിതീയ രൂപങ്ങളില്‍ നിര്‍ദിഷ്‌ട ക്രിയയ്‌ക്ക്‌ അടിസ്ഥാനമായെടുക്കുന്ന വശം. ഉദാ: ΔABC യില്‍ B യില്‍ നിന്നുള്ള ശീര്‍ഷ ലംബം പരിഗണിക്കുമ്പോള്‍ AC എന്ന വശമാണ്‌ ആധാരം. A ശീര്‍ഷമായെടുത്താല്‍ BC ആണ്‌ ആധാരം.', '', NULL),
(652, 'base', 'ബേസ്‌', '-', 'bipolar transistor', NULL),
(653, 'base hydrolysis', 'ക്ഷാരീയ ജലവിശ്ലേഷണം', 'ക്ഷാരീയ മാധ്യമത്തില്‍ നടത്തുന്ന ജലവിശ്ലേഷണം. ഉദാ: CH3−COOC2H5+NaOH →CH3−COONa+C2H5OH.', '', NULL),
(654, 'basement', 'ബേസ്‌മെന്റ്‌ ', '1. അവസാദ ശിലകളാല്‍ ആവരണം ചെയ്യപ്പെട്ടു കിടക്കുന്ന ആഗ്നേയ ശിലകളുടെയും കായാന്തരിത ശിലകളുടെയും വ്യൂഹം. 2. ഭൂവല്‍ക്കം എന്നും അര്‍ഥമുണ്ട്‌.', '', NULL),
(655, 'basic rock', 'അടിസ്ഥാന ശില', 'സിലിക്കയുടെ അംശം നന്നേ കുറഞ്ഞ ആഗ്നേയ ശില. ഉദാ: ബസാള്‍ട്ട്‌, ഗാബ്രാ.', '', NULL),
(656, 'basic slag', 'ക്ഷാരീയ കിട്ടം', 'ക്ഷാരസ്വഭാവമുള്ള ഫ്‌ളക്‌സ്‌ അപദ്രവ്യവുമായി ചേരുമ്പോള്‍ ഉണ്ടാകുന്ന കിട്ടം.', '', NULL),
(657, 'BASIC', 'ബേസിക്‌', 'ഒരു കംപ്യൂട്ടര്‍ പ്രാഗ്രാമിങ്‌ ഭാഷ. Beginners All Purpose Symbolic Instruction Code എന്നതിന്റെ ചുരുക്കം.', '', NULL),
(658, 'basicity', 'ബേസികത', 'അമ്ലത്തിന്റെ ഒരു തന്മാത്രയില്‍ നിന്ന്‌ ലഭ്യമാകുന്ന പ്രാട്ടോണുകളുടെ എണ്ണം. ഉദാ: സള്‍ഫ്യൂറിക്‌ അമ്ലത്തിന്റെ ബേസികത 2 ആണ്‌.', '', NULL),
(659, 'basidiomycetes', 'ബസിഡിയോമൈസെറ്റെസ്‌', 'ഒരിനം കുമിള്‍ വര്‍ഗം. ബസിഡിയോസ്‌പോറുകള്‍ വഴിയാണ്‌ ഇവയുടെ പ്രത്യുത്‌പാദനം.', '', NULL),
(660, 'basidium', 'ബെസിഡിയം', 'ബെസിഡിയോ മൈസെറ്റെസ്‌ ഫംഗസുകളില്‍ ബെസിഡിയോ സ്‌പോറുകള്‍ ഉണ്ടാകുന്ന ഗദാകാരമോ സിലിണ്ടറാകാരമോ ആയ ഹൈഫാകോശം. ബെസിഡിയത്തിലെ ചെറു വൃന്തങ്ങളായ സ്റ്റെറിഗ്മകളിലാണ്‌ ബെസിഡിയോ സ്‌പോറുകള്‍ ഉണ്ടാകുന്നത്‌.', '', NULL),
(661, 'basin', 'തടം', '1. ഭൂവല്‍ക്കത്തിലെ വിശാലമായ നിമ്‌നഭാഗം. ജലം നിറഞ്ഞ തടങ്ങള്‍ തടാക തടങ്ങളോ സമുദ്രതടങ്ങളോ ആയിരിക്കും. 2. നദികള്‍ പിന്‍വാങ്ങിയുണ്ടാകുന്ന തടങ്ങള്‍, നദീതടങ്ങള്‍.', '', NULL),
(662, 'basipetal', 'അധോമുഖം', 'സസ്യങ്ങളില്‍ മുകളില്‍ നിന്ന്‌ അടിഭാഗത്തേക്ക്‌ അനുക്രമമായിട്ടുള്ള വികാസം. ഇതു മൂലം പ്രായം കുറഞ്ഞ സസ്യാംഗങ്ങള്‍ അടിയിലും പ്രായം ചെന്നവ അഗ്രഭാഗത്തും കാണുന്നു.', '', NULL),
(663, 'bass', 'മന്ത്രസ്വരം', 'കീഴ്‌സ്ഥായി. സംഗീതത്തിലെ ഏറ്റവും താഴ്‌ന്ന സ്വരം.', '', NULL),
(664, 'bat', 'വവ്വാല്‍', 'പറക്കുന്ന ഒരിനം സസ്‌തനി. Chiroptera എന്ന ഓര്‍ഡറില്‍ പെടുന്നു.', '', NULL),
(665, 'batho chromatic shift', 'ബാത്തോക്രാമാറ്റിക്‌ ഷിഫ്‌റ്റ്‌', 'ഒരു സംയുക്തത്തിന്റെ പ്രതിദീപ്‌തി, തന്മാത്രയില്‍ ബാത്തോക്രാമിക്‌ ഗ്രൂപ്പ്‌ ഉള്ളതിനാല്‍ സ്‌പെക്‌ട്രത്തിന്റെ ചുവന്ന ഭാഗത്തേക്ക്‌ നീങ്ങുന്ന പ്രക്രിയ.', '', NULL),
(666, 'batholith', 'ബാഥോലിത്ത്‌', 'അന്തര്‍ജന്യ ആഗ്നേയശിലാപടലം. മിക്കവാറും ഗ്രാനൈറ്റ്‌ ആയിരിക്കും. ചുരുങ്ങിയത്‌ നൂറ്‌ ചതുരശ്ര കിലോമീറ്ററെങ്കിലും വിസ്‌താരമുണ്ടായിരിക്കും. പര്‍വതനം നടക്കുന്ന മേഖലകളില്‍ ഫലകങ്ങള്‍ താഴോട്ടിറങ്ങുന്ന സ്ഥലങ്ങളിലാണ്‌ ഇത്‌ കാണപ്പെടുന്നത്‌. bathylith എന്നും പറയും.', '', NULL),
(667, 'bath salt', 'സ്‌നാന ലവണം', 'ജലത്തിന്റെ കാഠിന്യം കുറയ്‌ക്കാനായി ചേര്‍ക്കുന്ന സോഡിയം ലവണം.', '', NULL),
(668, 'bathymetry', 'ആഴമിതി', 'ജലാശയങ്ങളുടെ, പ്രത്യേകിച്ച്‌ സമുദ്രങ്ങളുടെ ആഴമളക്കല്‍.', '', NULL),
(669, 'bathyscaphe', 'ബാഥിസ്‌കേഫ്‌', 'സമുദ്രനിമഗ്ന വാഹനം. ഇതുപയോഗിച്ചാണ്‌ 1960 ല്‍ ശാന്തസമുദ്രത്തിന്റെ അടിത്തട്ടിലെ 10916 മീറ്റര്‍ ആഴമുള്ള മറിയാനാ കിടങ്ങില്‍ ആദ്യമായി പര്യവേക്ഷണം നടത്തിയത്‌.', '', NULL),
(670, 'bathysphere', 'ബാഥിസ്‌ഫിയര്‍', 'സമുദ്രനിമഗ്ന ഗോളം. സമുദ്രത്തിന്റെ അടിത്തട്ടില്‍ പര്യവേക്ഷണം നടത്തുന്നതിനുള്ള ഗോളപേടകം. രണ്ടുപേര്‍ക്ക്‌ പരീക്ഷണ ഉപകരണമടക്കം ഇതിനുള്ളില്‍ പ്രവര്‍ത്തിക്കാം.', '', NULL),
(671, 'battery', 'ബാറ്ററി', 'വൈദ്യുതപരമായി പരസ്‌പരം ബന്ധിപ്പിച്ചിരിക്കുന്ന സെല്ലുകളുടെ ഒരു യൂണിറ്റ്‌.', '', NULL),
(672, 'bauxite', 'ബോക്‌സൈറ്റ്‌', 'അലൂമിനിയം ഹൈഡ്രാക്‌സൈഡും കളിമണ്ണും ചേര്‍ന്ന ഖനിജം. അലൂമിനിയത്തിന്റെ പ്രധാന അയിരാണ്‌ ഇത്‌.', '', NULL),
(673, 'bay', 'ഉള്‍ക്കടല്‍', 'സമുദ്രത്തില്‍ നിന്ന്‌ ഉള്ളോട്ട്‌ തള്ളിനില്‍ക്കുന്ന വിസ്‌തൃതവും തുറന്നതുമായ ഭാഗം. അല്ലെങ്കില്‍ തീരത്തോട്‌ ചേര്‍ന്നു നില്‍ക്കുന്ന കടലോ തടാകമോ ആകാം.', '', NULL),
(674, 'BCG', 'ബി. സി. ജി', 'Bacillus Calmette Guerin എന്നതിന്റെ ചുരുക്കം. ക്ഷയരോഗത്തിന്‌ കാരണമായ ഒരിനം ട്യൂബര്‍ക്കിള്‍ ബാസിലസ്‌. ക്ഷയരോഗത്തിനെതിരായ വാക്‌സിന്‍ തയ്യാറാക്കാന്‍ ഉപയോഗിക്കുന്നു.', '', NULL),
(675, 'Bacillus Calmette Guerin', 'ട്യൂബര്‍ക്കിള്‍ ബാസിലസ്', 'ക്ഷയരോഗത്തിന്‌ കാരണമായ ഒരിനം ട്യൂബര്‍ക്കിള്‍ ബാസിലസ്‌. ക്ഷയരോഗത്തിനെതിരായ വാക്‌സിന്‍ തയ്യാറാക്കാന്‍ ഉപയോഗിക്കുന്നു.', '', NULL),
(676, 'beach', 'ബീച്ച്‌', 'കടല്‍ത്തീരം, കടലിലെ ജലവിതാനത്തിന്റെ ഉയര്‍ന്നതും താഴ്‌ന്നതുമായ വിതാനത്തിനിടയിലുള്ള മണലും ചരലും നിറഞ്ഞ കടല്‍ ത്തീരം.', '', NULL),
(677, 'beat', 'വിസ്‌പന്ദം', 'സമീപ ആവൃത്തികളിലുള്ള രണ്ടു ശബ്‌ദങ്ങള്‍ ഒന്നിച്ചു പുറപ്പെടുവിച്ചാല്‍ ഉച്ചതയില്‍ ആവര്‍ത്തിച്ചുണ്ടാകുന്ന ഏറ്റക്കുറച്ചില്‍.', '', NULL),
(678, 'beaufort\'s scale', 'ബ്യൂഫോര്‍ട്‌സ്‌ തോത്‌', 'കാറ്റിന്റെ വേഗത അളക്കുന്നതിനുള്ള തോത്‌. ഏകക പ്രതലവിസ്‌തൃതിയില്‍ കാറ്റ്‌ ചെലുത്തുന്ന ശക്തിയുടെ അളവനുസരിച്ച്‌ 1 (ശാന്തം) മുതല്‍ 12 (അതിശക്തിയുള്ള കൊടുങ്കാറ്റ്‌) വരെയുള്ള സംഖ്യകള്‍കൊണ്ട്‌ സൂചിപ്പിക്കുന്നു. 5 ഇളംകാറ്റിനെ സൂചിപ്പിക്കുന്നു.', '', NULL),
(679, 'beaver', 'ബീവര്‍', 'റോഡന്റ്‌ വിഭാഗത്തില്‍പ്പെടുന്ന ഒരു സസ്‌തനി. ജലാശയങ്ങള്‍ക്കടുത്തുള്ള ചെറുമരങ്ങള്‍ മുറിച്ച്‌ അണകെട്ടി വെള്ളം തടഞ്ഞുനിര്‍ത്തുന്ന സ്വഭാവം കാണിക്കുന്നു.', '', NULL),
(680, 'Beckmann thermometer', 'ബെക്ക്‌മാന്‍ തെര്‍മോമീറ്റര്‍', 'താപനിലയിലുള്ള ചെറിയ അന്തരങ്ങള്‍ അളക്കാനുപയോഗിക്കുന്ന തെര്‍മോമീറ്റര്‍.', '', NULL),
(681, 'Becquerel', 'ബെക്വറല്‍', 'റേഡിയോ ആക്‌റ്റീവതയുടെ SI ഏകകം. ഒരു റേഡിയോ ആക്‌ടീവ്‌ സാമ്പിളില്‍ ഓരോ സെക്കന്‍ഡിലും ഓരോ അണുകേന്ദ്രത്തിന്‌ ക്ഷയം സംഭവിക്കുന്നുവെങ്കില്‍ റേഡിയോ ആക്‌റ്റീവത ഒരു ബെക്വറല്‍ ആണ്‌. പ്രതീകം Bq.', '', NULL),
(682, 'beetle', 'വണ്ട്‌', 'കട്ടിയേറിയ ഒരു ജോഡി മുന്‍ ചിറകുകളുള്ള ഷഡ്‌പദം.', '', NULL),
(683, 'bel', 'ബെല്‍', 'രണ്ടു ശബ്‌ദതരംഗങ്ങളുടെ തീവ്രത ( P1, P2) താരതമ്യം ചെയ്യാനുള്ള ഏകകം. P1, P2 എന്നിവയ്‌ക്കിടയിലെ ശബ്‌ദതീവ്രതാവ്യത്യാസം N ബെല്‍ എങ്കില്‍ N=log (P2 / P1) എന്ന്‌ നിര്‍വചിച്ചിരിക്കുന്നു. വൈദ്യുത സിഗ്നലിന്റെ പവര്‍ നില താരതമ്യം ചെയ്യുവാനും ഈ ഏകകം ഉപയോഗിക്കുന്നു. കൂടുതല്‍ പ്രചാരത്തിലുള്ളത്‌ ഡെസിബല്‍ ആണ്‌. 10 ഡെസിബല്‍=1 ബെല്‍.', '', NULL),
(684, 'beneficiation', 'ശുദ്ധീകരണം', 'അയിരുകളില്‍ നിന്ന്‌ വിലയേറിയ ഘടകങ്ങളും പാഴ്‌വസ്‌തുക്കളും വേര്‍തിരിച്ചെടുക്കുന്ന പ്രക്രിയ.', '', NULL),
(685, 'benthos', 'ബെന്തോസ്‌', 'കടലിനടിത്തട്ടില്‍ ജീവിക്കുന്ന ജീവജാലങ്ങള്‍.', '', NULL),
(686, 'benzene sulphonic acid', 'ബെന്‍സീന്‍ സള്‍ഫോണിക്‌ അമ്ലം', 'ക്രിസ്റ്റലീയഖരം. ഉരുകല്‍നില 52.50C.ജലത്തില്‍ ലയിക്കും. ഉല്‍പ്രരകമായും പല കാര്‍ബണിക സംശ്ലേഷണങ്ങളില്‍ അഭികാരകമായും ഉപയോഗിക്കുന്നു.', '', NULL),
(687, 'benzidine', 'ബെന്‍സിഡീന്‍', 'ഒരു അരോമാറ്റിക അമീന്‍. ഉരുകല്‍ നില 128 0 c', '', NULL),
(688, 'benzine', 'ബെന്‍സൈന്‍', 'പെട്രാളിയത്തില്‍ നിന്ന്‌ ലഭിക്കുന്ന ഒരു കൂട്ടം ഹൈഡ്രാകാര്‍ബണുകള്‍. ലായകമായി ഉപയോഗിക്കുന്നു.', '', NULL),
(689, 'benzoate', 'ബെന്‍സോയേറ്റ്‌', 'ബെന്‍സോയിക്‌ അമ്ലത്തിന്റെ ലവണം.', '', NULL),
(690, 'benzonitrile', 'ബെന്‍സോ നൈട്രല്‍', 'C6H5-CN. നിറമില്ലാത്ത വിഷദ്രാവകം.', '', NULL),
(691, 'benzopyrene', 'ബെന്‍സോ പൈറിന്‍', 'C20H12. കോള്‍ടാറില്‍ ദുര്‍ല്ലഭമായി കാണുന്ന ഒരു ബഹുസംവൃത ഹൈഡ്രാ കാര്‍ബണ്‍.', '', NULL),
(692, 'benzoyl', 'ബെന്‍സോയ്‌ല്‍', 'സംയോജകത 1 ആയിട്ടുള്ള ഒരു റാഡിക്കല്‍.', '', NULL),
(693, 'benzyl alcohol', 'ബെന്‍സൈല്‍ ആല്‍ക്കഹോള്‍', 'C6H5-CH2-OH. നിറമില്ലാത്ത ഒരു അരോമാറ്റിക ദ്രാവകം. സന്ദൗര്യ വസ്‌തുക്കളുടെ നിര്‍മ്മാണത്തില്‍ ലായകമായി ഉപയോഗിക്കുന്നു.', '', NULL),
(694, 'benzylidine chloride', 'ബെന്‍സിലിഡീന്‍ ക്ലോറൈഡ്‌', 'C6H5-CHCl2. നിറമില്ലാത്ത എണ്ണ രൂപത്തിലുള്ള ദ്രാവകം. ചായങ്ങളുടെ നിര്‍മാണത്തിന്‌ ഉപയോഗിക്കുന്നു.', '', NULL),
(695, 'Bergius process', 'ബെര്‍ജിയസ്‌ പ്രക്രിയ', 'കരിയില്‍ നിന്ന്‌ ഹൈഡ്രാകാര്‍ബണ്‍ മിശ്രിതങ്ങള്‍ ഉത്‌പാദിപ്പിക്കുന്ന പ്രക്രിയ. പൊടിച്ച കരിയും ടാറും അടങ്ങിയ മിശ്രിതം അയേണ്‍ ഓക്‌സൈഡിന്റെ സാന്നിധ്യത്തില്‍ 200 അന്തരീക്ഷ മര്‍ദ്ദത്തില്‍ ഹൈഡ്രജനെ 450 0 c വരെ തപിപ്പിക്കുന്നതാണ്‌ ഈ പ്രക്രിയ.', '', NULL),
(696, 'berry', 'ബെറി', 'ഒരിനം മാംസള ഫലം. ഫലകഞ്ചുകത്തിലെ പുറംതൊലി ഒഴികെയുള്ള ഭാഗം മാംസളമായ ഇതിനുള്ളില്‍ സാധാരണ ഒന്നിലധികം വിത്തുകളുണ്ടായിരിക്കും. ഉദാ: തക്കാളി, പേരക്ക, മുന്തിരിങ്ങ.', '', NULL),
(697, 'beta iron', 'ബീറ്റാ അയേണ്‍', '700 0 c നും 910 0 c നും ഇടയിലുള്ള താപനിലയില്‍ സ്ഥിരത്വമുള്ള ശുദ്ധ ഇരുമ്പിന്റെ ഒരു അപരരൂപം.', '', NULL),
(698, 'beta rays', 'ബീറ്റാ കിരണങ്ങള്‍', 'റേഡിയോ ആക്‌റ്റീവ്‌ പദാര്‍ഥങ്ങളുടെ വിഘടന ഫലമായി ഉണ്ടാകുന്ന ഇലക്‌ട്രാണ്‍/പോസിട്രാണ്‍ ധാരയ്‌ക്ക്‌ പൊതുവേ പറയുന്ന പേര്‍.', '', NULL),
(699, 'betatron', 'ബീറ്റാട്രാണ്‍', 'ഇലക്‌ട്രാണുകളെ ഉന്നതോര്‍ജത്തിലെത്തിക്കുവാനുള്ള ത്വരിത്രം. വൃത്താകാരത്തിലുള്ള ഒരു ട്യൂബിലേക്ക്‌ ഇലക്‌ട്രാണ്‍ പ്രവേശിക്കുന്നു. ഈ ട്യൂബ്‌ വര്‍ധിച്ചുകൊണ്ടിരിക്കുന്ന ഒരു കാന്തിക മണ്ഡലത്തിലായിരിക്കും. ഈ കാന്തിക മണ്ഡലമാണ്‌ ഇലക്‌ട്രാണുകളെ ത്വരിപ്പിക്കുന്നതും വര്‍ത്തുളപഥത്തില്‍ നിര്‍ത്തുന്നതും.', '', NULL),
(700, 'Betelgeuse', 'തിരുവാതിര', 'ഒറിയോണ്‍ നക്ഷത്ര മണ്ഡലത്തിലെ ചുവന്ന മഹാഭീമന്‍ നക്ഷത്രം. ഈ നക്ഷത്രത്തിനടുത്ത്‌ ചന്ദ്രന്‍ നില്‍ക്കുന്ന നാളാണ്‌ തിരുവാതിരനാള്‍.', '', NULL),
(701, 'bias', 'ബയാസ്‌', 'ഒരു പ്രത്യേക ധര്‍മ്മം നിര്‍വഹിക്കുവാന്‍ ഇലക്‌ട്രാണിക്‌ ഉപകരണത്തിന്റെ ഇലക്‌ട്രാഡുകള്‍ക്കിടയില്‍ പ്രയോഗിക്കുന്ന പൊട്ടന്‍ഷ്യല്‍. ഉദാ: ഒരു പി എന്‍ സന്ധിയിലൂടെ വൈദ്യുതി ഒഴുകുന്ന വിധത്തില്‍ ഇലക്‌ട്രാഡുകളെ ബയെസ്‌ ചെയ്യുന്നതാണ്‌ മുന്നോക്ക ബയെസ്‌. ഒരു പി.എന്‍ സന്ധിയിലൂടെ വൈദ്യുതി പ്രവാഹത്തെ തടയുന്ന വിധത്തില്‍ ഇലക്‌ട്രാഡുകളെ ബയസ്‌ ചെയ്യുന്നതാണ്‌ പിന്നോക്ക ബയസ്‌.', '', NULL),
(702, 'biaxial', 'ദ്വി അക്ഷീയം', 'രണ്ട്‌ അക്ഷങ്ങളുള്ള. ഉദാ: ദ്വി അക്ഷീയ ക്രിസ്റ്റല്‍.', '', NULL),
(703, 'biconcave lens', 'ഉഭയാവതല ലെന്‍സ്‌', '-', 'lens', NULL),
(704, 'biconvex lens', 'ഉഭയോത്തല ലെന്‍സ്‌', '-', 'lens', NULL),
(705, 'bicuspid valve', 'ബൈകസ്‌പിഡ്‌ വാല്‍വ്‌', '-', 'mitral valve', NULL),
(706, 'biennial plants', 'ദ്വിവര്‍ഷ സസ്യങ്ങള്‍', 'ജീവിത കാലഘട്ടം രണ്ട്‌ വര്‍ഷം കൊണ്ട്‌ തീരുന്ന സസ്യങ്ങള്‍. ഉദാ: ക്യാരട്ട്‌, കാബേജ്‌. ഇവ ആദ്യവര്‍ഷങ്ങളില്‍ വളര്‍ന്നുവലുതായി ആഹാര സാധനങ്ങള്‍ സംഭരിച്ച്‌ വയ്‌ക്കുന്നു. രണ്ടാമത്തെ വര്‍ഷത്തില്‍ പൂക്കളും ഫലങ്ങളും ഉണ്ടാകുന്നു.', '', NULL),
(707, 'Big bang', 'മഹാവിസ്‌ഫോടനം', 'പ്രപഞ്ചോത്‌പത്തിയെ സംബന്ധിച്ച ഒരു സിദ്ധാന്തം. പദാര്‍ത്ഥത്തിന്റെ അനന്തമായ മര്‍ദ,താപ,സാന്ദ്ര അവസ്ഥയിലുണ്ടായ ഒരു പൊട്ടിത്തെറിയെ തുടര്‍ന്നാണ്‌ ഈ പ്രപഞ്ചം ഉണ്ടായത്‌. ഏതാണ്ട്‌ 1370 കോടി വര്‍ഷങ്ങള്‍ക്കുമുമ്പ്‌ ഉണ്ടായ ഈ മഹാവിസ്‌ഫോടനത്തെ തുടര്‍ന്ന്‌ പ്രപഞ്ചം വികസിക്കുവാന്‍ തുടങ്ങി. അതോടെ താപനില താഴ്‌ന്നു തുടങ്ങി. പ്രപഞ്ചത്തിന്റെ ഇപ്പോഴത്തെ താപനില ശരാശരി 2.7Kആണ്‌ എന്ന്‌ കണ്ടെത്തിയിട്ടുണ്ട്‌. ഈ താപനിലയുമായി ബന്ധപ്പെട്ട വികിരണമാണ്‌ പരഭാഗ വികിരണം.', '', NULL),
(708, 'Big Crunch', 'മഹാപതനം', 'പ്രപഞ്ചാന്ത്യത്തിന്റെ സാധ്യതകളിലൊന്ന്‌. പ്രപഞ്ചത്തിന്റെ ശരാശരി സാന്ദ്രത ഒരു ക്രാന്തിക മൂല്യത്തില്‍ കൂടുതലാണെങ്കില്‍ ഗുരുത്വബലം മൂലം പ്രപഞ്ചവികാസ നിരക്ക്‌ കുറഞ്ഞുവരികയും ഒടുവില്‍ നിലയ്‌ക്കുകയും പിന്നീട്‌ സങ്കോചിക്കാന്‍ തുടങ്ങുകയും ചെയ്യും. പ്രപഞ്ചം മുഴുവന്‍ ഞെരുങ്ങിയമര്‍ന്ന്‌ ഒരു ബിന്ദുവിലേക്ക്‌ - മഹാസ്‌ഫോടനത്തിന്റെ പ്രാരംഭാവസ്ഥയിലേക്ക്‌ പതിക്കുകയും ചെയ്യും. എന്നാല്‍, ഈ സാധ്യതയെ പാടെ നിരാകരിക്കുന്നതാണ്‌ സമീപകാല നിരീക്ഷണഫലങ്ങള്‍. പ്രപഞ്ചവികാസ നിരക്ക്‌ കുറയുന്നില്ലെന്നു മാത്രമല്ല വര്‍ധിച്ചുവരികയുമാണ്‌.', '', NULL),
(709, 'bilabiate', 'ദ്വിലേബിയം', 'രണ്ടു ചുണ്ടുകളുള്ള പുഷ്‌പദളങ്ങളെ വിശേഷിപ്പിക്കുന്ന പദം. ഉദാ: തുമ്പപ്പൂവ്‌.', '', NULL),
(710, 'bilateral symmetry', 'ദ്വിപാര്‍ശ്വസമമിതി', 'ശരീരത്തിന്റെ അപാക്ഷ ( dorsal)ത്തിന്റെയും അധഃസ്ഥ ( ventral) ത്തിന്റെയും മധ്യത്തിലൂടെ വിഭജിച്ചാല്‍ മാത്രം രണ്ട്‌ സമഭാഗങ്ങള്‍ ലഭിക്കുന്ന ശരീരസമമിതി. ഉയര്‍ന്ന ജീവികള്‍ അധികവും ദ്വിപാര്‍ശ്വസമമിതി കാണിക്കുന്നു. ഉദാ: മത്സ്യങ്ങള്‍, സസ്‌തനികള്‍.', '', NULL),
(711, 'bile', 'പിത്തരസം', 'കരളിലെ കോശങ്ങള്‍ ഉല്‍പ്പാദിപ്പിക്കുന്ന സ്രവം. ഇതിലടങ്ങിയ ലവണങ്ങള്‍ കുടലിലെ ഡുവോഡിനത്തിലെ കൊഴുപ്പുകളുടെ ദഹനത്തെ സഹായിക്കുന്നു. പിത്തരസത്തില്‍ ഹീമോഗ്ലോബിന്റെ വിഘടനത്തില്‍ നിന്നുണ്ടാകുന്ന ബിലിറൂബിന്‍ എന്ന വര്‍ണകം ഉണ്ടായിരിക്കും.', '', NULL),
(712, 'bile duct', 'പിത്തവാഹിനി', 'പിത്തരസത്തെ പിത്താശയത്തില്‍ നിന്ന്‌ ഡുവോഡിനത്തിലേക്ക്‌ നയിക്കുന്ന നാളി.', '', NULL),
(713, 'bilirubin', 'ബിലിറൂബിന്‍', 'പിത്തരസത്തിലടങ്ങിയ മഞ്ഞവര്‍ണകം. മഞ്ഞപ്പിത്തം എന്ന രോഗം വരുമ്പോള്‍ രക്തത്തിലും മൂത്രത്തിലും ഇതിന്റെ അളവ്‌ കൂടുന്നു.', '', NULL),
(714, 'billion', 'നൂറുകോടി', 'ബ്രിട്ടീഷ്‌ സമ്പ്രദായമനുസരിച്ച്‌ ഒരു ബില്യണ്‍=1 ലക്ഷം കോടി ആണ്‌.', '', NULL),
(715, 'bimolecular', 'ദ്വിതന്മാത്രീയം', 'രണ്ട്‌ അഭികാരക തന്മാത്രകള്‍ തമ്മില്‍ പ്രതിപ്രവര്‍ത്തനം നടക്കുന്ന ഒരു രാസ അഭിക്രിയ. ഉദാ: 2HI→H2+I2.', '', NULL),
(716, 'binary acid', 'ദ്വയാങ്ക അമ്ലം', 'അമ്ല ഹൈഡ്രജന്‍ അണുക്കള്‍ ഓക്‌സിജന്‍ അല്ലാത്ത മറ്റേതെങ്കിലും അണുവിനോട്‌ ഘടിപ്പിച്ചിട്ടുള്ള അമ്ലം.', '', NULL),
(717, 'binary compound', 'ദ്വയാങ്ക സംയുക്തം', 'രണ്ട്‌ മൂലകങ്ങള്‍ മാത്രം അടങ്ങിയ സംയുക്തം.', '', NULL),
(718, 'binary digit', 'ദ്വയാങ്ക അക്കം', '-', 'bit ', NULL),
(719, 'binary fission', 'ദ്വിവിഭജനം', 'ഏകകോശ ജീവികളുടെ പ്രത്യുത്‌പാദന രീതി. ശരീരം രണ്ടായി വിഭജിച്ച്‌ ഓരോ ഖണ്ഡവും അതേ ജീവിയായി മാറുന്നു. ഉദാ: അമീബ, പാരമേസിയം എന്നിവയുടെ വിഭജനം.', '', NULL),
(720, 'binary number system', 'ദ്വയാങ്ക സംഖ്യാ പദ്ധതി', '0, 1 എന്നീ രണ്ടു പ്രതീകങ്ങള്‍ മാത്രമുപയോഗിക്കുന്ന സംഖ്യാ പദ്ധതി. ഈ പദ്ധതിയിലുള്ള സംഖ്യയ്‌ക്ക്‌ ദ്വയാങ്ക സംഖ്യ എന്നു പറയുന്നു. ഡിജിറ്റല്‍ സാങ്കേതിക വിദ്യയുടെ അടിസ്ഥാനം ഈ സംഖ്യാ സമ്പ്രദായമാണ്‌.', '', NULL),
(721, 'binary operation', 'ദ്വയാങ്കക്രിയ', 'പൊതുവേ രണ്ട്‌ രാശികള്‍ തമ്മിലുള്ള ക്രിയ. ഒരു ഗണത്തിലെ രണ്ട്‌ അംഗങ്ങള്‍ തമ്മില്‍ ചെയ്യുന്ന ക്രിയയുടെ ഫലം അതേ ഗണത്തിലെ അംഗമാണെങ്കില്‍ പ്രസ്‌തുത ക്രിയ ദ്വയാങ്കക്രിയയാണെന്ന്‌ പറയാം. ഉദാ: സങ്കലനം, വ്യവകലനം, ഗുണനം.', '', NULL),
(722, 'binary star', 'ഇരട്ട നക്ഷത്രം', 'ദ്വന്ദ്വ താരം, ഒരു പൊതുകേന്ദ്രത്തെ ആധാരമാക്കി കറങ്ങുന്ന ഇരട്ട നക്ഷത്രങ്ങള്‍. ഉദാ: ആല്‍ഫാ സെന്റോറി Aയും B യും.', '', NULL),
(723, 'binary vector system', 'ബൈനറി വെക്‌റ്റര്‍ വ്യൂഹം', 'ക്ലോണ്‍ ചെയ്‌ത ജീനുകളടങ്ങിയ T-DNA ഭാഗം സസ്യകോശങ്ങളിലെത്തിക്കാനുപയോഗിക്കുന്ന അഗ്രാബാക്‌റ്റീരിയത്തിന്റെ രണ്ടു പ്ലാസ്‌മിഡുകളടങ്ങിയ വ്യൂഹം. ഒരു പ്ലാസ്‌മിഡില്‍ വിറുലന്‍സ്‌ ( virulence) ജീനുകളും മറ്റേതില്‍ സന്നിവേശിപ്പിച്ച T-DNA ഭാഗവും ഉണ്ടാവും.', '', NULL),
(724, 'binding energy', 'ബന്ധനോര്‍ജം', '1. അണു കേന്ദ്രത്തിനകത്ത്‌ പ്രാട്ടോണുകളെയും ന്യൂട്രാണുകളെയും ബന്ധിച്ചു നിര്‍ത്തുന്ന ഊര്‍ജം. ന്യൂക്ലിയോണുകള്‍ കൂടിച്ചേര്‍ന്ന്‌ ന്യൂക്ലിയസ്‌ രൂപം കൊള്ളുമ്പോള്‍ അവയുടെ മൊത്തം ദ്രവ്യമാനത്തിന്റെ ചെറിയ ഒരംശം ഊര്‍ജമായി മാറുന്നു. ദ്രവ്യമാനത്തില്‍ വരുന്ന കുറവാണ്‌ ദ്രവ്യമാനനഷ്‌ടം. ഇതാണ്‌ ബന്ധനോര്‍ജത്തിന്‌ കാരണം. 2. അണുകേന്ദ്രത്തിന്‌ പുറത്ത്‌ നിശ്ചിത ഓര്‍ബിറ്റലുകളിലുള്ള ഇലക്‌ട്രാണുകളുടെ ഊര്‍ജനില. ഒരു ഇലക്‌ട്രാണ്‍ ഉയര്‍ന്ന ഓര്‍ബിറ്റലില്‍ നിന്ന്‌ താഴ്‌ന്ന ഓര്‍ബിറ്റലിലേക്ക്‌ സംക്രമിക്കുമ്പോള്‍ ഈ ഓര്‍ബിറ്റലുകളിലെ ഊര്‍ജവ്യത്യാസം വികിരണമായി ഉത്സര്‍ജിക്കപ്പെടുന്നു.', '', NULL),
(725, 'binding process', 'ബന്ധന പ്രക്രിയ', 'കാരിരുമ്പില്‍ നിന്ന്‌ ഉരുക്ക്‌ നിര്‍മിക്കാനുള്ള ഒരു മാര്‍ഗം.', '', NULL),
(726, 'binocular vision', 'ദ്വിനേത്ര വീക്ഷണം', 'ഒരു വസ്‌തുവിന്റെ പ്രതിബിംബം രണ്ടു കണ്ണുകളിലും ഒരേ സമയത്ത്‌ രൂപപ്പെടുന്നതു വഴി ഉണ്ടാകുന്ന ത്രിമാന ദൃശ്യാനുഭവം. മനുഷ്യനും മറ്റു പ്രമേറ്റുകള്‍ക്കും മൂങ്ങയെപോലുള്ള ചില പക്ഷികള്‍ക്കും ഈ കഴിവുണ്ട്‌.', '', NULL),
(727, 'binomial', 'ദ്വിപദം', 'രണ്ടു പദങ്ങള്‍ മാത്രമുള്ള ബഹുപദം. ഉദാ: 2 x+5y; x-y', '', NULL),
(728, 'binomial coefficients', 'ദ്വിപദ ഗുണോത്തരങ്ങള്‍', '-', 'binomial theorem', NULL),
(729, 'binomial nomenclature', 'ദ്വിനാമ പദ്ധതി', 'ഓരോ ജീവിയെയും രണ്ടു ഭാഗങ്ങളുള്ള ഒരു പേരുകൊണ്ട്‌ സൂചിപ്പിക്കുന്ന നാമപദ്ധതി. പേരിന്റെ ആദ്യഭാഗം ജീനസിനെയും രണ്ടാമത്തെ ഭാഗം സ്‌പീഷീസിനെയും കുറിക്കുന്നു. ലിന്നേയസ്‌ (1707-1778) എന്ന സ്വീഡിഷ്‌ ശാസ്‌ത്രജ്ഞന്‍ ആവിഷ്‌കരിച്ചു. ഉദാ: തെങ്ങിന്റെ ശാസ്‌ത്രനാമം Cocos nucifera, വളര്‍ത്തുനായയുടെ ശാസ്‌ത്രനാമം Canis familaris.', '', NULL),
(730, 'binomial surd', 'ദ്വിപദകരണി', '-', 'surd', NULL),
(731, 'binomial theorem', 'ദ്വിപദ സിദ്ധാന്തം', 'ദ്വിപദങ്ങളുടെ ഘാതങ്ങളെ വിപുലനം ചെയ്യുന്നതിനുള്ള സിദ്ധാന്തം. n ഒരു ധനസംഖ്യയാണെങ്കില്‍ ഈ സിദ്ധാന്തം ഇപ്രകാരമാണ്‌: ഇതില്‍ Xn-ryrന്റെ ഗുണോത്തരം ആണ്‌. ഇത്തരം ഗുണോത്തരങ്ങളെ ദ്വിപദഗുണോത്തരങ്ങള്‍ എന്ന്‌ പറയുന്നു. ( n!=n(n-1)(n-2)............ 3x2x1)', '', NULL),
(732, 'bioaccumulation', 'ജൈവസാന്ദ്രീകരണം', 'ഭക്ഷ്യശൃംഖലയിലൂടെ കടന്നുപോകുമ്പോള്‍ ഉയര്‍ന്ന തട്ടിലെത്തുമ്പോള്‍ ചില രാസവസ്‌തുക്കളുടെ സാന്ദ്രത ജന്തുക്കളില്‍ കൂടിവരുന്നത്‌ . മാംസഭോജികളിലാണ്‌ ഏറ്റവും ഉയര്‍ന്ന സാന്ദ്രതയെത്തുക.', '', NULL),
(733, 'biodegradation', 'ജൈവവിഘടനം', 'ജന്തു ശരീരത്തില്‍ വെച്ച്‌ ഒരു സംയുക്തം അതിന്റെ ഘടകങ്ങളായി വിഘടിക്കപ്പെടുന്നത്‌.', '', NULL),
(734, 'biochemical oxygen demand', 'ജൈവരാസിക ഓക്‌സിജന്‍ ആവശ്യകത', 'ജലത്തിന്റെ ജൈവ മലിനീകരണത്തിന്റെ തോത്‌. BOD എന്ന്‌ ചുരുക്കം. ജലത്തിലുള്ള ജൈവ മാലിന്യങ്ങളെ വിഘടിപ്പിക്കുന്ന സൂക്ഷ്‌മാണുക്കള്‍ ഉപയോഗിക്കുന്ന ഓക്‌സിജന്റെ അളവിനെ സൂചിപ്പിക്കുന്നു. ജൈവമാലിന്യം കൂടുന്തോറും BOD കൂടിക്കൊണ്ടിരിക്കും. biological oxygen demand എന്നും പറയുന്നു.', '', NULL),
(735, 'biocoenosis', 'ജൈവസഹവാസം', 'സസ്യങ്ങളുടെയും ജന്തുക്കളുടെയും പരസ്‌പരാശ്രിതമായ വാസം.', '', NULL),
(736, 'biodiversity', 'ജൈവ വൈവിധ്യം', 'വാസ സ്ഥലങ്ങളിലെ ജീവികളുടെ സമ്പന്നത. ഇതിനെ മൂന്ന്‌ തലങ്ങളില്‍ പരിഗണിക്കാറുണ്ട്‌. 1 സ്‌പീഷീസ്‌ വൈവിധ്യം ( species diversity), 2 ജനിതക വൈവിധ്യം ( genetic diversity) 3 ആവാസ വ്യവസ്ഥാ വൈവിധ്യം ( ecosystem diversity).', '', NULL),
(737, 'biogas', 'ജൈവവാതകം', 'ജൈവവസ്‌തുക്കള്‍ ഓക്‌സിജന്റെ അഭാവത്തില്‍ അഴുകുമ്പോള്‍ ഉണ്ടാകുന്ന വാതകം. മീഥേനാണ്‌ പ്രധാന ഘടകം. ഇന്ധനമായി ഉപയോഗിക്കുന്നു.', '', NULL),
(738, 'biogenesis', 'ജൈവജനം', 'ജീവികളില്‍ നിന്നേ ജീവന്‍ ഉണ്ടാകൂ എന്ന സിദ്ധാന്തം. ലൂയി പാസ്‌ചര്‍ (1822-1895), സ്‌പല്ലന്‍സാനി (1729-1799) എന്നിവര്‍ ഇത്‌ സ്വതന്ത്രമായി തെളിയിച്ചു. അജൈവ പദാര്‍ഥങ്ങളില്‍ നിന്ന്‌ സൂക്ഷ്‌മജീവികള്‍ മാത്രമല്ല എലികള്‍ പോലുള്ള ഉയര്‍ന്ന ജീവികളും എപ്പോഴും ഉണ്ടായിക്കൊണ്ടിരിക്കുകയാണെന്നുള്ള വിശ്വാസം ആദ്യകാലത്ത്‌ നിലനിന്നിരുന്നു.', '', NULL),
(739, 'bioinformatics', 'ബയോഇന്‍ഫോര്‍മാറ്റിക്‌സ്‌ ', 'DNA, പ്രാട്ടീന്‍ ഇവ സംബന്ധിച്ച ഡാറ്റ കമ്പ്യൂട്ടര്‍വല്‍ക്കരിച്ച സിസ്റ്റങ്ങളില്‍ ശേഖരിച്ച്‌ സൂക്ഷിക്കുകയും പഠിക്കുകയും ചെയ്യുന്ന ശാസ്‌ത്രശാഖ.', '', NULL),
(740, 'biological clock', 'ജൈവഘടികാരം', 'ജീവികള്‍ക്ക്‌ സമയ ബോധം നല്‍കുന്ന (ബോധപൂര്‍വമുള്ള അറിവല്ല) ആന്തരിക സംവിധാനം.', '', NULL),
(741, 'biological control', 'ജൈവനിയന്ത്രണം', 'വിളകള്‍ക്ക്‌ നാശം വരുത്തുന്ന കീടങ്ങളെയും, കളകളെയും നിയന്ത്രിക്കുവാന്‍ അവയുടെ ശത്രുക്കളായ ജീവികളെ ഉപയോഗിക്കുന്ന സമ്പ്രദായം.', '', NULL),
(742, 'biological oxygen demand', 'ജൈവ ഓക്‌സിജന്‍ ആവശ്യകത', '-', 'biochemical oxygen demand', NULL),
(743, 'bioluminescence', 'ജൈവ ദീപ്‌തി', 'ജീവികള്‍ ദൃശ്യപ്രകാശം പുറപ്പെടുവിക്കുന്നത്‌.', '', NULL),
(744, 'biomass', 'ജൈവ പിണ്ഡം', 'ജൈവദ്രവ്യമാനം, ജീവികളുടെ ദ്രവ്യമാനം.', '', NULL),
(745, 'biome', 'ജൈവമേഖല', 'സസ്യങ്ങളും ജന്തുക്കളുമടങ്ങിയ പ്രധാന പ്രാദേശിക പാരിസ്ഥിതിക മേഖലകള്‍. ഉദാ: മഴക്കാടുകള്‍, പുല്‍മേടുകള്‍.', '', NULL),
(746, 'biometry', 'ജൈവ സാംഖ്യികം', 'സാംഖ്യിക രീതി ഉപയോഗിച്ചുള്ള ജീവശാസ്‌ത്ര ഡാറ്റകളുടെ വിശകലനം.', '', NULL),
(747, 'biopesticides', 'ജൈവ കീടനാശിനികള്‍', 'സൂക്ഷ്‌മ ജീവികളെ ഉപയോഗിച്ച്‌ നിര്‍മ്മിക്കുന്ന കീടനാശിനികള്‍. കീടങ്ങളെ നശിപ്പിക്കാനോ നിയന്ത്രിച്ചു നിര്‍ത്താനോ സഹായിക്കുന്നു.', '', NULL),
(748, 'biophysics', 'ജൈവഭൗതികം', 'ഭൗതിക ശാസ്‌ത്ര രീതികളുപയോഗിച്ച്‌ ജൈവ വ്യവസ്ഥകളെക്കുറിച്ച്‌ പഠിക്കുന്ന ശാസ്‌ത്രശാഖ.', '', NULL),
(749, 'biopiracy', 'ജൈവകൊള്ള', 'ബന്ധപ്പെട്ട രാജ്യങ്ങളുടെയും ജനവിഭാഗങ്ങളുടെയും അനുമതിയില്ലാതെയും നഷ്ടപരിഹാരം നല്‍കാതെയും ജൈവവസ്‌തുക്കള്‍ ബഹുരാഷ്ട്ര കമ്പനികളും മറ്റും ഉപയോഗിക്കുന്നത്‌.', '', NULL),
(750, 'biopsy', 'ബയോപ്‌സി', 'രോഗനിര്‍ണയത്തിനുവേണ്ടി ജീവനുള്ള ശരീരത്തില്‍ നിന്ന്‌ കലകളോ, കോശങ്ങളോ, ദ്രാവകങ്ങളോ എടുത്ത്‌ പരിശോധനയ്‌ക്ക്‌ വിധേയമാക്കുന്ന രീതി.', '', NULL),
(751, 'bioreactor', 'ബയോ റിയാക്‌ടര്‍', 'പുളിപ്പിക്കല്‍ ( fermentation) പോലുള്ള പ്രക്രിയകള്‍ നടത്താന്‍ ഉപയോഗിക്കുന്ന ഉപകരണം.', '', NULL),
(752, 'biosphere', 'ജീവമണ്ഡലം', 'ഭൂമിയുടെ ഉപരിതലത്തിലും അന്തരീക്ഷത്തിലും ജീവന്‍ നിലനില്‍ക്കുന്ന ഭാഗങ്ങള്‍ക്കുള്ള പൊതുപേര്‌.', '', NULL),
(753, 'biosynthesis', 'ജൈവസംശ്ലേഷണം', 'ജീവികളാല്‍ നടത്തപ്പെടുന്ന രാസിക സംശ്ലേഷണം.', '', NULL),
(754, 'biota', 'ജീവസമൂഹം', 'ഒരു പ്രദേശത്തെ സസ്യജാലവും ജന്തുജാലവും.', '', NULL),
(755, 'biotic factor', 'ജീവീയ ഘടകങ്ങള്‍', 'ഒരു ജീവിയുടെ ചുറ്റുപാടിലുള്ളതും അതിന്റെ നിലനില്‍പ്പില്‍ സ്വാധീനം ചെലുത്തുന്നതുമായ മറ്റു ജീവികളും അവയുടെ പ്രവര്‍ത്തനങ്ങളും. ചുറ്റുപാടിലുള്ള പ്രകാശം, ജലം, മണ്ണ്‌ തുടങ്ങിയ ഘടകങ്ങളെ അജീവീയ ഘടകങ്ങള്‍ എന്നു പറയുന്നു.', '', NULL),
(756, 'biotin', 'ബയോട്ടിന്‍', 'വൈറ്റമിന്‍ ബി കോംപ്ലക്‌സിലെ ഒരു ജീവകം. ചെറിയ തോതില്‍ എല്ലാ ജീവകോശങ്ങളിലും വിതരണം ചെയ്യപ്പെട്ടിരിക്കുന്നു.', '', NULL),
(757, 'bio transformation', 'ജൈവ രൂപാന്തരണം', 'കള്‍ച്ചര്‍ കോശങ്ങളെ ഉപയോഗിച്ച്‌ അടിസ്ഥാന വസ്‌തുക്കളെ ( substrates) ആവശ്യമുള്ള ഓര്‍ഗാനിക്‌ വസ്‌തുക്കളാക്കി മാറ്റല്‍. കള്‍ച്ചര്‍ കോശങ്ങളുല്‍പാദിപ്പിക്കുന്ന എന്‍സൈമുകളാണ്‌ ഇവിടെ രാസത്വരകങ്ങളായി പ്രവര്‍ത്തിക്കുന്നത്‌.', '', NULL),
(758, 'bipolar', 'ദ്വിധ്രുവീയം', 'രണ്ട്‌ ധ്രുവങ്ങള്‍ ഉള്ളത്‌, രണ്ട്‌ വ്യത്യസ്‌ത ചാര്‍ജുകള്‍ ഉള്ളത്‌ എന്നീ അര്‍ഥങ്ങള്‍ ലഭിക്കുവാന്‍ ഉപയോഗിക്കുന്ന വിശേഷണ പദം. ഉദാ: ബൈപോളാര്‍ ട്രാന്‍സിസ്റ്റര്‍.', '', NULL),
(759, 'bipolar transistor', 'ദ്വിധ്രുവീയ ട്രാന്‍സിസ്റ്റര്‍', 'ഇലക്‌ട്രാണുകള്‍ക്കും ഹോളുകള്‍ക്കും തുല്യ പ്രാധാന്യമുള്ള ട്രാന്‍സിസ്റ്റര്‍. രണ്ട്‌ വിധത്തിലുണ്ട്‌. 1. PNP ട്രാന്‍സിസ്റ്റര്‍. പി ടൈപ്പ്‌, എന്‍ ടൈപ്പ്‌, പി ടൈപ്പ്‌ എന്നീ അര്‍ധചാലക മേഖലകള്‍ ഒന്നിനെ തുടര്‍ന്ന്‌ മറ്റൊന്ന്‌ വരുന്നതാണ്‌ PNP. സാധാരണ പ്രവര്‍ത്തനത്തില്‍ ഒരു PN സന്ധി മുന്നോട്ടും തുടര്‍ന്നുവരുന്ന NP സന്ധി പിന്നോട്ടും ബയെസ്‌ ചെയ്‌തിരിക്കും. മുന്നോട്ട്‌ ബയെസ്‌ ചെയ്യുന്ന പി ഭാഗം എമിറ്ററും പിന്നോട്ട്‌ ബയെസ്‌ ചെയ്യുന്ന പി ഭാഗം കളക്‌ടറുമാണ്‌. നടുവിലെ ഭാഗം ബേസും ആണ്‌. NPN ട്രാന്‍സിസ്റ്ററിന്റെ ഘടനയും സമാനമാണ്‌. ചിത്രം കാണുക.', '', NULL);
